import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const EnterpriseCreateForm = ({ onCreated }) => {
  const [name, setName] = React.useState('');
  const [ownerEmail, setOwnerEmail] = React.useState('');
  const [seats, setSeats] = React.useState(10);
  const [loading, setLoading] = React.useState(false);

  const submit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const res = await axios.post('http://localhost:5005/api/admin/enterprises', { name, ownerEmail, seats: Number(seats) });
      onCreated && onCreated(res.data.enterprise);
      setName(''); setOwnerEmail(''); setSeats(10);
    } catch (err) {
      console.error(err);
      toast.error('Enterprise oluşturulamadı');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={submit} style={{
      display: 'grid',
      gap: '12px',
      gridTemplateColumns: '1fr 1fr 180px 140px'
    }}>
      <input placeholder="Enterprise adı" value={name} onChange={e=>setName(e.target.value)}
        required
        style={{ background:'rgba(20,20,20,0.8)', color:'#fff', border:'1px solid rgba(220,38,38,0.5)', borderRadius:8, padding:'10px 12px'}} />
      <input placeholder="Owner email" type="email" value={ownerEmail} onChange={e=>setOwnerEmail(e.target.value)}
        style={{ background:'rgba(20,20,20,0.8)', color:'#fff', border:'1px solid rgba(220,38,38,0.5)', borderRadius:8, padding:'10px 12px'}} />
      <input placeholder="Seats" type="number" min={1} value={seats} onChange={e=>setSeats(e.target.value)}
        required
        style={{ background:'rgba(20,20,20,0.8)', color:'#fff', border:'1px solid rgba(220,38,38,0.5)', borderRadius:8, padding:'10px 12px'}} />
      <button type="submit" disabled={loading} style={{
        background:'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)', color:'#fff', border:'none', borderRadius:8, padding:'10px 12px', fontWeight:700, cursor:'pointer'
      }}>{loading ? 'Creating...' : 'Create Enterprise'}</button>
    </form>
  );
};

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [pins, setPins] = useState([]);
  const [enterprises, setEnterprises] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedPin, setSelectedPin] = useState(null);
  const [showPinDetails, setShowPinDetails] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showUserResults, setShowUserResults] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [newPassword, setNewPassword] = useState('');
  const [isChangingPassword, setIsChangingPassword] = useState(false);
  const [showBanModal, setShowBanModal] = useState(false);
  const [banReason, setBanReason] = useState('');

  useEffect(() => {
    fetchData();
    // Her 30 saniyede bir veri güncelle
    const interval = setInterval(fetchData, 30000);
    return () => clearInterval(interval);
  }, []);

  // PIN details sayfası açıldığında selectedUser state'ini ayarla
  useEffect(() => {
    if (showPinDetails && selectedPin && users.length > 0) {
      const pinOwner = users.find(user => user.email === selectedPin.creatorEmail);
      if (pinOwner && (!selectedUser || selectedUser.email !== pinOwner.email)) {
        setSelectedUser(pinOwner);
      }
    }
  }, [showPinDetails, selectedPin, users]);

  const fetchData = async () => {
    try {
      const [usersResponse, pinsResponse, entResponse] = await Promise.all([
        axios.get('http://localhost:5005/api/admin/users'),
        axios.get('http://localhost:5005/api/admin/pins'),
        axios.get('http://localhost:5005/api/admin/enterprises')
      ]);
      
      setUsers(usersResponse.data);
      setPins(pinsResponse.data);
      setEnterprises(entResponse.data || []);
    } catch (error) {
      console.error('Admin verileri yüklenemedi:', error);
      toast.error('Admin verileri yüklenemedi!');
    } finally {
      setIsLoading(false);
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleString('tr-TR');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#22c55e';
      case 'completed': return '#3b82f6';
      case 'expired': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const parseScanResults = (results) => {
    if (!results) return null;
    
    let resultsString = results;
    if (typeof results !== 'string') {
      resultsString = String(results);
    }
    
    const lines = resultsString.split('\n');
    const sections = {
      suspiciousActivities: [],
      services: [],
      cheatWebsites: [],
      systemInfo: [],
      screenshot: null,
      fivemMods: [],
      usbDevices: [],
      discordAccount: null
    };
    
    let currentSection = null;
    
    lines.forEach(line => {
      line = line.trim();
      
      if (line.includes('SUSPICIOUS ACTIVITIES') || line.includes('DETECT')) {
        currentSection = 'suspiciousActivities';
      } else if (line.includes('SYSTEM INFORMATION')) {
        currentSection = 'systemInfo';
      } else if (line.includes('SERVICES')) {
        currentSection = 'services';
      } else if (line.includes('SCREENSHOT')) {
        currentSection = 'screenshot';
      } else if (line.includes('USB DEVICES')) {
        currentSection = 'usbDevices';
      } else if (line.includes('FiveM Mods:')) {
        const modsMatch = line.match(/FiveM Mods: Mods: (.+) \(Total: (\d+)\)/);
        if (modsMatch) {
          const modsString = modsMatch[1];
          const totalCount = modsMatch[2];
          const modList = modsString.split(', ').map(mod => mod.trim());
          sections.fivemMods = modList;
        }
      } else if (line.includes('CHEAT WEBSITES')) {
        currentSection = 'cheatWebsites';
      } else if (line.includes('USB Devices:')) {
        const usbMatch = line.match(/USB Devices: Devices: (.+) \(Total: (\d+)\)/);
        if (usbMatch) {
          const usbString = usbMatch[1];
          const totalCount = usbMatch[2];
          const usbList = usbString.split(', ').map(device => device.trim());
          sections.usbDevices = usbList;
        } else if (line.includes('USB Devices: Devices: No USB devices detected')) {
          sections.usbDevices = ['No USB devices detected'];
        }
      } else if (line.startsWith('Discord Account:')) {
        const id = line.replace('Discord Account:', '').trim();
        if (id) sections.discordAccount = id;
      } else if (line && currentSection && line.startsWith('-')) {
        const item = line.replace('- ', '').trim();
        if (item && !/^[\-_—]+$/.test(item)) {
          if (currentSection === 'suspiciousActivities') {
            sections.suspiciousActivities.push(item);
          } else if (currentSection === 'services') {
            sections.services.push(item);
          } else if (currentSection === 'cheatWebsites') {
            sections.cheatWebsites.push(item);
          }
        }
      } else if (line && currentSection === 'screenshot' && line.includes('Screenshot captured:')) {
        sections.screenshot = line.replace('Screenshot captured: ', '');
      } else if (line && currentSection === 'systemInfo' && (line.startsWith('OS:') || line.startsWith('User:') || line.startsWith('Desktop:') || line.startsWith('System Time:') || line.startsWith('Boot Time:') || line.startsWith('VPN:') || line.startsWith('Install Date:') || line.startsWith('Country:') || line.startsWith('Game Activity:') || line.startsWith('Recycle Activity:') || line.startsWith('Hardware Stats:') || line.startsWith('FiveM Mods:'))) {
        sections.systemInfo.push(line);
      }
    });
    
    return sections;
  };

  const handlePinClick = (pin) => {
    setSelectedPin(pin);
    setShowPinDetails(true);
  };

  const handleUserClick = (user) => {
    setSelectedUser(user);
    setShowUserResults(true);
  };

  // Confirm Ban - extracted to avoid very long inline JSX handlers
  const handleConfirmBan = async () => {
    if (!banReason.trim()) {
      toast.error('Ban reason is required');
      return;
    }

    const userEmail = selectedUser?.email || (showPinDetails && selectedPin ? selectedPin.creatorEmail : null);
    if (!userEmail) {
      toast.error('No user selected');
      return;
    }

    try {
      await axios.post('http://localhost:5005/api/admin/ban-user', {
        email: userEmail,
        reason: banReason.trim()
      });
      toast.success(`User ${userEmail} has been banned`);
      setShowBanModal(false);
      setBanReason('');
      setSelectedUser(null);
    } catch (error) {
      console.error('Ban error:', error);
      if (error.response?.status === 404) {
        toast.error('User not found');
      } else if (error.response?.status === 400) {
        toast.error(error.response.data.message || 'Invalid request');
      } else {
        toast.error('Failed to ban user');
      }
    }
  };


  const getUserCompletedPins = (user) => {
    // Bu kullanıcının tamamladığı PIN'leri filtrele
    // Şimdilik tüm tamamlanan PIN'leri döndürüyoruz
    // Gerçek uygulamada user ID ile PIN'leri eşleştirmek gerekir
    return pins.filter(pin => pin.scanCompleted);
  };

  const filteredPins = pins.filter(pin => {
    const matchesSearch = pin.pin.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = filterStatus === 'all' || pin.status === filterStatus;
    return matchesSearch && matchesStatus;
  });

  const stats = {
    totalUsers: users.length,
    totalPins: pins.length,
    activePins: pins.filter(p => p.status === 'active').length,
    completedPins: pins.filter(p => p.status === 'completed').length,
    expiredPins: pins.filter(p => p.status === 'expired').length,
    scansWithResults: pins.filter(p => p.scanCompleted).length
  };

  if (isLoading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
        color: '#ffffff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{
          fontSize: '1.2rem',
          color: '#ffffff',
          textAlign: 'center'
        }}>
          Loading admin data...
        </div>

        {/* Ban Modal (mounted in PIN details view) */}
        <div style={{
          position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
          background: 'rgba(0, 0, 0, 0.8)',
          display: showBanModal ? 'flex' : 'none',
          alignItems: 'center', justifyContent: 'center', zIndex: 10000, backdropFilter: 'blur(10px)'
        }}>
          <div style={{ background: 'rgba(20, 20, 20, 0.95)', borderRadius: '20px', padding: '40px', border: '2px solid rgba(220, 38, 38, 0.5)', boxShadow: '0 20px 60px rgba(0, 0, 0, 0.8)', maxWidth: '500px', width: '90%', position: 'relative' }}>
            <button onClick={() => { setShowBanModal(false); setBanReason(''); }} style={{ position: 'absolute', top: '15px', right: '15px', background: 'rgba(220, 38, 38, 0.2)', color: '#fff', border: '1px solid rgba(220, 38, 38, 0.5)', borderRadius: '50%', width: '35px', height: '35px', cursor: 'pointer', fontSize: '18px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>×</button>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#fff', fontSize: '1.8rem', marginBottom: '10px', fontWeight: '700' }}>Ban User</h2>
              <p style={{ color: 'rgba(255,255,255,0.8)', marginBottom: '30px' }}>
                You are about to ban: <strong style={{ color: '#ef4444' }}>{selectedUser?.email || (showPinDetails && selectedPin ? selectedPin.creatorEmail : 'Unknown User')}</strong>
              </p>
              <div style={{ marginBottom: '30px' }}>
                <label style={{ display: 'block', color: 'rgba(255,255,255,0.9)', marginBottom: '10px', fontWeight: 600 }}>Ban Reason (Required)</label>
                <textarea value={banReason} onChange={(e)=>setBanReason(e.target.value)} placeholder="Enter the reason for banning this user..." style={{ width:'100%', minHeight:'100px', padding:'15px', background:'rgba(0,0,0,0.6)', color:'#fff', border:'2px solid rgba(220,38,38,0.5)', borderRadius:'10px', fontSize:'14px', outline:'none', resize:'vertical', fontFamily:'inherit' }} />
              </div>
              <div style={{ display: 'flex', gap: '15px', justifyContent: 'center' }}>
                <button onClick={()=>{ setShowBanModal(false); setBanReason(''); }} style={{ background:'rgba(100,100,100,0.3)', color:'#fff', border:'1px solid rgba(255,255,255,0.3)', borderRadius:'10px', padding:'12px 24px', fontWeight:600 }}>Cancel</button>
                <button onClick={handleConfirmBan} style={{ background:'linear-gradient(135deg,#ef4444 0%, #dc2626 100%)', color:'#fff', border:'none', borderRadius:'10px', padding:'12px 24px', fontWeight:600 }}>Confirm Ban</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (showPinDetails && selectedPin) {
    const parsedResults = parseScanResults(selectedPin.scanResults);
    
    // Debug: Screenshot verisini kontrol et
    console.log('Selected PIN:', selectedPin);
    console.log('Screenshot data:', selectedPin.screenshot ? 'Available' : 'Not available');
    console.log('Screenshot length:', selectedPin.screenshot ? selectedPin.screenshot.length : 0);
    
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
        color: '#ffffff',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <Toaster position="top-right" />
        
        {/* Header */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.9)',
          backdropFilter: 'blur(20px)',
          padding: '20px',
          borderBottom: '1px solid rgba(220, 38, 38, 0.3)',
          position: 'relative',
          zIndex: 2
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            maxWidth: '1600px',
            margin: '0 auto'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
              <button
                onClick={() => {
                  setShowPinDetails(false);
                  setShowBanModal(false);
                  setBanReason('');
                  setSelectedUser(null);
                }}
                style={{
                  background: 'rgba(220, 38, 38, 0.2)',
                  color: '#ffffff',
                  border: '1px solid rgba(220, 38, 38, 0.5)',
                  borderRadius: '8px',
                  padding: '10px 16px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: '600',
                  transition: 'all 0.3s ease',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}
                onMouseOver={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 0.3)';
                }}
                onMouseOut={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 0.2)';
                }}
              >
                ← Back to Admin Panel
              </button>
              <h1 style={{
                color: '#ffffff',
                fontSize: '1.8rem',
                margin: 0,
                fontWeight: '600',
                textShadow: '0 0 10px rgba(220, 38, 38, 0.3)'
              }}>
                PIN Details: {selectedPin.pin}
              </h1>
            </div>
          </div>
        </div>

        {/* Content */}
        <div style={{
          maxWidth: '1600px',
          margin: '0 auto',
          padding: '30px 20px',
          position: 'relative',
          zIndex: 2
        }}>
          {/* PIN Info */}
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
            marginBottom: '30px'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600'
            }}>
              PIN Information
            </h2>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
              gap: '15px'
            }}>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
