              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  Status: {selectedPin.status}
                </div>
              </div>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  Created: {formatDate(selectedPin.createdAt)}
                </div>
              </div>
              {selectedPin.completedAt && (
                <div style={{
                  background: 'rgba(34, 197, 94, 0.1)',
                  borderRadius: '10px',
                  padding: '15px',
                  border: '1px solid rgba(34, 197, 94, 0.3)'
                }}>
                  <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                    Completed: {formatDate(selectedPin.completedAt)}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Scan Results */}
          {parsedResults && (
            <>
              {/* System Information */}
              {parsedResults.systemInfo?.length > 0 && (
                <div style={{
                  background: 'rgba(20, 20, 20, 0.8)',
                  borderRadius: '15px',
                  padding: '30px',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                  marginBottom: '30px'
                }}>
                  <h2 style={{
                    color: '#ffffff',
                    fontSize: '1.5rem',
                    margin: '0 0 20px 0',
                    fontWeight: '600'
                  }}>
                    System Information
                  </h2>
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                    gap: '15px'
                  }}>
                    {parsedResults.systemInfo.map((info, index) => (
                      <div key={index} style={{
                        background: 'rgba(34, 197, 94, 0.1)',
                        borderRadius: '10px',
                        padding: '15px',
                        border: '1px solid rgba(34, 197, 94, 0.3)'
                      }}>
                        <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px', lineHeight: '1.5' }}>
                          {info}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Services */}
              {parsedResults.services?.length > 0 && (
                <div style={{
                  background: 'rgba(20, 20, 20, 0.8)',
                  borderRadius: '15px',
                  padding: '30px',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                  marginBottom: '30px'
                }}>
                  <h2 style={{
                    color: '#ffffff',
                    fontSize: '1.5rem',
                    margin: '0 0 20px 0',
                    fontWeight: '600'
                  }}>
                    Services
                  </h2>
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                    gap: '15px'
                  }}>
                    {parsedResults.services.map((service, index) => (
                      <div key={index} style={{
                        background: service.includes(': on') ? 'rgba(34, 197, 94, 0.1)' : 'rgba(239, 68, 68, 0.1)',
                        borderRadius: '10px',
                        padding: '15px',
                        border: service.includes(': on') ? '1px solid rgba(34, 197, 94, 0.3)' : '1px solid rgba(239, 68, 68, 0.3)'
                      }}>
                        <div style={{ 
                          color: service.includes(': on') ? 'rgba(34, 197, 94, 0.9)' : 'rgba(239, 68, 68, 0.9)', 
                          fontSize: '14px', 
                          lineHeight: '1.5',
                          fontWeight: '600'
                        }}>
                          {service}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* FiveM Mods */}
              {parsedResults.fivemMods?.length > 0 && (
                <div style={{
                  background: 'rgba(20, 20, 20, 0.8)',
                  borderRadius: '15px',
                  padding: '30px',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                  marginBottom: '30px'
                }}>
                  <h2 style={{
                    color: '#ffffff',
                    fontSize: '1.5rem',
                    margin: '0 0 20px 0',
                    fontWeight: '600'
                  }}>
