import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const EnterpriseCreateForm = ({ onCreated }) => {
  const [name, setName] = React.useState('');
  const [ownerEmail, setOwnerEmail] = React.useState('');
  const [seats, setSeats] = React.useState(10);
  const [loading, setLoading] = React.useState(false);

  const submit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const res = await axios.post('http://localhost:5005/api/admin/enterprises', { name, ownerEmail, seats: Number(seats) });
      onCreated && onCreated(res.data.enterprise);
      setName(''); setOwnerEmail(''); setSeats(10);
    } catch (err) {
      console.error(err);
      toast.error('Enterprise oluşturulamadı');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={submit} style={{
      display: 'grid',
      gap: '12px',
      gridTemplateColumns: '1fr 1fr 180px 140px'
    }}>
      <input placeholder="Enterprise adı" value={name} onChange={e=>setName(e.target.value)}
        required
        style={{ background:'rgba(20,20,20,0.8)', color:'#fff', border:'1px solid rgba(220,38,38,0.5)', borderRadius:8, padding:'10px 12px'}} />
      <input placeholder="Owner email" type="email" value={ownerEmail} onChange={e=>setOwnerEmail(e.target.value)}
        style={{ background:'rgba(20,20,20,0.8)', color:'#fff', border:'1px solid rgba(220,38,38,0.5)', borderRadius:8, padding:'10px 12px'}} />
      <input placeholder="Seats" type="number" min={1} value={seats} onChange={e=>setSeats(e.target.value)}
        required
        style={{ background:'rgba(20,20,20,0.8)', color:'#fff', border:'1px solid rgba(220,38,38,0.5)', borderRadius:8, padding:'10px 12px'}} />
      <button type="submit" disabled={loading} style={{
        background:'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)', color:'#fff', border:'none', borderRadius:8, padding:'10px 12px', fontWeight:700, cursor:'pointer'
      }}>{loading ? 'Creating...' : 'Create Enterprise'}</button>
    </form>
  );
};

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [pins, setPins] = useState([]);
  const [enterprises, setEnterprises] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedPin, setSelectedPin] = useState(null);
  const [showPinDetails, setShowPinDetails] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showUserResults, setShowUserResults] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [newPassword, setNewPassword] = useState('');
  const [isChangingPassword, setIsChangingPassword] = useState(false);

  useEffect(() => {
    fetchData();
    // Her 30 saniyede bir veri güncelle
    const interval = setInterval(fetchData, 30000);
    return () => clearInterval(interval);
  }, []);

  const fetchData = async () => {
    try {
      const [usersResponse, pinsResponse, entResponse] = await Promise.all([
        axios.get('http://localhost:5005/api/admin/users'),
        axios.get('http://localhost:5005/api/admin/pins'),
        axios.get('http://localhost:5005/api/admin/enterprises')
      ]);
      
      setUsers(usersResponse.data);
      setPins(pinsResponse.data);
      setEnterprises(entResponse.data || []);
    } catch (error) {
      console.error('Admin verileri yüklenemedi:', error);
      toast.error('Admin verileri yüklenemedi!');
    } finally {
      setIsLoading(false);
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleString('tr-TR');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#22c55e';
      case 'completed': return '#3b82f6';
      case 'expired': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const parseScanResults = (results) => {
    if (!results) return null;
    
    let resultsString = results;
    if (typeof results !== 'string') {
      resultsString = String(results);
    }
    
    const lines = resultsString.split('\n');
    const sections = {
      suspiciousActivities: [],
      services: [],
      cheatWebsites: [],
      systemInfo: [],
      screenshot: null,
      fivemMods: [],
      usbDevices: [],
      discordAccount: null
    };
    
    let currentSection = null;
    
    lines.forEach(line => {
      line = line.trim();
      
      if (line.includes('SUSPICIOUS ACTIVITIES') || line.includes('DETECT')) {
        currentSection = 'suspiciousActivities';
      } else if (line.includes('SYSTEM INFORMATION')) {
        currentSection = 'systemInfo';
      } else if (line.includes('SERVICES')) {
        currentSection = 'services';
      } else if (line.includes('SCREENSHOT')) {
        currentSection = 'screenshot';
      } else if (line.includes('USB DEVICES')) {
        currentSection = 'usbDevices';
      } else if (line.includes('FiveM Mods:')) {
        const modsMatch = line.match(/FiveM Mods: Mods: (.+) \(Total: (\d+)\)/);
        if (modsMatch) {
          const modsString = modsMatch[1];
          const totalCount = modsMatch[2];
          const modList = modsString.split(', ').map(mod => mod.trim());
          sections.fivemMods = modList;
        }
      } else if (line.includes('CHEAT WEBSITES')) {
        currentSection = 'cheatWebsites';
      } else if (line.includes('USB Devices:')) {
        const usbMatch = line.match(/USB Devices: Devices: (.+) \(Total: (\d+)\)/);
        if (usbMatch) {
          const usbString = usbMatch[1];
          const totalCount = usbMatch[2];
          const usbList = usbString.split(', ').map(device => device.trim());
          sections.usbDevices = usbList;
        } else if (line.includes('USB Devices: Devices: No USB devices detected')) {
          sections.usbDevices = ['No USB devices detected'];
        }
      } else if (line.startsWith('Discord Account:')) {
        const id = line.replace('Discord Account:', '').trim();
        if (id) sections.discordAccount = id;
      } else if (line && currentSection && line.startsWith('-')) {
        const item = line.replace('- ', '').trim();
        if (item && !/^[\-_—]+$/.test(item)) {
          if (currentSection === 'suspiciousActivities') {
            sections.suspiciousActivities.push(item);
          } else if (currentSection === 'services') {
            sections.services.push(item);
          } else if (currentSection === 'cheatWebsites') {
            sections.cheatWebsites.push(item);
          }
        }
      } else if (line && currentSection === 'screenshot' && line.includes('Screenshot captured:')) {
        sections.screenshot = line.replace('Screenshot captured: ', '');
      } else if (line && currentSection === 'systemInfo' && (line.startsWith('OS:') || line.startsWith('User:') || line.startsWith('Desktop:') || line.startsWith('System Time:') || line.startsWith('Boot Time:') || line.startsWith('VPN:') || line.startsWith('Install Date:') || line.startsWith('Country:') || line.startsWith('Game Activity:') || line.startsWith('Recycle Activity:') || line.startsWith('Hardware Stats:') || line.startsWith('FiveM Mods:'))) {
        sections.systemInfo.push(line);
      }
    });
    
    return sections;
  };

  const handlePinClick = (pin) => {
    setSelectedPin(pin);
    setShowPinDetails(true);
  };

  const handleUserClick = (user) => {
    setSelectedUser(user);
    setShowUserResults(true);
  };

  const changeUserPassword = async (e) => {
    e.preventDefault();
    if (!newPassword.trim()) {
      toast.error('Please enter a new password');
      return;
    }
    
    try {
      setIsChangingPassword(true);
      await axios.post('http://localhost:5005/api/admin/change-user-password', {
        email: selectedUser.email,
        newPassword: newPassword
      });
      toast.success(`Password changed successfully for ${selectedUser.email}`);
      setNewPassword('');
    } catch (error) {
      console.error('Password change error:', error);
      if (error.response?.status === 404) {
        toast.error('User not found');
      } else if (error.response?.status === 400) {
        toast.error(error.response.data.message || 'Invalid request');
      } else {
        toast.error('Failed to change password');
      }
    } finally {
      setIsChangingPassword(false);
    }
  };

  const getUserCompletedPins = (user) => {
    // Bu kullanıcının tamamladığı PIN'leri filtrele
    // Şimdilik tüm tamamlanan PIN'leri döndürüyoruz
    // Gerçek uygulamada user ID ile PIN'leri eşleştirmek gerekir
    return pins.filter(pin => pin.scanCompleted);
  };

  const filteredPins = pins.filter(pin => {
    const matchesSearch = pin.pin.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = filterStatus === 'all' || pin.status === filterStatus;
    return matchesSearch && matchesStatus;
  });

  const stats = {
    totalUsers: users.length,
    totalPins: pins.length,
    activePins: pins.filter(p => p.status === 'active').length,
    completedPins: pins.filter(p => p.status === 'completed').length,
    expiredPins: pins.filter(p => p.status === 'expired').length,
    scansWithResults: pins.filter(p => p.scanCompleted).length
  };

  if (isLoading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
        color: '#ffffff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{
          fontSize: '1.2rem',
          color: '#ffffff',
          textAlign: 'center'
        }}>
          Loading admin data...
        </div>
      </div>
    );
  }

  if (showPinDetails && selectedPin) {
    const parsedResults = parseScanResults(selectedPin.scanResults);
    
    // Debug: Screenshot verisini kontrol et
    console.log('Selected PIN:', selectedPin);
    console.log('Screenshot data:', selectedPin.screenshot ? 'Available' : 'Not available');
    console.log('Screenshot length:', selectedPin.screenshot ? selectedPin.screenshot.length : 0);
    
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
        color: '#ffffff',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <Toaster position="top-right" />
        
        {/* Header */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.9)',
          backdropFilter: 'blur(20px)',
          padding: '20px',
          borderBottom: '1px solid rgba(220, 38, 38, 0.3)',
          position: 'relative',
          zIndex: 2
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            maxWidth: '1600px',
            margin: '0 auto'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
              <button
                onClick={() => setShowPinDetails(false)}
                style={{
                  background: 'rgba(220, 38, 38, 0.2)',
                  color: '#ffffff',
                  border: '1px solid rgba(220, 38, 38, 0.5)',
                  borderRadius: '8px',
                  padding: '10px 16px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: '600',
                  transition: 'all 0.3s ease',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}
                onMouseOver={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 0.3)';
                }}
                onMouseOut={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 0.2)';
                }}
              >
                ← Back to Admin Panel
              </button>
              <h1 style={{
                color: '#ffffff',
                fontSize: '1.8rem',
                margin: 0,
                fontWeight: '600',
                textShadow: '0 0 10px rgba(220, 38, 38, 0.3)'
              }}>
                PIN Details: {selectedPin.pin}
              </h1>
            </div>
          </div>
        </div>

        {/* Content */}
        <div style={{
          maxWidth: '1600px',
          margin: '0 auto',
          padding: '30px 20px',
          position: 'relative',
          zIndex: 2
        }}>
          {/* PIN Info */}
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
            marginBottom: '30px'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600'
            }}>
              PIN Information
            </h2>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
              gap: '15px'
            }}>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  PIN: {selectedPin.pin}
                </div>
              </div>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  Status: {selectedPin.status}
                </div>
              </div>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  Created: {formatDate(selectedPin.createdAt)}
                </div>
              </div>
              {selectedPin.completedAt && (
                <div style={{
                  background: 'rgba(34, 197, 94, 0.1)',
                  borderRadius: '10px',
                  padding: '15px',
                  border: '1px solid rgba(34, 197, 94, 0.3)'
                }}>
                  <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                    Completed: {formatDate(selectedPin.completedAt)}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Change Password Section */}
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
            marginBottom: '30px'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600'
            }}>
              Change User Password
            </h2>
            
            <form onSubmit={changeUserPassword} style={{
              display: 'flex',
              gap: '15px',
              alignItems: 'center',
              flexWrap: 'wrap'
            }}>
              <input
                type="password"
                placeholder="Enter new password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
                style={{
                  background: 'rgba(20, 20, 20, 0.8)',
                  color: '#ffffff',
                  border: '1px solid rgba(220, 38, 38, 0.5)',
                  borderRadius: '8px',
                  padding: '12px 16px',
                  fontSize: '14px',
                  outline: 'none',
                  flex: '1',
                  minWidth: '300px'
                }}
              />
              <button
                type="submit"
                disabled={isChangingPassword}
                style={{
                  background: 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)',
                  color: '#ffffff',
                  border: 'none',
                  borderRadius: '8px',
                  padding: '12px 24px',
                  fontSize: '14px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  whiteSpace: 'nowrap'
                }}
                onMouseOver={(e) => {
                  if (!isChangingPassword) {
                    e.target.style.transform = 'translateY(-2px)';
                    e.target.style.boxShadow = '0 8px 25px rgba(220, 38, 38, 0.5)';
                  }
                }}
                onMouseOut={(e) => {
                  e.target.style.transform = 'translateY(0)';
                  e.target.style.boxShadow = 'none';
                }}
              >
                {isChangingPassword ? 'Changing...' : 'Change Password'}
              </button>
            </form>
            
            <div style={{
              marginTop: '15px',
              padding: '12px',
              background: 'rgba(220, 38, 38, 0.1)',
              borderRadius: '8px',
              border: '1px solid rgba(220, 38, 38, 0.3)',
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '13px'
            }}>
              <strong>Note:</strong> This will change the user's password immediately. The user will need to use the new password for their next login.
            </div>
          </div>

          {/* Scan Results */}
          {parsedResults && (
            <>
              {/* System Information */}
              {parsedResults.systemInfo?.length > 0 && (
                <div style={{
                  background: 'rgba(20, 20, 20, 0.8)',
                  borderRadius: '15px',
                  padding: '30px',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                  marginBottom: '30px'
                }}>
                  <h2 style={{
                    color: '#ffffff',
                    fontSize: '1.5rem',
                    margin: '0 0 20px 0',
                    fontWeight: '600'
                  }}>
                    System Information
                  </h2>
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                    gap: '15px'
                  }}>
                    {parsedResults.systemInfo.map((info, index) => (
                      <div key={index} style={{
                        background: 'rgba(34, 197, 94, 0.1)',
                        borderRadius: '10px',
                        padding: '15px',
                        border: '1px solid rgba(34, 197, 94, 0.3)'
                      }}>
                        <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px', lineHeight: '1.5' }}>
                          {info}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* FiveM Mods */}
              {parsedResults.fivemMods?.length > 0 && (
                <div style={{
                  background: 'rgba(20, 20, 20, 0.8)',
                  borderRadius: '15px',
                  padding: '30px',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                  marginBottom: '30px'
                }}>
                  <h2 style={{
                    color: '#ffffff',
                    fontSize: '1.5rem',
                    margin: '0 0 20px 0',
                    fontWeight: '600'
                  }}>
                    FiveM Mods ({parsedResults.fivemMods.length})
                  </h2>
                  <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '14px' }}>
                    <ul style={{ margin: 0, paddingLeft: '20px' }}>
                      {parsedResults.fivemMods.map((mod, index) => (
                        <li key={index} style={{ marginBottom: '8px', display: 'flex', alignItems: 'center', gap: '8px' }}>
                          📄 {mod}
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              )}

              {/* Cheat Websites */}
              {parsedResults.cheatWebsites?.length > 0 && (
                <div style={{
                  background: 'rgba(20, 20, 20, 0.8)',
                  borderRadius: '15px',
                  padding: '30px',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                  marginBottom: '30px'
                }}>
                  <h2 style={{
                    color: '#ffffff',
                    fontSize: '1.5rem',
                    margin: '0 0 20px 0',
                    fontWeight: '600'
                  }}>
                    Cheat Websites ({parsedResults.cheatWebsites.length})
                  </h2>
                  <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '14px' }}>
                    <ul style={{ margin: 0, paddingLeft: '20px' }}>
                      {parsedResults.cheatWebsites.map((site, index) => (
                        <li key={index} style={{ marginBottom: '8px' }}>{site}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              )}

              {/* Discord Account */}
              {parsedResults.discordAccount && (
                <div style={{
                  background: 'rgba(20, 20, 20, 0.8)',
                  borderRadius: '15px',
                  padding: '30px',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                  marginBottom: '30px'
                }}>
                  <h2 style={{
                    color: '#ffffff',
                    fontSize: '1.5rem',
                    margin: '0 0 20px 0',
                    fontWeight: '600'
                  }}>
                    Discord Account
                  </h2>
                  <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '14px' }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                      <span>ID: {parsedResults.discordAccount}</span>
                    </div>
                  </div>
                </div>
              )}

              {/* Screenshot */}
              {selectedPin.screenshot && (
                <div style={{
                  background: 'rgba(20, 20, 20, 0.8)',
                  borderRadius: '15px',
                  padding: '30px',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                  marginBottom: '30px'
                }}>
                  <h2 style={{
                    color: '#ffffff',
                    fontSize: '1.5rem',
                    margin: '0 0 20px 0',
                    fontWeight: '600'
                  }}>
                    PC Screenshot
                  </h2>
                  <div style={{
                    background: 'rgba(0, 0, 0, 0.5)',
                    borderRadius: '10px',
                    padding: '20px',
                    textAlign: 'center'
                  }}>
                    {(() => {
                      try {
                        // Screenshot'u kontrol et ve göster
                        const screenshotData = selectedPin.screenshot;
                        if (screenshotData && typeof screenshotData === 'string' && screenshotData.length > 0) {
                          const src = screenshotData.startsWith('data:image')
                            ? screenshotData
                            : `data:image/png;base64,${screenshotData}`;
                          return (
                            <img 
                              src={src}
                              alt="PC Screenshot"
                              style={{
                                maxWidth: '100%',
                                maxHeight: '500px',
                                borderRadius: '8px',
                                border: '1px solid rgba(255, 255, 255, 0.2)'
                              }}
                            />
                          );
                        } else {
                          return (
                            <div style={{
                              color: 'rgba(255, 255, 255, 0.6)',
                              fontSize: '14px',
                              padding: '20px'
                            }}>
                              Screenshot data not available
                            </div>
                          );
                        }
                      } catch (error) {
                        console.error('Screenshot render hatası:', error);
                        return (
                          <div style={{
                            color: 'rgba(255, 255, 255, 0.6)',
                            fontSize: '14px',
                            padding: '20px'
                          }}>
                            Screenshot render error
                          </div>
                        );
                      }
                    })()}
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </div>
    );
  }

  if (showUserResults && selectedUser) {
    const userCompletedPins = getUserCompletedPins(selectedUser);
    
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
        color: '#ffffff',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <Toaster position="top-right" />
        
        {/* Header */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.9)',
          backdropFilter: 'blur(20px)',
          padding: '20px',
          borderBottom: '1px solid rgba(220, 38, 38, 0.3)',
          position: 'relative',
          zIndex: 2
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            maxWidth: '1600px',
            margin: '0 auto'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
              <button
                onClick={() => setShowUserResults(false)}
                style={{
                  background: 'rgba(220, 38, 38, 0.2)',
                  color: '#ffffff',
                  border: '1px solid rgba(220, 38, 38, 0.5)',
                  borderRadius: '8px',
                  padding: '10px 16px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: '600',
                  transition: 'all 0.3s ease',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}
                onMouseOver={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 0.3)';
                }}
                onMouseOut={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 0.2)';
                }}
              >
                ← Back to Admin Panel
              </button>
              <h1 style={{
                color: '#ffffff',
                fontSize: '1.8rem',
                margin: 0,
                fontWeight: '600',
                textShadow: '0 0 10px rgba(220, 38, 38, 0.3)'
              }}>
                User Results: {selectedUser.name}
              </h1>
            </div>
          </div>
        </div>

        {/* Content */}
        <div style={{
          maxWidth: '1600px',
          margin: '0 auto',
          padding: '30px 20px',
          position: 'relative',
          zIndex: 2
        }}>
          {/* User Info */}
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
            marginBottom: '30px'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600'
            }}>
              User Information
            </h2>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
              gap: '15px'
            }}>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  Name: {selectedUser.name}
                </div>
              </div>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  Email: {selectedUser.email}
                </div>
              </div>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  Role: {selectedUser.role}
                </div>
              </div>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  Role: {selectedUser.role}
                </div>
              </div>
              <div style={{
                background: 'rgba(34, 197, 94, 0.1)',
                borderRadius: '10px',
                padding: '15px',
                border: '1px solid rgba(34, 197, 94, 0.3)'
              }}>
                <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px' }}>
                  Completed Scans: {userCompletedPins.length}
                </div>
              </div>
            </div>
          </div>

          {/* Completed Scans */}
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600'
            }}>
              Completed Scan Results ({userCompletedPins.length})
            </h2>
            
            {userCompletedPins.length > 0 ? (
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))',
                gap: '20px'
              }}>
                {userCompletedPins.map((pin) => {
                  const parsedResults = parseScanResults(pin.scanResults);
                  return (
                    <div key={pin._id} style={{
                      background: 'rgba(34, 197, 94, 0.1)',
                      borderRadius: '15px',
                      padding: '25px',
                      border: '1px solid rgba(34, 197, 94, 0.3)',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease'
                    }}
                    onClick={() => {
                      setSelectedPin(pin);
                      setShowPinDetails(true);
                      setShowUserResults(false);
                    }}
                    onMouseOver={(e) => {
                      e.target.style.background = 'rgba(34, 197, 94, 0.2)';
                      e.target.style.transform = 'translateY(-3px)';
                    }}
                    onMouseOut={(e) => {
                      e.target.style.background = 'rgba(34, 197, 94, 0.1)';
                      e.target.style.transform = 'translateY(0)';
                    }}
                    >
                      <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '18px', fontWeight: '600', marginBottom: '15px' }}>
                        PIN: {pin.pin}
                      </div>
                      
                      <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '14px', marginBottom: '8px' }}>
                        Completed: {formatDate(pin.completedAt)}
                      </div>
                      
                      {parsedResults && (
                        <div style={{ marginTop: '15px' }}>
                          {parsedResults.systemInfo?.length > 0 && (
                            <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '12px', marginBottom: '5px' }}>
                              System Info: {parsedResults.systemInfo.length} items
                            </div>
                          )}
                          {parsedResults.fivemMods?.length > 0 && (
                            <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '12px', marginBottom: '5px' }}>
                              FiveM Mods: {parsedResults.fivemMods.length} mods
                            </div>
                          )}
                          {parsedResults.cheatWebsites?.length > 0 && (
                            <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '12px', marginBottom: '5px' }}>
                              Cheat Websites: {parsedResults.cheatWebsites.length} sites
                            </div>
                          )}
                          {parsedResults.discordAccount && (
                            <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '12px', marginBottom: '5px' }}>
                              Discord ID: {parsedResults.discordAccount}
                            </div>
                          )}
                          {pin.screenshot && (
                            <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '12px', marginBottom: '5px' }}>
                              Screenshot: Available
                            </div>
                          )}
                        </div>
                      )}
                      
                      <div style={{
                        marginTop: '15px',
                        padding: '8px 12px',
                        background: 'rgba(59, 130, 246, 0.2)',
                        borderRadius: '6px',
                        color: '#3b82f6',
                        fontSize: '12px',
                        fontWeight: '600',
                        textAlign: 'center'
                      }}>
                        Click to view full details
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <div style={{
                textAlign: 'center',
                color: 'rgba(255, 255, 255, 0.6)',
                fontSize: '16px',
                padding: '40px'
              }}>
                No completed scans found for this user.
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
      color: '#ffffff',
      position: 'relative',
      overflow: 'hidden'
    }}>
      <Toaster position="top-right" />
      
      {/* Header */}
      <div style={{
        background: 'rgba(20, 20, 20, 0.9)',
        backdropFilter: 'blur(20px)',
        padding: '20px',
        borderBottom: '1px solid rgba(220, 38, 38, 0.3)',
        position: 'relative',
        zIndex: 2
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          maxWidth: '1600px',
          margin: '0 auto'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
            <h1 style={{
              color: '#ffffff',
              fontSize: '2rem',
              margin: 0,
              fontWeight: '700',
              textShadow: '0 0 10px rgba(220, 38, 38, 0.3)'
            }}>
              VOID SCANNER ADMIN
            </h1>
          </div>
          
          <div style={{
            fontSize: '1.5rem',
            fontWeight: '700',
            color: '#ffffff',
            textShadow: '0 0 20px rgba(220, 38, 38, 0.5)'
          }}>
            ADMIN PANEL
          </div>
        </div>
      </div>

      {/* Content */}
      <div style={{
        maxWidth: '1600px',
        margin: '0 auto',
        padding: '30px 20px',
        position: 'relative',
        zIndex: 2
      }}>
        {/* Tabs */}
        <div style={{
          display: 'flex',
          gap: '10px',
          marginBottom: '30px',
          flexWrap: 'wrap'
        }}>
          <button
            onClick={() => setActiveTab('overview')}
            style={{
              background: activeTab === 'overview' ? 'rgba(220, 38, 38, 0.3)' : 'rgba(20, 20, 20, 0.8)',
              color: '#ffffff',
              border: '1px solid rgba(220, 38, 38, 0.5)',
              borderRadius: '8px',
              padding: '12px 24px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '600',
              transition: 'all 0.3s ease'
            }}
          >
            Overview
          </button>
          <button
            onClick={() => setActiveTab('users')}
            style={{
              background: activeTab === 'users' ? 'rgba(220, 38, 38, 0.3)' : 'rgba(20, 20, 20, 0.8)',
              color: '#ffffff',
              border: '1px solid rgba(220, 38, 38, 0.5)',
              borderRadius: '8px',
              padding: '12px 24px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '600',
              transition: 'all 0.3s ease'
            }}
          >
            Users ({users.length})
          </button>
          <button
            onClick={() => setActiveTab('pins')}
            style={{
              background: activeTab === 'pins' ? 'rgba(220, 38, 38, 0.3)' : 'rgba(20, 20, 20, 0.8)',
              color: '#ffffff',
              border: '1px solid rgba(220, 38, 38, 0.5)',
              borderRadius: '8px',
              padding: '12px 24px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '600',
              transition: 'all 0.3s ease'
            }}
          >
            PINs ({pins.length})
          </button>
          <button
            onClick={() => setActiveTab('enterprises')}
            style={{
              background: activeTab === 'enterprises' ? 'rgba(220, 38, 38, 0.3)' : 'rgba(20, 20, 20, 0.8)',
              color: '#ffffff',
              border: '1px solid rgba(220, 38, 38, 0.5)',
              borderRadius: '8px',
              padding: '12px 24px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '600',
              transition: 'all 0.3s ease'
            }}
          >
            Enterprises ({enterprises.length})
          </button>
        </div>

        {/* Overview Tab */}
        {activeTab === 'overview' && (
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
            gap: '20px',
            marginBottom: '30px'
          }}>
            <div style={{
              background: 'rgba(20, 20, 20, 0.8)',
              borderRadius: '15px',
              padding: '30px',
              border: '1px solid rgba(34, 197, 94, 0.3)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
            }}>
              <h3 style={{
                color: '#22c55e',
                fontSize: '1.2rem',
                margin: '0 0 15px 0',
                fontWeight: '600'
              }}>
                Total Users
              </h3>
              <div style={{
                fontSize: '2.5rem',
                fontWeight: '700',
                color: '#ffffff'
              }}>
                {stats.totalUsers}
              </div>
            </div>

            <div style={{
              background: 'rgba(20, 20, 20, 0.8)',
              borderRadius: '15px',
              padding: '30px',
              border: '1px solid rgba(59, 130, 246, 0.3)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
            }}>
              <h3 style={{
                color: '#3b82f6',
                fontSize: '1.2rem',
                margin: '0 0 15px 0',
                fontWeight: '600'
              }}>
                Total PINs
              </h3>
              <div style={{
                fontSize: '2.5rem',
                fontWeight: '700',
                color: '#ffffff'
              }}>
                {stats.totalPins}
              </div>
            </div>

            <div style={{
              background: 'rgba(20, 20, 20, 0.8)',
              borderRadius: '15px',
              padding: '30px',
              border: '1px solid rgba(16, 185, 129, 0.3)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
            }}>
              <h3 style={{
                color: '#10b981',
                fontSize: '1.2rem',
                margin: '0 0 15px 0',
                fontWeight: '600'
              }}>
                Completed Scans
              </h3>
              <div style={{
                fontSize: '2.5rem',
                fontWeight: '700',
                color: '#ffffff'
              }}>
                {stats.scansWithResults}
              </div>
            </div>

            <div style={{
              background: 'rgba(20, 20, 20, 0.8)',
              borderRadius: '15px',
              padding: '30px',
              border: '1px solid rgba(245, 158, 11, 0.3)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
            }}>
              <h3 style={{
                color: '#f59e0b',
                fontSize: '1.2rem',
                margin: '0 0 15px 0',
                fontWeight: '600'
              }}>
                Active PINs
              </h3>
              <div style={{
                fontSize: '2.5rem',
                fontWeight: '700',
                color: '#ffffff'
              }}>
                {stats.activePins}
              </div>
            </div>
          </div>
        )}

        {activeTab === 'overview' && (
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600'
            }}>
              Enterprises (who is in which enterprise)
            </h2>

            {enterprises?.length > 0 ? (
              <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fit,minmax(320px,1fr))', gap: '15px' }}>
                {enterprises.map((ent) => (
                  <div key={ent.id} style={{
                    background: 'rgba(34, 197, 94, 0.1)',
                    borderRadius: '10px',
                    padding: '20px',
                    border: '1px solid rgba(34, 197, 94, 0.3)'
                  }}>
                    <div style={{ color:'#fff', fontWeight:700, marginBottom:8 }}>
                      {ent.name} — {(ent.members?.length || 0)}/{ent.seats}
                    </div>
                    <div style={{ color:'rgba(255,255,255,0.8)', fontSize:14, marginBottom:6 }}>
                      Owner: {ent.ownerEmail || '-'}
                    </div>
                    <div style={{ color:'rgba(255,255,255,0.8)', fontSize:13 }}>
                      Members:
                      <ul style={{ margin:'6px 0 0 18px' }}>
                        {(ent.members || []).map((m,i) => (
                          <li key={i}>{m}</li>
                        ))}
                        {(ent.members || []).length === 0 && <li>-</li>}
                      </ul>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div style={{ color:'rgba(255,255,255,0.7)' }}>No enterprises yet</div>
            )}
          </div>
        )}

        {/* Users Tab */}
        {activeTab === 'users' && (
          <div>
            {/* Admin Permission Grant Section */}
            <div style={{
              background: 'rgba(20, 20, 20, 0.8)',
              borderRadius: '15px',
              padding: '30px',
              border: '1px solid rgba(220, 38, 38, 0.3)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
              marginBottom: '20px'
            }}>
              <h2 style={{
                color: '#ffffff',
                fontSize: '1.5rem',
                margin: '0 0 20px 0',
                fontWeight: '600'
              }}>
                Grant Admin Panel Access
              </h2>
              
              <form onSubmit={async (e) => {
                e.preventDefault();
                const email = e.target.email.value;
                if (!email) {
                  toast.error('Please enter an email address');
                  return;
                }
                
                try {
                  await axios.post('http://localhost:5005/api/admin/grant-admin-access', { email });
                  toast.success(`Admin panel access granted to ${email}`);
                  e.target.email.value = '';
                } catch (error) {
                  console.error('Grant admin access error:', error);
                  if (error.response?.status === 404) {
                    toast.error('User not found with this email');
                  } else if (error.response?.status === 400) {
                    toast.error(error.response.data.message || 'Invalid request');
                  } else {
                    toast.error('Failed to grant admin access');
                  }
                }
              }} style={{
                display: 'flex',
                gap: '15px',
                alignItems: 'center',
                flexWrap: 'wrap'
              }}>
                <input
                  type="email"
                  name="email"
                  placeholder="Enter user email to grant admin access"
                  required
                  style={{
                    background: 'rgba(20, 20, 20, 0.8)',
                    color: '#ffffff',
                    border: '1px solid rgba(220, 38, 38, 0.5)',
                    borderRadius: '8px',
                    padding: '12px 16px',
                    fontSize: '14px',
                    outline: 'none',
                    flex: '1',
                    minWidth: '300px'
                  }}
                />
                <button
                  type="submit"
                  style={{
                    background: 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)',
                    color: '#ffffff',
                    border: 'none',
                    borderRadius: '8px',
                    padding: '12px 24px',
                    fontSize: '14px',
                    fontWeight: '600',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    whiteSpace: 'nowrap'
                  }}
                  onMouseOver={(e) => {
                    e.target.style.transform = 'translateY(-2px)';
                    e.target.style.boxShadow = '0 8px 25px rgba(220, 38, 38, 0.5)';
                  }}
                  onMouseOut={(e) => {
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = 'none';
                  }}
                >
                  Grant Admin Access
                </button>
              </form>
              
              <div style={{
                marginTop: '15px',
                padding: '12px',
                background: 'rgba(220, 38, 38, 0.1)',
                borderRadius: '8px',
                border: '1px solid rgba(220, 38, 38, 0.3)',
                color: 'rgba(255, 255, 255, 0.8)',
                fontSize: '13px'
              }}>
                <strong>Note:</strong> This will add a "Panel" button to the user's dashboard, allowing them to access the admin panel.
              </div>
            </div>

            {/* Registered Users Section */}
            <div style={{
              background: 'rgba(20, 20, 20, 0.8)',
              borderRadius: '15px',
              padding: '30px',
              border: '1px solid rgba(220, 38, 38, 0.3)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
            }}>
              <h2 style={{
                color: '#ffffff',
                fontSize: '1.5rem',
                margin: '0 0 20px 0',
                fontWeight: '600'
              }}>
                Registered Users
              </h2>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
              gap: '15px'
            }}>
              {users.map((user) => {
                const userEnts = (enterprises || []).filter(e => (e.members || []).includes(user.email) || e.ownerEmail === user.email);
                const userEntLabel = userEnts.length > 0 ? userEnts.map(e => `${e.name} (${(e.members?.length||0)}/${e.seats})`).join(', ') : '-';
                return (
                <div key={user.id} style={{
                  background: 'rgba(34, 197, 94, 0.1)',
                  borderRadius: '10px',
                  padding: '20px',
                  border: '1px solid rgba(34, 197, 94, 0.3)',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
                onClick={() => handleUserClick(user)}
                onMouseOver={(e) => {
                  e.target.style.background = 'rgba(34, 197, 94, 0.2)';
                  e.target.style.transform = 'translateY(-2px)';
                }}
                onMouseOut={(e) => {
                  e.target.style.background = 'rgba(34, 197, 94, 0.1)';
                  e.target.style.transform = 'translateY(0)';
                }}
                >
                  <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '16px', fontWeight: '600', marginBottom: '8px' }}>
                    {user.name}
                  </div>
                  <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '14px', marginBottom: '4px' }}>
                    Email: {user.email}
                  </div>
                  <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '14px', marginBottom: '4px' }}>
                    Role: {user.role}
                  </div>
                  <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px', marginTop: '6px', background:'rgba(34,197,94,0.15)', border:'1px solid rgba(34,197,94,0.35)', borderRadius:8, padding:'8px 10px' }}>
                    Enterprise: {userEntLabel}
                  </div>
                  <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '14px' }}>
                    Joined: {formatDate(user.createdAt)}
                  </div>
                </div>
              )})}
            </div>
            </div>
          </div>
        )}

        {/* PINs Tab */}
        {activeTab === 'pins' && (
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '20px',
              flexWrap: 'wrap',
              gap: '15px'
            }}>
              <h2 style={{
                color: '#ffffff',
                fontSize: '1.5rem',
                margin: 0,
                fontWeight: '600'
              }}>
                All PINs
              </h2>
              
              <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                <input
                  type="text"
                  placeholder="Search PINs..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  style={{
                    background: 'rgba(20, 20, 20, 0.8)',
                    color: '#ffffff',
                    border: '1px solid rgba(220, 38, 38, 0.5)',
                    borderRadius: '8px',
                    padding: '8px 12px',
                    fontSize: '14px',
                    outline: 'none'
                  }}
                />
                
                <select
                  value={filterStatus}
                  onChange={(e) => setFilterStatus(e.target.value)}
                  style={{
                    background: 'rgba(20, 20, 20, 0.8)',
                    color: '#ffffff',
                    border: '1px solid rgba(220, 38, 38, 0.5)',
                    borderRadius: '8px',
                    padding: '8px 12px',
                    fontSize: '14px',
                    outline: 'none'
                  }}
                >
                  <option value="all">All Status</option>
                  <option value="active">Active</option>
                  <option value="completed">Completed</option>
                  <option value="expired">Expired</option>
                </select>
              </div>
            </div>
            
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
              gap: '15px'
            }}>
              {filteredPins.map((pin) => (
                <div key={pin._id} style={{
                  background: 'rgba(34, 197, 94, 0.1)',
                  borderRadius: '10px',
                  padding: '20px',
                  border: '1px solid rgba(34, 197, 94, 0.3)',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
                onClick={() => handlePinClick(pin)}
                onMouseOver={(e) => {
                  e.target.style.background = 'rgba(34, 197, 94, 0.2)';
                  e.target.style.transform = 'translateY(-2px)';
                }}
                onMouseOut={(e) => {
                  e.target.style.background = 'rgba(34, 197, 94, 0.1)';
                  e.target.style.transform = 'translateY(0)';
                }}
                >
                  <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '16px', fontWeight: '600', marginBottom: '8px' }}>
                    PIN: {pin.pin}
                  </div>
                  <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '14px', marginBottom: '4px' }}>
                    Status: <span style={{ color: getStatusColor(pin.status) }}>{pin.status}</span>
                  </div>
                  <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '14px', marginBottom: '4px' }}>
                    Created By: { (pin.creatorName && pin.creatorEmail)
                      ? `${pin.creatorName} - ${pin.creatorEmail}`
                      : (pin.creatorName || pin.creatorEmail || '-') }
                  </div>
                  <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '14px', marginBottom: '4px' }}>
                    Scan Completed: {pin.scanCompleted ? 'Yes' : 'No'}
                  </div>
                  <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '14px', marginBottom: '4px' }}>
                    Created: {formatDate(pin.createdAt)}
                  </div>
                  {pin.completedAt && (
                    <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '14px' }}>
                      Completed: {formatDate(pin.completedAt)}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'enterprises' && (
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600'
            }}>
              Enterprises
            </h2>

            {/* Create enterprise form */}
            <EnterpriseCreateForm 
              onCreated={(ent)=>{
                setEnterprises([ent, ...enterprises]);
                toast.success('Enterprise oluşturuldu');
              }}
            />

            {/* List */}
            <div style={{ marginTop: '20px', display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: '15px' }}>
              {enterprises.map((ent)=> (
                <div key={ent.id} style={{
                  background: 'rgba(34, 197, 94, 0.1)',
                  borderRadius: '10px',
                  padding: '20px',
                  border: '1px solid rgba(34, 197, 94, 0.3)'
                }}>
                  <div style={{ color:'#fff', fontWeight:600, marginBottom:8 }}>{ent.name}</div>
                  <div style={{ color:'rgba(255,255,255,0.8)', fontSize:14 }}>Owner: {ent.ownerEmail || '-'}</div>
                  <div style={{ color:'rgba(255,255,255,0.8)', fontSize:14 }}>Seats: {ent.members?.length || 0} / {ent.seats}</div>
                  <div style={{ color:'rgba(255,255,255,0.8)', fontSize:14 }}>Members: {(ent.members||[]).join(', ') || '-'}</div>
                  <div style={{ marginTop: 10, display:'flex', gap:10 }}>
                    <button onClick={async ()=>{
                      try {
                        await axios.delete(`http://localhost:5005/api/admin/enterprises/${ent.id}`);
                        toast.success('Enterprise deleted');
                        setEnterprises(enterprises.filter(e=>e.id!==ent.id));
                      } catch (err) {
                        toast.error('Delete failed');
                      }
                    }} style={{
                      background:'rgba(239,68,68,0.2)', color:'#ef4444', border:'1px solid rgba(239,68,68,0.5)', borderRadius:8, padding:'8px 12px', fontWeight:700, cursor:'pointer'
                    }}>Delete</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
