import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [enterprises, setEnterprises] = useState([]);
  const [pins, setPins] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showUserResults, setShowUserResults] = useState(false);
  const [userResults, setUserResults] = useState([]);
  const [isChangingPassword, setIsChangingPassword] = useState(false);
  const [newPassword, setNewPassword] = useState('');
  const [showBanModal, setShowBanModal] = useState(false);
  const [banReason, setBanReason] = useState('');
  const [activeTab, setActiveTab] = useState('dashboard');
  const [showEnterpriseForm, setShowEnterpriseForm] = useState(false);
  const [enterpriseName, setEnterpriseName] = useState('');
  const [enterpriseOwner, setEnterpriseOwner] = useState('');
  const [enterpriseSeats, setEnterpriseSeats] = useState(10);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [usersRes, enterprisesRes, pinsRes] = await Promise.all([
        axios.get('http://localhost:5005/api/users'),
        axios.get('http://localhost:5005/api/enterprises'),
        axios.get('http://localhost:5005/api/pins')
      ]);
      
      setUsers(usersRes.data);
      setEnterprises(enterprisesRes.data);
      setPins(pinsRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load admin data');
    } finally {
      setLoading(false);
    }
  };

  const handleUserClick = async (user) => {
    setSelectedUser(user);
    setShowUserResults(true);
    
    try {
      const response = await axios.get(`http://localhost:5005/api/user-results/${user.email}`);
      setUserResults(response.data);
    } catch (error) {
      console.error('Error fetching user results:', error);
      toast.error('Failed to load user results');
      setUserResults([]);
    }
  };

  const changeUserPassword = async () => {
    if (!selectedUser || !newPassword.trim()) {
      toast.error('Please enter a new password');
      return;
    }

    setIsChangingPassword(true);
    try {
      await axios.put(`http://localhost:5005/api/users/${selectedUser.email}/password`, {
        newPassword: newPassword.trim()
      });
      
      toast.success('Password changed successfully');
      setNewPassword('');
    } catch (error) {
      console.error('Error changing password:', error);
      toast.error('Failed to change password');
    } finally {
      setIsChangingPassword(false);
    }
  };

  const banUser = async () => {
    if (!selectedUser || !banReason.trim()) {
      toast.error('Please enter a ban reason');
      return;
    }

    try {
      await axios.post('http://localhost:5005/api/admin/ban-user', {
        email: selectedUser.email,
        reason: banReason.trim()
      });
      
      toast.success('User banned successfully');
      setShowBanModal(false);
      setBanReason('');
      fetchData();
    } catch (error) {
      console.error('Error banning user:', error);
      toast.error('Failed to ban user');
    }
  };

  const unbanUser = async (userEmail) => {
    try {
      await axios.post('http://localhost:5005/api/admin/unban-user', {
        email: userEmail
      });
      
      toast.success('User unbanned successfully');
      fetchData();
    } catch (error) {
      console.error('Error unbanning user:', error);
      toast.error('Failed to unban user');
    }
  };

  const createEnterprise = async () => {
    if (!enterpriseName.trim() || !enterpriseOwner.trim()) {
      toast.error('Please fill all required fields');
      return;
    }

    try {
      await axios.post('http://localhost:5005/api/enterprises', {
        name: enterpriseName.trim(),
        ownerEmail: enterpriseOwner.trim(),
        seats: enterpriseSeats
      });
      
      toast.success('Enterprise created successfully');
      setEnterpriseName('');
      setEnterpriseOwner('');
      setEnterpriseSeats(10);
      setShowEnterpriseForm(false);
      fetchData();
    } catch (error) {
      console.error('Error creating enterprise:', error);
      toast.error('Failed to create enterprise');
    }
  };

  const deleteEnterprise = async (enterpriseId) => {
    try {
      await axios.delete(`http://localhost:5005/api/enterprises/${enterpriseId}`);
      toast.success('Enterprise deleted successfully');
      fetchData();
    } catch (error) {
      console.error('Error deleting enterprise:', error);
      toast.error('Failed to delete enterprise');
    }
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0f0f0f 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#dc2626',
        fontFamily: 'Courier New, monospace'
      }}>
        <div style={{
          textAlign: 'center',
          fontSize: '18px',
          fontWeight: 'bold',
          textShadow: '0 0 20px rgba(220, 38, 38, 0.5)'
        }}>
          Loading admin data...
        </div>
      </div>
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #000000 0%, #0d0d0d 25%, #1a0a0a 50%, #0d0d0d 75%, #000000 100%)',
      color: '#ffffff',
      fontFamily: 'Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      display: 'flex',
      position: 'relative',
      overflow: 'hidden'
    }}>
      <Toaster position="top-right" />
      
      {/* Background Pattern */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: `
          radial-gradient(circle at 20% 80%, rgba(220, 38, 38, 0.03) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(220, 38, 38, 0.03) 0%, transparent 50%),
          radial-gradient(circle at 40% 40%, rgba(220, 38, 38, 0.02) 0%, transparent 50%)
        `,
        pointerEvents: 'none'
      }}></div>
      
      {/* Sidebar */}
      <div style={{
        width: '280px',
        background: 'linear-gradient(180deg, rgba(0, 0, 0, 0.95) 0%, rgba(10, 10, 10, 0.98) 100%)',
        borderRight: '3px solid #dc2626',
        padding: '0',
        boxShadow: '0 0 50px rgba(220, 38, 38, 0.4), inset -1px 0 0 rgba(220, 38, 38, 0.2)',
        position: 'relative',
        zIndex: 10
      }}>
        {/* Sidebar Header */}
        <div style={{
          padding: '30px 25px',
          borderBottom: '2px solid rgba(220, 38, 38, 0.3)',
          background: 'linear-gradient(135deg, rgba(220, 38, 38, 0.1) 0%, rgba(0, 0, 0, 0.8) 100%)',
          position: 'relative'
        }}>
          <div style={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            height: '2px',
            background: 'linear-gradient(90deg, transparent, #dc2626, transparent)',
            opacity: 0.6
          }}></div>
          
          <h1 style={{
            color: '#ffffff',
            fontSize: '24px',
            fontWeight: '800',
            textShadow: '0 0 30px rgba(220, 38, 38, 0.8), 0 2px 4px rgba(0, 0, 0, 0.5)',
            margin: '0 0 8px 0',
            letterSpacing: '1px',
            textTransform: 'uppercase'
          }}>
            VOID ADMIN
          </h1>
          
          <div style={{
            color: '#dc2626',
            fontSize: '12px',
            fontWeight: '600',
            letterSpacing: '2px',
            textTransform: 'uppercase',
            opacity: 0.8
          }}>
            CONTROL PANEL
          </div>
          
          <div style={{
            position: 'absolute',
            bottom: 0,
            left: '25px',
            right: '25px',
            height: '1px',
            background: 'linear-gradient(90deg, transparent, rgba(220, 38, 38, 0.5), transparent)'
          }}></div>
        </div>

        {/* Navigation */}
        <nav style={{ padding: '25px 20px 0 20px' }}>
          <div style={{ marginBottom: '12px' }}>
            <button
              onClick={() => setActiveTab('dashboard')}
              style={{
                width: '100%',
                padding: '16px 20px',
                background: activeTab === 'dashboard' 
                  ? 'linear-gradient(135deg, rgba(220, 38, 38, 0.25) 0%, rgba(220, 38, 38, 0.1) 100%)' 
                  : 'transparent',
                border: activeTab === 'dashboard' 
                  ? '2px solid #dc2626' 
                  : '2px solid rgba(220, 38, 38, 0.3)',
                borderRadius: '12px',
                color: activeTab === 'dashboard' ? '#ffffff' : '#cccccc',
                fontSize: '15px',
                fontWeight: activeTab === 'dashboard' ? '700' : '600',
                cursor: 'pointer',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                textAlign: 'left',
                position: 'relative',
                overflow: 'hidden',
                boxShadow: activeTab === 'dashboard' 
                  ? '0 8px 32px rgba(220, 38, 38, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)' 
                  : '0 4px 16px rgba(0, 0, 0, 0.2)',
                textShadow: activeTab === 'dashboard' ? '0 0 20px rgba(220, 38, 38, 0.6)' : 'none'
              }}
              onMouseOver={(e) => {
                if (activeTab !== 'dashboard') {
                  e.target.style.background = 'linear-gradient(135deg, rgba(220, 38, 38, 0.15) 0%, rgba(220, 38, 38, 0.05) 100%)';
                  e.target.style.border = '2px solid rgba(220, 38, 38, 0.6)';
                  e.target.style.color = '#ffffff';
                  e.target.style.transform = 'translateX(4px)';
                  e.target.style.boxShadow = '0 8px 24px rgba(220, 38, 38, 0.2)';
                }
              }}
              onMouseOut={(e) => {
                if (activeTab !== 'dashboard') {
                  e.target.style.background = 'transparent';
                  e.target.style.border = '2px solid rgba(220, 38, 38, 0.3)';
                  e.target.style.color = '#cccccc';
                  e.target.style.transform = 'translateX(0)';
                  e.target.style.boxShadow = '0 4px 16px rgba(0, 0, 0, 0.2)';
                }
              }}
            >
              Dashboard
            </button>
          </div>

          <div style={{ marginBottom: '12px' }}>
            <button
              onClick={() => setActiveTab('users')}
              style={{
                width: '100%',
                padding: '16px 20px',
                background: activeTab === 'users' 
                  ? 'linear-gradient(135deg, rgba(220, 38, 38, 0.25) 0%, rgba(220, 38, 38, 0.1) 100%)' 
                  : 'transparent',
                border: activeTab === 'users' 
                  ? '2px solid #dc2626' 
                  : '2px solid rgba(220, 38, 38, 0.3)',
                borderRadius: '12px',
                color: activeTab === 'users' ? '#ffffff' : '#cccccc',
                fontSize: '15px',
                fontWeight: activeTab === 'users' ? '700' : '600',
                cursor: 'pointer',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                textAlign: 'left',
                position: 'relative',
                overflow: 'hidden',
                boxShadow: activeTab === 'users' 
                  ? '0 8px 32px rgba(220, 38, 38, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.1)' 
                  : '0 4px 16px rgba(0, 0, 0, 0.2)',
                textShadow: activeTab === 'users' ? '0 0 20px rgba(220, 38, 38, 0.6)' : 'none'
              }}
              onMouseOver={(e) => {
                if (activeTab !== 'users') {
                  e.target.style.background = 'linear-gradient(135deg, rgba(220, 38, 38, 0.15) 0%, rgba(220, 38, 38, 0.05) 100%)';
                  e.target.style.border = '2px solid rgba(220, 38, 38, 0.6)';
                  e.target.style.color = '#ffffff';
                  e.target.style.transform = 'translateX(4px)';
                  e.target.style.boxShadow = '0 8px 24px rgba(220, 38, 38, 0.2)';
                }
              }}
              onMouseOut={(e) => {
                if (activeTab !== 'users') {
                  e.target.style.background = 'transparent';
                  e.target.style.border = '2px solid rgba(220, 38, 38, 0.3)';
                  e.target.style.color = '#cccccc';
                  e.target.style.transform = 'translateX(0)';
                  e.target.style.boxShadow = '0 4px 16px rgba(0, 0, 0, 0.2)';
                }
              }}
            >
              Users ({users.length})
            </button>
          </div>

