                color: activeTab === 'pins' ? '#ffffff' : '#cccccc',
                fontSize: '15px',
                fontWeight: activeTab === 'pins' ? '700' : '600',
                cursor: 'pointer',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                textAlign: 'left',
                position: 'relative',
