import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const EnterpriseCreateForm = ({ onCreated }) => {
  const [name, setName] = React.useState('');
  const [ownerEmail, setOwnerEmail] = React.useState('');
  const [seats, setSeats] = React.useState(10);
  const [loading, setLoading] = React.useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const response = await axios.post('http://localhost:5005/api/enterprises', {
        name,
        ownerEmail,
        seats
      });
      
      toast.success('Enterprise created successfully!');
      onCreated();
      setName('');
      setOwnerEmail('');
      setSeats(10);
    } catch (error) {
      console.error('Error creating enterprise:', error);
      toast.error('Failed to create enterprise');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      background: 'rgba(20, 20, 20, 0.9)',
      borderRadius: '15px',
      padding: '30px',
      border: '1px solid rgba(75, 85, 99, 0.3)',
      boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
      marginBottom: '30px'
    }}>
      <h3 style={{
        color: '#10b981',
        marginBottom: '20px',
        fontSize: '20px',
        fontWeight: 'bold'
      }}>
        Create New Enterprise
      </h3>
      
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '20px' }}>
          <label style={{
            display: 'block',
            color: '#d1d5db',
            marginBottom: '8px',
            fontSize: '14px',
            fontWeight: '500'
          }}>
            Enterprise Name:
          </label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '12px',
              background: 'rgba(30, 30, 30, 0.8)',
              border: '1px solid rgba(75, 85, 99, 0.5)',
              borderRadius: '8px',
              color: '#e5e7eb',
              fontSize: '14px',
              outline: 'none',
              transition: 'border-color 0.3s ease'
            }}
            onFocus={(e) => {
              e.target.style.borderColor = 'rgba(16, 185, 129, 0.5)';
            }}
            onBlur={(e) => {
              e.target.style.borderColor = 'rgba(75, 85, 99, 0.5)';
            }}
          />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{
            display: 'block',
            color: '#d1d5db',
            marginBottom: '8px',
            fontSize: '14px',
            fontWeight: '500'
          }}>
            Owner Email:
          </label>
          <input
            type="email"
            value={ownerEmail}
            onChange={(e) => setOwnerEmail(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '12px',
              background: 'rgba(30, 30, 30, 0.8)',
              border: '1px solid rgba(75, 85, 99, 0.5)',
              borderRadius: '8px',
              color: '#e5e7eb',
              fontSize: '14px',
              outline: 'none',
              transition: 'border-color 0.3s ease'
            }}
            onFocus={(e) => {
              e.target.style.borderColor = 'rgba(16, 185, 129, 0.5)';
            }}
            onBlur={(e) => {
              e.target.style.borderColor = 'rgba(75, 85, 99, 0.5)';
            }}
          />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label style={{
            display: 'block',
            color: '#d1d5db',
            marginBottom: '8px',
            fontSize: '14px',
            fontWeight: '500'
          }}>
            Number of Seats:
          </label>
          <input
            type="number"
            value={seats}
            onChange={(e) => setSeats(parseInt(e.target.value))}
            min="1"
            required
            style={{
              width: '100%',
              padding: '12px',
              background: 'rgba(30, 30, 30, 0.8)',
              border: '1px solid rgba(75, 85, 99, 0.5)',
              borderRadius: '8px',
              color: '#e5e7eb',
              fontSize: '14px',
              outline: 'none',
              transition: 'border-color 0.3s ease'
            }}
            onFocus={(e) => {
              e.target.style.borderColor = 'rgba(16, 185, 129, 0.5)';
            }}
            onBlur={(e) => {
              e.target.style.borderColor = 'rgba(75, 85, 99, 0.5)';
            }}
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          style={{
            background: loading ? 'rgba(75, 85, 99, 0.8)' : 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
            color: '#ffffff',
            border: 'none',
            borderRadius: '8px',
            padding: '12px 24px',
            fontSize: '14px',
            fontWeight: '500',
            cursor: loading ? 'not-allowed' : 'pointer',
            transition: 'all 0.3s ease',
            boxShadow: '0 4px 15px rgba(16, 185, 129, 0.3)',
            opacity: loading ? 0.6 : 1
          }}
          onMouseOver={(e) => {
            if (!loading) {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 8px 25px rgba(16, 185, 129, 0.5)';
            }
          }}
          onMouseOut={(e) => {
            if (!loading) {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 4px 15px rgba(16, 185, 129, 0.3)';
            }
          }}
        >
          {loading ? 'Creating...' : 'Create Enterprise'}
        </button>
      </form>
    </div>
  );
};

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [enterprises, setEnterprises] = useState([]);
  const [pins, setPins] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showUserResults, setShowUserResults] = useState(false);
  const [userResults, setUserResults] = useState([]);
  const [isChangingPassword, setIsChangingPassword] = useState(false);
  const [newPassword, setNewPassword] = useState('');
  const [showBanModal, setShowBanModal] = useState(false);
  const [banReason, setBanReason] = useState('');
  const [showEnterpriseForm, setShowEnterpriseForm] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [usersRes, enterprisesRes, pinsRes] = await Promise.all([
        axios.get('http://localhost:5005/api/users'),
        axios.get('http://localhost:5005/api/enterprises'),
        axios.get('http://localhost:5005/api/pins')
      ]);
      
      setUsers(usersRes.data);
      setEnterprises(enterprisesRes.data);
      setPins(pinsRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load admin data');
    } finally {
      setLoading(false);
    }
  };

  const handleUserClick = async (user) => {
    setSelectedUser(user);
    setShowUserResults(true);
    
    try {
      const response = await axios.get(`http://localhost:5005/api/user-results/${user.email}`);
      setUserResults(response.data);
    } catch (error) {
      console.error('Error fetching user results:', error);
      toast.error('Failed to load user results');
      setUserResults([]);
    }
  };

  const changeUserPassword = async () => {
    if (!selectedUser || !newPassword.trim()) {
      toast.error('Please enter a new password');
      return;
    }

    setIsChangingPassword(true);
    try {
      await axios.put(`http://localhost:5005/api/users/${selectedUser.email}/password`, {
        newPassword: newPassword.trim()
      });
      
      toast.success('Password changed successfully');
      setNewPassword('');
    } catch (error) {
      console.error('Error changing password:', error);
      toast.error('Failed to change password');
    } finally {
      setIsChangingPassword(false);
    }
  };

  const deleteEnterprise = async (enterpriseId) => {
    try {
      await axios.delete(`http://localhost:5005/api/enterprises/${enterpriseId}`);
      toast.success('Enterprise deleted successfully');
      fetchData();
    } catch (error) {
      console.error('Error deleting enterprise:', error);
      toast.error('Failed to delete enterprise');
    }
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#e5e7eb'
      }}>
        <div style={{
          textAlign: 'center',
          fontSize: '18px',
          fontWeight: '500'
        }}>
          Loading admin data...
        </div>
      </div>
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f172a 0%, #1e293b 100%)',
      color: '#e5e7eb',
      fontFamily: 'Inter, -apple-system, BlinkMacSystemFont, sans-serif'
    }}>
      <Toaster position="top-right" />
      
      {/* Header */}
      <div style={{
        background: 'rgba(15, 23, 42, 0.8)',
        borderBottom: '1px solid rgba(75, 85, 99, 0.3)',
        padding: '20px 0',
        backdropFilter: 'blur(10px)'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <h1 style={{
            color: '#10b981',
            fontSize: '28px',
            fontWeight: 'bold',
            margin: 0
          }}>
            Void Scanner Admin
          </h1>
          
          <div style={{ display: 'flex', gap: '15px' }}>
            <button
              onClick={() => setShowEnterpriseForm(!showEnterpriseForm)}
              style={{
                background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                color: '#ffffff',
                border: 'none',
                borderRadius: '8px',
                padding: '10px 20px',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                boxShadow: '0 4px 15px rgba(16, 185, 129, 0.3)'
              }}
              onMouseOver={(e) => {
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 8px 25px rgba(16, 185, 129, 0.5)';
              }}
              onMouseOut={(e) => {
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = '0 4px 15px rgba(16, 185, 129, 0.3)';
              }}
            >
              {showEnterpriseForm ? 'Hide Form' : 'Create Enterprise'}
            </button>
            
            <button
              onClick={fetchData}
              style={{
                background: 'rgba(75, 85, 99, 0.8)',
                color: '#e5e7eb',
                border: 'none',
                borderRadius: '8px',
                padding: '10px 20px',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
              onMouseOver={(e) => {
                e.target.style.background = 'rgba(75, 85, 99, 1)';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onMouseOut={(e) => {
                e.target.style.background = 'rgba(75, 85, 99, 0.8)';
                e.target.style.transform = 'translateY(0)';
              }}
            >
              Refresh Data
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '30px 20px'
      }}>
        {showEnterpriseForm && (
          <EnterpriseCreateForm onCreated={() => {
            setShowEnterpriseForm(false);
            fetchData();
          }} />
        )}

        {showUserResults ? (
          <div>
            <button
              onClick={() => {
                setShowUserResults(false);
                setSelectedUser(null);
                setUserResults([]);
              }}
              style={{
                background: 'rgba(75, 85, 99, 0.8)',
                color: '#e5e7eb',
                border: 'none',
                borderRadius: '8px',
                padding: '10px 20px',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                marginBottom: '20px'
              }}
              onMouseOver={(e) => {
                e.target.style.background = 'rgba(75, 85, 99, 1)';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onMouseOut={(e) => {
                e.target.style.background = 'rgba(75, 85, 99, 0.8)';
                e.target.style.transform = 'translateY(0)';
              }}
            >
              Back to Admin Panel
            </button>

            <h2 style={{
              color: '#10b981',
              fontSize: '24px',
              fontWeight: 'bold',
              marginBottom: '20px'
            }}>
              User Results: {selectedUser?.username}
            </h2>

            {/* User Information */}
            <div style={{
              background: 'rgba(20, 20, 20, 0.9)',
              borderRadius: '15px',
              padding: '25px',
              border: '1px solid rgba(75, 85, 99, 0.3)',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
              marginBottom: '30px'
            }}>
              <h3 style={{
                color: '#10b981',
                fontSize: '18px',
                fontWeight: 'bold',
                marginBottom: '15px'
              }}>
                User Information
              </h3>
              
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
                <div>
                  <strong style={{ color: '#d1d5db' }}>Name:</strong>
                  <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{selectedUser?.username}</span>
                </div>
                <div>
                  <strong style={{ color: '#d1d5db' }}>Email:</strong>
                  <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{selectedUser?.email}</span>
                </div>
                <div>
                  <strong style={{ color: '#d1d5db' }}>Role:</strong>
                  <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{selectedUser?.role}</span>
                </div>
                <div>
                  <strong style={{ color: '#d1d5db' }}>Completed Scans:</strong>
                  <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{userResults.length}</span>
                </div>
              </div>

              <div style={{ marginTop: '20px', display: 'flex', gap: '15px', alignItems: 'center' }}>
                <input
                  type="password"
                  placeholder="New password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  style={{
                    padding: '8px 12px',
                    background: 'rgba(30, 30, 30, 0.8)',
                    border: '1px solid rgba(75, 85, 99, 0.5)',
                    borderRadius: '6px',
                    color: '#e5e7eb',
                    fontSize: '14px',
                    outline: 'none',
                    width: '200px'
                  }}
                />
                <button
                  onClick={changeUserPassword}
                  disabled={isChangingPassword}
                  style={{
                    background: isChangingPassword ? 'rgba(75, 85, 99, 0.8)' : 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                    color: '#ffffff',
                    border: 'none',
                    borderRadius: '6px',
                    padding: '8px 16px',
                    fontSize: '14px',
                    fontWeight: '500',
                    cursor: isChangingPassword ? 'not-allowed' : 'pointer',
                    transition: 'all 0.3s ease',
                    opacity: isChangingPassword ? 0.6 : 1
                  }}
                  onMouseOver={(e) => {
                    if (!isChangingPassword) {
                      e.target.style.transform = 'translateY(-2px)';
                      e.target.style.boxShadow = '0 4px 15px rgba(59, 130, 246, 0.3)';
                    }
                  }}
                  onMouseOut={(e) => {
                    if (!isChangingPassword) {
                      e.target.style.transform = 'translateY(0)';
                      e.target.style.boxShadow = 'none';
                    }
                  }}
                >
                  {isChangingPassword ? 'Changing...' : 'Reset Password'}
                </button>
                <button
                  onClick={() => {
                    console.log('Ban User button clicked');
                    console.log('showBanModal before:', showBanModal);
                    setShowBanModal(true);
                    setBanReason('');
                    console.log('setShowBanModal(true) called');
                  }}
                  style={{
                    background: 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
                    color: '#ffffff',
                    border: 'none',
                    borderRadius: '6px',
                    padding: '8px 16px',
                    fontSize: '14px',
                    fontWeight: '500',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 4px 15px rgba(239, 68, 68, 0.3)'
                  }}
                  onMouseOver={(e) => {
                    e.target.style.transform = 'translateY(-2px)';
                    e.target.style.boxShadow = '0 8px 25px rgba(239, 68, 68, 0.5)';
                  }}
                  onMouseOut={(e) => {
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = '0 4px 15px rgba(239, 68, 68, 0.3)';
                  }}
                >
                  Ban User
                </button>
              </div>
            </div>

            {/* Completed Scan Results */}
            <h3 style={{
              color: '#10b981',
              fontSize: '18px',
              fontWeight: 'bold',
              marginBottom: '20px'
            }}>
              Completed Scan Results ({userResults.length})
            </h3>

            {userResults.length === 0 ? (
              <div style={{
                background: 'rgba(20, 20, 20, 0.9)',
                borderRadius: '15px',
                padding: '40px',
                border: '1px solid rgba(75, 85, 99, 0.3)',
                textAlign: 'center',
                color: '#9ca3af'
              }}>
                No scan results found for this user.
              </div>
            ) : (
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                gap: '20px'
              }}>
                {userResults.map((result, index) => (
                  <div key={index} style={{
                    background: 'rgba(20, 20, 20, 0.9)',
                    borderRadius: '15px',
                    padding: '20px',
                    border: '1px solid rgba(16, 185, 129, 0.3)',
                    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
                    transition: 'all 0.3s ease'
                  }}>
                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#10b981' }}>PIN:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{result.pin}</span>
                    </div>
                    
                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#10b981' }}>Completed:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>
                        {new Date(result.completedAt).toLocaleString()}
                      </span>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#10b981' }}>System Info:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>
                        {result.systemInfo ? Object.keys(result.systemInfo).length : 0} items
                      </span>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#10b981' }}>FiveM Mods:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>
                        {result.fivemMods ? result.fivemMods.length : 0} mods
                      </span>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#10b981' }}>Cheat Websites:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>
                        {result.cheatWebsites ? result.cheatWebsites.length : 0} sites
                      </span>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#10b981' }}>Discord ID:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>
                        {result.discordId || 'Not found'}
                      </span>
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                      <strong style={{ color: '#10b981' }}>Screenshot:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>
                        {result.screenshot ? 'Available' : 'Not available'}
                      </span>
                    </div>

                    <button
                      onClick={() => {
                        // Open detailed results in new tab
                        const resultsWindow = window.open('', '_blank');
                        resultsWindow.document.write(`
                          <!DOCTYPE html>
                          <html>
                            <head>
                              <title>Scan Results - ${result.pin}</title>
                              <style>
                                body {
                                  background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
                                  color: #e5e7eb;
                                  font-family: Inter, -apple-system, BlinkMacSystemFont, sans-serif;
                                  margin: 0;
                                  padding: 20px;
                                }
                                .container {
                                  max-width: 1200px;
                                  margin: 0 auto;
                                }
                                .header {
                                  text-align: center;
                                  margin-bottom: 30px;
                                }
                                .section {
                                  background: rgba(20, 20, 20, 0.9);
                                  border-radius: 15px;
                                  padding: 25px;
                                  margin-bottom: 20px;
                                  border: 1px solid rgba(75, 85, 99, 0.3);
                                  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
                                }
                                .section h3 {
                                  color: #10b981;
                                  margin-bottom: 15px;
                                  font-size: 18px;
                                }
                                .info-grid {
                                  display: grid;
                                  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
                                  gap: 15px;
                                }
                                .info-item {
                                  background: rgba(30, 30, 30, 0.8);
                                  padding: 12px;
                                  border-radius: 8px;
                                  border: 1px solid rgba(75, 85, 99, 0.3);
                                }
                                .info-label {
                                  color: #10b981;
                                  font-weight: bold;
                                  font-size: 12px;
                                  margin-bottom: 5px;
                                }
                                .info-value {
                                  color: #e5e7eb;
                                  font-size: 14px;
                                }
                                .mod-item {
                                  background: rgba(30, 30, 30, 0.8);
                                  padding: 10px;
                                  border-radius: 6px;
                                  margin-bottom: 8px;
                                  border-left: 3px solid #10b981;
                                }
                                .screenshot {
                                  max-width: 100%;
                                  border-radius: 8px;
                                  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
                                }
                              </style>
                            </head>
                            <body>
                              <div class="container">
                                <div class="header">
                                  <h1 style="color: #10b981; font-size: 28px;">Scan Results</h1>
                                  <p style="color: #9ca3af;">PIN: ${result.pin} | Completed: ${new Date(result.completedAt).toLocaleString()}</p>
                                </div>

                                ${result.systemInfo ? `
                                <div class="section">
                                  <h3>System Information</h3>
                                  <div class="info-grid">
                                    ${Object.entries(result.systemInfo).map(([key, value]) => `
                                      <div class="info-item">
                                        <div class="info-label">${key}</div>
                                        <div class="info-value">${value}</div>
                                      </div>
                                    `).join('')}
                                  </div>
                                </div>
                                ` : ''}

                                ${result.fivemMods && result.fivemMods.length > 0 ? `
                                <div class="section">
                                  <h3>FiveM Mods (${result.fivemMods.length})</h3>
                                  ${result.fivemMods.map(mod => `
                                    <div class="mod-item">📄 ${mod}</div>
                                  `).join('')}
                                </div>
                                ` : ''}

                                ${result.cheatWebsites && result.cheatWebsites.length > 0 ? `
                                <div class="section">
                                  <h3>Cheat Websites (${result.cheatWebsites.length})</h3>
                                  ${result.cheatWebsites.map(site => `
                                    <div class="mod-item">🌐 ${site}</div>
                                  `).join('')}
                                </div>
                                ` : ''}

                                ${result.discordId ? `
                                <div class="section">
                                  <h3>Discord Account</h3>
                                  <div class="info-item">
                                    <div class="info-label">Discord ID</div>
                                    <div class="info-value">${result.discordId}</div>
                                  </div>
                                </div>
                                ` : ''}

                                ${result.screenshot ? `
                                <div class="section">
                                  <h3>PC Screenshot</h3>
                                  <img src="data:image/png;base64,${result.screenshot}" alt="PC Screenshot" class="screenshot">
                                </div>
                                ` : ''}
                              </div>
                            </body>
                          </html>
                        `);
                        resultsWindow.document.close();
                      }}
                      style={{
                        background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
                        color: '#ffffff',
                        border: 'none',
                        borderRadius: '8px',
                        padding: '10px 20px',
                        fontSize: '14px',
                        fontWeight: '500',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease',
                        boxShadow: '0 4px 15px rgba(59, 130, 246, 0.3)',
                        width: '100%'
                      }}
                      onMouseOver={(e) => {
                        e.target.style.transform = 'translateY(-2px)';
                        e.target.style.boxShadow = '0 8px 25px rgba(59, 130, 246, 0.5)';
                      }}
                      onMouseOut={(e) => {
                        e.target.style.transform = 'translateY(0)';
                        e.target.style.boxShadow = '0 4px 15px rgba(59, 130, 246, 0.3)';
                      }}
                    >
                      Click to view full details
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        ) : (
          <div>
            {/* Users Section */}
            <div style={{
              background: 'rgba(20, 20, 20, 0.9)',
              borderRadius: '15px',
              padding: '25px',
              border: '1px solid rgba(75, 85, 99, 0.3)',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
              marginBottom: '30px'
            }}>
              <h2 style={{
                color: '#10b981',
                fontSize: '20px',
                fontWeight: 'bold',
                marginBottom: '20px'
              }}>
                Users ({users.length})
              </h2>
              
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                gap: '15px'
              }}>
                {users.map((user, index) => (
                  <div key={index} style={{
                    background: 'rgba(30, 30, 30, 0.8)',
                    borderRadius: '10px',
                    padding: '15px',
                    border: '1px solid rgba(75, 85, 99, 0.3)',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease'
                  }}
                  onMouseOver={(e) => {
                    e.target.style.borderColor = 'rgba(16, 185, 129, 0.5)';
                    e.target.style.transform = 'translateY(-2px)';
                  }}
                  onMouseOut={(e) => {
                    e.target.style.borderColor = 'rgba(75, 85, 99, 0.3)';
                    e.target.style.transform = 'translateY(0)';
                  }}
                  onClick={() => handleUserClick(user)}
                  >
                    <div style={{ marginBottom: '8px' }}>
                      <strong style={{ color: '#10b981' }}>Name:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{user.username}</span>
                    </div>
                    <div style={{ marginBottom: '8px' }}>
                      <strong style={{ color: '#10b981' }}>Email:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{user.email}</span>
                    </div>
                    <div>
                      <strong style={{ color: '#10b981' }}>Role:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{user.role}</span>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Enterprises Section */}
            <div style={{
              background: 'rgba(20, 20, 20, 0.9)',
              borderRadius: '15px',
              padding: '25px',
              border: '1px solid rgba(75, 85, 99, 0.3)',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
              marginBottom: '30px'
            }}>
              <h2 style={{
                color: '#10b981',
                fontSize: '20px',
                fontWeight: 'bold',
                marginBottom: '20px'
              }}>
                Enterprises ({enterprises.length})
              </h2>
              
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                gap: '15px'
              }}>
                {enterprises.map((enterprise, index) => (
                  <div key={index} style={{
                    background: 'rgba(30, 30, 30, 0.8)',
                    borderRadius: '10px',
                    padding: '15px',
                    border: '1px solid rgba(75, 85, 99, 0.3)',
                    position: 'relative'
                  }}>
                    <div style={{ marginBottom: '8px' }}>
                      <strong style={{ color: '#10b981' }}>Name:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{enterprise.name}</span>
                    </div>
                    <div style={{ marginBottom: '8px' }}>
                      <strong style={{ color: '#10b981' }}>Owner:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{enterprise.ownerEmail}</span>
                    </div>
                    <div style={{ marginBottom: '8px' }}>
                      <strong style={{ color: '#10b981' }}>Seats:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{enterprise.seats}</span>
                    </div>
                    <div style={{ marginBottom: '8px' }}>
                      <strong style={{ color: '#10b981' }}>Members:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{enterprise.members ? enterprise.members.length : 0}</span>
                    </div>
                    <button
                      onClick={() => deleteEnterprise(enterprise._id)}
                      style={{
                        position: 'absolute',
                        top: '10px',
                        right: '10px',
                        background: 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
                        color: '#ffffff',
                        border: 'none',
                        borderRadius: '6px',
                        padding: '6px 12px',
                        fontSize: '12px',
                        fontWeight: '500',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease',
                        boxShadow: '0 2px 8px rgba(239, 68, 68, 0.3)'
                      }}
                      onMouseOver={(e) => {
                        e.target.style.transform = 'scale(1.05)';
                        e.target.style.boxShadow = '0 4px 15px rgba(239, 68, 68, 0.5)';
                      }}
                      onMouseOut={(e) => {
                        e.target.style.transform = 'scale(1)';
                        e.target.style.boxShadow = '0 2px 8px rgba(239, 68, 68, 0.3)';
                      }}
                    >
                      Delete
                    </button>
                  </div>
                ))}
              </div>
            </div>

            {/* Pins Section */}
            <div style={{
              background: 'rgba(20, 20, 20, 0.9)',
              borderRadius: '15px',
              padding: '25px',
              border: '1px solid rgba(75, 85, 99, 0.3)',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)'
            }}>
              <h2 style={{
                color: '#10b981',
                fontSize: '20px',
                fontWeight: 'bold',
                marginBottom: '20px'
              }}>
                ALL PINS ({pins.length})
              </h2>
              
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
                gap: '15px'
              }}>
                {pins.map((pin, index) => (
                  <div key={index} style={{
                    background: 'rgba(16, 185, 129, 0.1)',
                    borderRadius: '10px',
                    padding: '15px',
                    border: '1px solid rgba(16, 185, 129, 0.3)',
                    position: 'relative'
                  }}>
                    <div style={{ marginBottom: '8px' }}>
                      <strong style={{ color: '#10b981' }}>PIN:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{pin.pin}</span>
                    </div>
                    <div style={{ marginBottom: '8px' }}>
                      <strong style={{ color: '#10b981' }}>Created By:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{pin.creatorEmail}</span>
                    </div>
                    <div style={{ marginBottom: '8px' }}>
                      <strong style={{ color: '#10b981' }}>Status:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>{pin.status}</span>
                    </div>
                    <div>
                      <strong style={{ color: '#10b981' }}>Created:</strong>
                      <span style={{ color: '#e5e7eb', marginLeft: '8px' }}>
                        {new Date(pin.createdAt).toLocaleString()}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
