import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [enterprises, setEnterprises] = useState([]);
  const [pins, setPins] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showUserResults, setShowUserResults] = useState(false);
  const [userResults, setUserResults] = useState([]);
  const [isChangingPassword, setIsChangingPassword] = useState(false);
  const [newPassword, setNewPassword] = useState('');
  const [showBanModal, setShowBanModal] = useState(false);
  const [banReason, setBanReason] = useState('');
  const [activeTab, setActiveTab] = useState('dashboard');
  const [showEnterpriseForm, setShowEnterpriseForm] = useState(false);
  const [enterpriseName, setEnterpriseName] = useState('');
  const [enterpriseOwner, setEnterpriseOwner] = useState('');
  const [enterpriseSeats, setEnterpriseSeats] = useState(10);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [usersRes, enterprisesRes, pinsRes] = await Promise.all([
        axios.get('http://localhost:5005/api/users'),
        axios.get('http://localhost:5005/api/enterprises'),
        axios.get('http://localhost:5005/api/pins')
      ]);
      
      setUsers(usersRes.data);
      setEnterprises(enterprisesRes.data);
      setPins(pinsRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load admin data');
    } finally {
      setLoading(false);
    }
  };

  const handleUserClick = async (user) => {
    setSelectedUser(user);
    setShowUserResults(true);
    
    try {
      const response = await axios.get(`http://localhost:5005/api/user-results/${user.email}`);
      setUserResults(response.data);
    } catch (error) {
      console.error('Error fetching user results:', error);
      toast.error('Failed to load user results');
      setUserResults([]);
    }
  };

  const changeUserPassword = async () => {
    if (!selectedUser || !newPassword.trim()) {
      toast.error('Please enter a new password');
      return;
    }

    setIsChangingPassword(true);
    try {
      await axios.put(`http://localhost:5005/api/users/${selectedUser.email}/password`, {
        newPassword: newPassword.trim()
      });
      
      toast.success('Password changed successfully');
      setNewPassword('');
    } catch (error) {
      console.error('Error changing password:', error);
      toast.error('Failed to change password');
    } finally {
      setIsChangingPassword(false);
    }
  };

  const banUser = async () => {
    if (!selectedUser || !banReason.trim()) {
      toast.error('Please enter a ban reason');
      return;
    }

    try {
      await axios.post('http://localhost:5005/api/admin/ban-user', {
        email: selectedUser.email,
        reason: banReason.trim()
      });
      
      toast.success('User banned successfully');
      setShowBanModal(false);
      setBanReason('');
      fetchData();
    } catch (error) {
      console.error('Error banning user:', error);
      toast.error('Failed to ban user');
    }
  };

  const createEnterprise = async () => {
    if (!enterpriseName.trim() || !enterpriseOwner.trim()) {
      toast.error('Please fill all required fields');
      return;
    }

    try {
      await axios.post('http://localhost:5005/api/enterprises', {
        name: enterpriseName.trim(),
        ownerEmail: enterpriseOwner.trim(),
        seats: enterpriseSeats
      });
      
      toast.success('Enterprise created successfully');
      setEnterpriseName('');
      setEnterpriseOwner('');
      setEnterpriseSeats(10);
      setShowEnterpriseForm(false);
      fetchData();
    } catch (error) {
      console.error('Error creating enterprise:', error);
      toast.error('Failed to create enterprise');
    }
  };

  const deleteEnterprise = async (enterpriseId) => {
    try {
      await axios.delete(`http://localhost:5005/api/enterprises/${enterpriseId}`);
      toast.success('Enterprise deleted successfully');
      fetchData();
    } catch (error) {
      console.error('Error deleting enterprise:', error);
      toast.error('Failed to delete enterprise');
    }
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0f0f0f 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#dc2626',
        fontFamily: 'Courier New, monospace'
      }}>
        <div style={{
          textAlign: 'center',
          fontSize: '18px',
          fontWeight: 'bold',
          textShadow: '0 0 20px rgba(220, 38, 38, 0.5)'
        }}>
          Loading admin data...
        </div>
      </div>
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0f0f0f 100%)',
      color: '#fff',
      fontFamily: 'Courier New, monospace',
      display: 'flex'
    }}>
      <Toaster position="top-right" />
      
      {/* Sidebar */}
      <div style={{
        width: '250px',
        background: 'rgba(0, 0, 0, 0.9)',
        borderRight: '2px solid #dc2626',
        padding: '20px 0',
        boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)'
      }}>
        <div style={{
          textAlign: 'center',
          marginBottom: '30px',
          padding: '0 20px'
        }}>
          <h1 style={{
            color: '#dc2626',
            fontSize: '20px',
            fontWeight: 'bold',
            textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
            margin: 0
          }}>
            VOID ADMIN
          </h1>
          <div style={{
            height: '2px',
            background: 'linear-gradient(90deg, transparent, #dc2626, transparent)',
            margin: '10px 0'
          }}></div>
        </div>

        <nav style={{ padding: '0 20px' }}>
          <div style={{ marginBottom: '20px' }}>
            <button
              onClick={() => setActiveTab('dashboard')}
              style={{
                width: '100%',
                padding: '12px',
                background: activeTab === 'dashboard' ? 'rgba(220, 38, 38, 0.2)' : 'transparent',
                border: '1px solid #dc2626',
                borderRadius: '5px',
                color: '#fff',
                fontSize: '14px',
                fontWeight: 'bold',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                textAlign: 'left',
                fontFamily: 'Courier New, monospace'
              }}
              onMouseOver={(e) => {
                if (activeTab !== 'dashboard') {
                  e.target.style.background = 'rgba(220, 38, 38, 0.1)';
                }
              }}
              onMouseOut={(e) => {
                if (activeTab !== 'dashboard') {
                  e.target.style.background = 'transparent';
                }
              }}
            >
              📊 Dashboard
            </button>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <button
              onClick={() => setActiveTab('users')}
              style={{
                width: '100%',
                padding: '12px',
                background: activeTab === 'users' ? 'rgba(220, 38, 38, 0.2)' : 'transparent',
                border: '1px solid #dc2626',
                borderRadius: '5px',
                color: '#fff',
                fontSize: '14px',
                fontWeight: 'bold',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                textAlign: 'left',
                fontFamily: 'Courier New, monospace'
              }}
              onMouseOver={(e) => {
                if (activeTab !== 'users') {
                  e.target.style.background = 'rgba(220, 38, 38, 0.1)';
                }
              }}
              onMouseOut={(e) => {
                if (activeTab !== 'users') {
                  e.target.style.background = 'transparent';
                }
              }}
            >
              👥 Users ({users.length})
            </button>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <button
              onClick={() => setActiveTab('enterprises')}
              style={{
                width: '100%',
                padding: '12px',
                background: activeTab === 'enterprises' ? 'rgba(220, 38, 38, 0.2)' : 'transparent',
                border: '1px solid #dc2626',
                borderRadius: '5px',
                color: '#fff',
                fontSize: '14px',
                fontWeight: 'bold',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                textAlign: 'left',
                fontFamily: 'Courier New, monospace'
              }}
              onMouseOver={(e) => {
                if (activeTab !== 'enterprises') {
                  e.target.style.background = 'rgba(220, 38, 38, 0.1)';
                }
              }}
              onMouseOut={(e) => {
                if (activeTab !== 'enterprises') {
                  e.target.style.background = 'transparent';
                }
              }}
            >
              🏢 Enterprises ({enterprises.length})
            </button>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <button
              onClick={() => setActiveTab('pins')}
              style={{
                width: '100%',
                padding: '12px',
                background: activeTab === 'pins' ? 'rgba(220, 38, 38, 0.2)' : 'transparent',
                border: '1px solid #dc2626',
                borderRadius: '5px',
                color: '#fff',
                fontSize: '14px',
                fontWeight: 'bold',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                textAlign: 'left',
                fontFamily: 'Courier New, monospace'
              }}
              onMouseOver={(e) => {
                if (activeTab !== 'pins') {
                  e.target.style.background = 'rgba(220, 38, 38, 0.1)';
                }
              }}
              onMouseOut={(e) => {
                if (activeTab !== 'pins') {
                  e.target.style.background = 'transparent';
                }
              }}
            >
              🔑 Pins ({pins.length})
            </button>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <button
              onClick={() => setActiveTab('settings')}
              style={{
                width: '100%',
                padding: '12px',
                background: activeTab === 'settings' ? 'rgba(220, 38, 38, 0.2)' : 'transparent',
                border: '1px solid #dc2626',
                borderRadius: '5px',
                color: '#fff',
                fontSize: '14px',
                fontWeight: 'bold',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                textAlign: 'left',
                fontFamily: 'Courier New, monospace'
              }}
              onMouseOver={(e) => {
                if (activeTab !== 'settings') {
                  e.target.style.background = 'rgba(220, 38, 38, 0.1)';
                }
              }}
              onMouseOut={(e) => {
                if (activeTab !== 'settings') {
                  e.target.style.background = 'transparent';
                }
              }}
            >
              ⚙️ Settings
            </button>
          </div>
        </nav>

        <div style={{
          position: 'absolute',
          bottom: '20px',
          left: '20px',
          right: '20px'
        }}>
          <button
            onClick={fetchData}
            style={{
              width: '100%',
              padding: '10px',
              background: 'rgba(220, 38, 38, 0.2)',
              border: '1px solid #dc2626',
              borderRadius: '5px',
              color: '#fff',
              fontSize: '12px',
              fontWeight: 'bold',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              fontFamily: 'Courier New, monospace'
            }}
            onMouseOver={(e) => {
              e.target.style.background = 'rgba(220, 38, 38, 0.3)';
            }}
            onMouseOut={(e) => {
              e.target.style.background = 'rgba(220, 38, 38, 0.2)';
            }}
          >
            🔄 Refresh Data
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div style={{
        flex: 1,
        padding: '20px',
        overflow: 'auto'
      }}>
        {showUserResults ? (
          <div>
            <button
              onClick={() => {
                setShowUserResults(false);
                setSelectedUser(null);
                setUserResults([]);
              }}
              style={{
                background: 'rgba(220, 38, 38, 0.2)',
                color: '#fff',
                border: '1px solid #dc2626',
                borderRadius: '5px',
                padding: '10px 20px',
                fontSize: '14px',
                fontWeight: 'bold',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                marginBottom: '20px',
                fontFamily: 'Courier New, monospace'
              }}
              onMouseOver={(e) => {
                e.target.style.background = 'rgba(220, 38, 38, 0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.background = 'rgba(220, 38, 38, 0.2)';
              }}
            >
              ← Back to Admin Panel
            </button>

            <h2 style={{
              color: '#dc2626',
              fontSize: '24px',
              fontWeight: 'bold',
              marginBottom: '20px',
              textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
              fontFamily: 'Courier New, monospace'
            }}>
              User Results: {selectedUser?.username}
            </h2>

            {/* User Information */}
            <div style={{
              background: 'rgba(0, 0, 0, 0.8)',
              borderRadius: '10px',
              padding: '25px',
              border: '2px solid #dc2626',
              boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
              marginBottom: '30px'
            }}>
              <h3 style={{
                color: '#dc2626',
                fontSize: '18px',
                fontWeight: 'bold',
                marginBottom: '15px',
                fontFamily: 'Courier New, monospace'
              }}>
                User Information
              </h3>
              
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
                <div>
                  <strong style={{ color: '#dc2626' }}>Name:</strong>
                  <span style={{ color: '#fff', marginLeft: '8px' }}>{selectedUser?.username}</span>
                </div>
                <div>
                  <strong style={{ color: '#dc2626' }}>Email:</strong>
                  <span style={{ color: '#fff', marginLeft: '8px' }}>{selectedUser?.email}</span>
                </div>
                <div>
                  <strong style={{ color: '#dc2626' }}>Role:</strong>
                  <span style={{ color: '#fff', marginLeft: '8px' }}>{selectedUser?.role}</span>
                </div>
                <div>
                  <strong style={{ color: '#dc2626' }}>Completed Scans:</strong>
                  <span style={{ color: '#fff', marginLeft: '8px' }}>{userResults.length}</span>
                </div>
                {selectedUser?.isBanned && (
                  <div style={{ gridColumn: '1 / -1', marginTop: '10px' }}>
                    <strong style={{ color: '#dc2626' }}>Status:</strong>
                    <span style={{ color: '#dc2626', marginLeft: '8px' }}>BANNED</span>
                    {selectedUser?.banReason && (
                      <div style={{ marginTop: '5px' }}>
                        <strong style={{ color: '#dc2626' }}>Ban Reason:</strong>
                        <span style={{ color: '#dc2626', marginLeft: '8px' }}>{selectedUser.banReason}</span>
                      </div>
                    )}
                  </div>
                )}
              </div>

              <div style={{ marginTop: '20px', display: 'flex', gap: '15px', alignItems: 'center' }}>
                <input
                  type="password"
                  placeholder="New password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  style={{
                    padding: '8px 12px',
                    background: 'rgba(0, 0, 0, 0.8)',
                    border: '1px solid #dc2626',
                    borderRadius: '5px',
                    color: '#fff',
                    fontSize: '14px',
                    outline: 'none',
                    width: '200px',
                    fontFamily: 'Courier New, monospace'
                  }}
                />
                <button
                  onClick={changeUserPassword}
                  disabled={isChangingPassword}
                  style={{
                    background: isChangingPassword ? 'rgba(100, 100, 100, 0.5)' : 'rgba(220, 38, 38, 0.8)',
                    color: '#fff',
                    border: '1px solid #dc2626',
                    borderRadius: '5px',
                    padding: '8px 16px',
                    fontSize: '14px',
                    fontWeight: 'bold',
                    cursor: isChangingPassword ? 'not-allowed' : 'pointer',
                    transition: 'all 0.3s ease',
                    opacity: isChangingPassword ? 0.6 : 1,
                    fontFamily: 'Courier New, monospace'
                  }}
                  onMouseOver={(e) => {
                    if (!isChangingPassword) {
                      e.target.style.background = 'rgba(220, 38, 38, 1)';
                    }
                  }}
                  onMouseOut={(e) => {
                    if (!isChangingPassword) {
                      e.target.style.background = 'rgba(220, 38, 38, 0.8)';
                    }
                  }}
                >
                  {isChangingPassword ? 'Changing...' : 'Reset Password'}
                </button>
                {!selectedUser?.isBanned && (
                  <button
                    onClick={() => setShowBanModal(true)}
                    style={{
                      background: 'rgba(220, 38, 38, 0.8)',
                      color: '#fff',
                      border: '1px solid #dc2626',
                      borderRadius: '5px',
                      padding: '8px 16px',
                      fontSize: '14px',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      fontFamily: 'Courier New, monospace'
                    }}
                    onMouseOver={(e) => {
                      e.target.style.background = 'rgba(220, 38, 38, 1)';
                    }}
                    onMouseOut={(e) => {
                      e.target.style.background = 'rgba(220, 38, 38, 0.8)';
                    }}
                  >
                    Ban User
                  </button>
                )}
              </div>
            </div>

            {/* Completed Scan Results */}
            <h3 style={{
              color: '#dc2626',
              fontSize: '18px',
              fontWeight: 'bold',
              marginBottom: '20px',
              fontFamily: 'Courier New, monospace'
            }}>
              Completed Scan Results ({userResults.length})
            </h3>

            {userResults.length === 0 ? (
              <div style={{
                background: 'rgba(0, 0, 0, 0.8)',
                borderRadius: '10px',
                padding: '40px',
                border: '2px solid #dc2626',
                textAlign: 'center',
                color: '#999',
                fontFamily: 'Courier New, monospace'
              }}>
                No scan results found for this user.
              </div>
            ) : (
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                gap: '20px'
              }}>
                {userResults.map((result, index) => (
                  <div key={index} style={{
                    background: 'rgba(0, 0, 0, 0.8)',
                    borderRadius: '10px',
                    padding: '20px',
                    border: '2px solid #dc2626',
                    boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
                    transition: 'all 0.3s ease'
                  }}>
                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#dc2626' }}>PIN:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>{result.pin}</span>
                    </div>
                    
                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#dc2626' }}>Completed:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>
                        {new Date(result.completedAt).toLocaleString()}
                      </span>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#dc2626' }}>System Info:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>
                        {result.systemInfo ? Object.keys(result.systemInfo).length : 0} items
                      </span>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#dc2626' }}>FiveM Mods:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>
                        {result.fivemMods ? result.fivemMods.length : 0} mods
                      </span>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#dc2626' }}>Cheat Websites:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>
                        {result.cheatWebsites ? result.cheatWebsites.length : 0} sites
                      </span>
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <strong style={{ color: '#dc2626' }}>Discord ID:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>
                        {result.discordId || 'Not found'}
                      </span>
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                      <strong style={{ color: '#dc2626' }}>Screenshot:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>
                        {result.screenshot ? 'Available' : 'Not available'}
                      </span>
                    </div>

                    <button
                      onClick={() => {
                        const resultsWindow = window.open('', '_blank');
                        resultsWindow.document.write(`
                          <!DOCTYPE html>
                          <html>
                            <head>
                              <title>Scan Results - ${result.pin}</title>
                              <style>
                                body {
                                  background: linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0f0f0f 100%);
                                  color: #fff;
                                  font-family: 'Courier New', monospace;
                                  margin: 0;
                                  padding: 20px;
                                }
                                .container {
                                  max-width: 1200px;
                                  margin: 0 auto;
                                }
                                .header {
                                  text-align: center;
                                  margin-bottom: 30px;
                                }
                                .section {
                                  background: rgba(0, 0, 0, 0.8);
                                  border-radius: 10px;
                                  padding: 25px;
                                  margin-bottom: 20px;
                                  border: 2px solid #dc2626;
                                  box-shadow: 0 0 30px rgba(220, 38, 38, 0.3);
                                }
                                .section h3 {
                                  color: #dc2626;
                                  margin-bottom: 15px;
                                  font-size: 18px;
                                  font-family: 'Courier New', monospace;
                                }
                                .info-grid {
                                  display: grid;
                                  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
                                  gap: 15px;
                                }
                                .info-item {
                                  background: rgba(0, 0, 0, 0.8);
                                  padding: 12px;
                                  border-radius: 5px;
                                  border: 1px solid #dc2626;
                                }
                                .info-label {
                                  color: #dc2626;
                                  font-weight: bold;
                                  font-size: 12px;
                                  margin-bottom: 5px;
                                }
                                .info-value {
                                  color: #fff;
                                  font-size: 14px;
                                }
                                .mod-item {
                                  background: rgba(0, 0, 0, 0.8);
                                  padding: 10px;
                                  border-radius: 5px;
                                  margin-bottom: 8px;
                                  border-left: 3px solid #dc2626;
                                }
                                .screenshot {
                                  max-width: 100%;
                                  border-radius: 5px;
                                  box-shadow: 0 0 30px rgba(220, 38, 38, 0.3);
                                }
                              </style>
                            </head>
                            <body>
                              <div class="container">
                                <div class="header">
                                  <h1 style="color: #dc2626; font-size: 28px; font-family: 'Courier New', monospace;">Scan Results</h1>
                                  <p style="color: #999;">PIN: ${result.pin} | Completed: ${new Date(result.completedAt).toLocaleString()}</p>
                                </div>

                                ${result.systemInfo ? `
                                <div class="section">
                                  <h3>System Information</h3>
                                  <div class="info-grid">
                                    ${Object.entries(result.systemInfo).map(([key, value]) => `
                                      <div class="info-item">
                                        <div class="info-label">${key}</div>
                                        <div class="info-value">${value}</div>
                                      </div>
                                    `).join('')}
                                  </div>
                                </div>
                                ` : ''}

                                ${result.fivemMods && result.fivemMods.length > 0 ? `
                                <div class="section">
                                  <h3>FiveM Mods (${result.fivemMods.length})</h3>
                                  ${result.fivemMods.map(mod => `
                                    <div class="mod-item">📄 ${mod}</div>
                                  `).join('')}
                                </div>
                                ` : ''}

                                ${result.cheatWebsites && result.cheatWebsites.length > 0 ? `
                                <div class="section">
                                  <h3>Cheat Websites (${result.cheatWebsites.length})</h3>
                                  ${result.cheatWebsites.map(site => `
                                    <div class="mod-item">🌐 ${site}</div>
                                  `).join('')}
                                </div>
                                ` : ''}

                                ${result.discordId ? `
                                <div class="section">
                                  <h3>Discord Account</h3>
                                  <div class="info-item">
                                    <div class="info-label">Discord ID</div>
                                    <div class="info-value">${result.discordId}</div>
                                  </div>
                                </div>
                                ` : ''}

                                ${result.screenshot ? `
                                <div class="section">
                                  <h3>PC Screenshot</h3>
                                  <img src="data:image/png;base64,${result.screenshot}" alt="PC Screenshot" class="screenshot">
                                </div>
                                ` : ''}
                              </div>
                            </body>
                          </html>
                        `);
                        resultsWindow.document.close();
                      }}
                      style={{
                        background: 'rgba(220, 38, 38, 0.8)',
                        color: '#fff',
                        border: '1px solid #dc2626',
                        borderRadius: '5px',
                        padding: '10px 20px',
                        fontSize: '14px',
                        fontWeight: 'bold',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease',
                        width: '100%',
                        fontFamily: 'Courier New, monospace'
                      }}
                      onMouseOver={(e) => {
                        e.target.style.background = 'rgba(220, 38, 38, 1)';
                      }}
                      onMouseOut={(e) => {
                        e.target.style.background = 'rgba(220, 38, 38, 0.8)';
                      }}
                    >
                      Click to view full details
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        ) : (
          <div>
            {/* Dashboard Tab */}
            {activeTab === 'dashboard' && (
              <div>
                <h2 style={{
                  color: '#dc2626',
                  fontSize: '28px',
                  fontWeight: 'bold',
                  marginBottom: '30px',
                  textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
                  fontFamily: 'Courier New, monospace'
                }}>
                  Admin Dashboard
                </h2>

                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
                  gap: '20px',
                  marginBottom: '30px'
                }}>
                  <div style={{
                    background: 'rgba(0, 0, 0, 0.8)',
                    borderRadius: '10px',
                    padding: '20px',
                    border: '2px solid #dc2626',
                    boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
                    textAlign: 'center'
                  }}>
                    <h3 style={{ color: '#dc2626', fontSize: '18px', marginBottom: '10px', fontFamily: 'Courier New, monospace' }}>
                      Total Users
                    </h3>
                    <div style={{ color: '#fff', fontSize: '32px', fontWeight: 'bold' }}>
                      {users.length}
                    </div>
                  </div>

                  <div style={{
                    background: 'rgba(0, 0, 0, 0.8)',
                    borderRadius: '10px',
                    padding: '20px',
                    border: '2px solid #dc2626',
                    boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
                    textAlign: 'center'
                  }}>
                    <h3 style={{ color: '#dc2626', fontSize: '18px', marginBottom: '10px', fontFamily: 'Courier New, monospace' }}>
                      Total Enterprises
                    </h3>
                    <div style={{ color: '#fff', fontSize: '32px', fontWeight: 'bold' }}>
                      {enterprises.length}
                    </div>
                  </div>

                  <div style={{
                    background: 'rgba(0, 0, 0, 0.8)',
                    borderRadius: '10px',
                    padding: '20px',
                    border: '2px solid #dc2626',
                    boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
                    textAlign: 'center'
                  }}>
                    <h3 style={{ color: '#dc2626', fontSize: '18px', marginBottom: '10px', fontFamily: 'Courier New, monospace' }}>
                      Total Pins
                    </h3>
                    <div style={{ color: '#fff', fontSize: '32px', fontWeight: 'bold' }}>
                      {pins.length}
                    </div>
                  </div>

                  <div style={{
                    background: 'rgba(0, 0, 0, 0.8)',
                    borderRadius: '10px',
                    padding: '20px',
                    border: '2px solid #dc2626',
                    boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
                    textAlign: 'center'
                  }}>
                    <h3 style={{ color: '#dc2626', fontSize: '18px', marginBottom: '10px', fontFamily: 'Courier New, monospace' }}>
                      Banned Users
                    </h3>
                    <div style={{ color: '#dc2626', fontSize: '32px', fontWeight: 'bold' }}>
                      {users.filter(u => u.isBanned).length}
                    </div>
                  </div>
                </div>

                <div style={{
                  background: 'rgba(0, 0, 0, 0.8)',
                  borderRadius: '10px',
                  padding: '25px',
                  border: '2px solid #dc2626',
                  boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)'
                }}>
                  <h3 style={{
                    color: '#dc2626',
                    fontSize: '18px',
                    fontWeight: 'bold',
                    marginBottom: '15px',
                    fontFamily: 'Courier New, monospace'
                  }}>
                    Recent Activity
                  </h3>
                  <div style={{ color: '#999', fontFamily: 'Courier New, monospace' }}>
                    System is running normally. All services are operational.
                  </div>
                </div>
              </div>
            )}

            {/* Users Tab */}
            {activeTab === 'users' && (
              <div>
                <div style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  marginBottom: '20px'
                }}>
                  <h2 style={{
                    color: '#dc2626',
                    fontSize: '24px',
                    fontWeight: 'bold',
                    textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
                    fontFamily: 'Courier New, monospace'
                  }}>
                    Users Management ({users.length} Total)
                  </h2>
                  <div style={{
                    display: 'flex',
                    gap: '10px',
                    alignItems: 'center'
                  }}>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '8px 15px',
                      border: '1px solid #dc2626',
                      borderRadius: '5px',
                      color: '#fff',
                      fontSize: '12px',
                      fontFamily: 'Courier New, monospace'
                    }}>
                      Admins: {users.filter(u => u.role === 'admin').length}
                    </div>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '8px 15px',
                      border: '1px solid #dc2626',
                      borderRadius: '5px',
                      color: '#fff',
                      fontSize: '12px',
                      fontFamily: 'Courier New, monospace'
                    }}>
                      Banned: {users.filter(u => u.isBanned).length}
                    </div>
                  </div>
                </div>

                {/* User Statistics */}
                <div style={{
                  background: 'rgba(0, 0, 0, 0.8)',
                  borderRadius: '10px',
                  padding: '20px',
                  border: '2px solid #dc2626',
                  boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
                  marginBottom: '20px'
                }}>
                  <h3 style={{
                    color: '#dc2626',
                    fontSize: '16px',
                    fontWeight: 'bold',
                    marginBottom: '15px',
                    fontFamily: 'Courier New, monospace'
                  }}>
                    User Statistics
                  </h3>
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                    gap: '15px'
                  }}>
                    <div style={{
                      background: 'rgba(220, 38, 38, 0.1)',
                      padding: '15px',
                      borderRadius: '5px',
                      border: '1px solid #dc2626',
                      textAlign: 'center'
                    }}>
                      <div style={{ color: '#dc2626', fontSize: '24px', fontWeight: 'bold' }}>
                        {users.length}
                      </div>
                      <div style={{ color: '#fff', fontSize: '12px', fontFamily: 'Courier New, monospace' }}>
                        Total Users
                      </div>
                    </div>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '15px',
                      borderRadius: '5px',
                      border: '1px solid #dc2626',
                      textAlign: 'center'
                    }}>
                      <div style={{ color: '#dc2626', fontSize: '24px', fontWeight: 'bold' }}>
                        {users.filter(u => u.role === 'admin').length}
                      </div>
                      <div style={{ color: '#fff', fontSize: '12px', fontFamily: 'Courier New, monospace' }}>
                        Admins
                      </div>
                    </div>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '15px',
                      borderRadius: '5px',
                      border: '1px solid #dc2626',
                      textAlign: 'center'
                    }}>
                      <div style={{ color: '#dc2626', fontSize: '24px', fontWeight: 'bold' }}>
                        {users.filter(u => u.role === 'user').length}
                      </div>
                      <div style={{ color: '#fff', fontSize: '12px', fontFamily: 'Courier New, monospace' }}>
                        Regular Users
                      </div>
                    </div>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '15px',
                      borderRadius: '5px',
                      border: '1px solid #dc2626',
                      textAlign: 'center'
                    }}>
                      <div style={{ color: '#dc2626', fontSize: '24px', fontWeight: 'bold' }}>
                        {users.filter(u => u.isBanned).length}
                      </div>
                      <div style={{ color: '#fff', fontSize: '12px', fontFamily: 'Courier New, monospace' }}>
                        Banned Users
                      </div>
                    </div>
                  </div>
                </div>
                
                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                  gap: '15px'
                }}>
                  {users.map((user, index) => (
                    <div key={index} style={{
                      background: user.isBanned ? 'rgba(220, 38, 38, 0.1)' : 'rgba(0, 0, 0, 0.8)',
                      borderRadius: '10px',
                      padding: '15px',
                      border: user.isBanned ? '2px solid #dc2626' : '2px solid #dc2626',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      boxShadow: user.isBanned ? '0 0 30px rgba(220, 38, 38, 0.3)' : '0 0 30px rgba(220, 38, 38, 0.3)',
                      position: 'relative'
                    }}
                    onMouseOver={(e) => {
                      e.target.style.borderColor = '#dc2626';
                      e.target.style.boxShadow = '0 0 40px rgba(220, 38, 38, 0.5)';
                      e.target.style.transform = 'translateY(-2px)';
                    }}
                    onMouseOut={(e) => {
                      e.target.style.borderColor = '#dc2626';
                      e.target.style.boxShadow = user.isBanned ? '0 0 30px rgba(220, 38, 38, 0.3)' : '0 0 30px rgba(220, 38, 38, 0.3)';
                      e.target.style.transform = 'translateY(0)';
                    }}
                    onClick={() => handleUserClick(user)}
                    >
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Name:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px' }}>{user.username}</span>
                      </div>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Email:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px' }}>{user.email}</span>
                      </div>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Role:</strong>
                        <span style={{ 
                          color: user.role === 'admin' ? '#dc2626' : '#fff', 
                          marginLeft: '8px',
                          fontWeight: user.role === 'admin' ? 'bold' : 'normal',
                          textTransform: 'uppercase'
                        }}>
                          {user.role}
                        </span>
                      </div>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Created:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px', fontSize: '12px' }}>
                          {new Date(user.createdAt).toLocaleString()}
                        </span>
                      </div>
                      {user.isBanned && (
                        <div style={{ 
                          marginTop: '8px', 
                          padding: '8px', 
                          background: 'rgba(220, 38, 38, 0.2)', 
                          borderRadius: '5px',
                          border: '1px solid #dc2626'
                        }}>
                          <strong style={{ color: '#dc2626', fontSize: '12px' }}>🚫 BANNED</strong>
                          {user.banReason && (
                            <div style={{ color: '#dc2626', fontSize: '12px', marginTop: '4px' }}>
                              Reason: {user.banReason}
                            </div>
                          )}
                        </div>
                      )}
                      {user.role === 'admin' && (
                        <div style={{
                          position: 'absolute',
                          top: '10px',
                          right: '10px',
                          background: 'rgba(220, 38, 38, 0.8)',
                          color: '#fff',
                          padding: '4px 8px',
                          borderRadius: '3px',
                          fontSize: '10px',
                          fontWeight: 'bold',
                          fontFamily: 'Courier New, monospace'
                        }}>
                          ADMIN
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Enterprises Tab */}
            {activeTab === 'enterprises' && (
              <div>
                <div style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  marginBottom: '20px'
                }}>
                  <h2 style={{
                    color: '#dc2626',
                    fontSize: '24px',
                    fontWeight: 'bold',
                    textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
                    fontFamily: 'Courier New, monospace'
                  }}>
                    Enterprises Management
                  </h2>
                  <button
                    onClick={() => setShowEnterpriseForm(!showEnterpriseForm)}
                    style={{
                      background: 'rgba(220, 38, 38, 0.8)',
                      color: '#fff',
                      border: '1px solid #dc2626',
                      borderRadius: '5px',
                      padding: '10px 20px',
                      fontSize: '14px',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      fontFamily: 'Courier New, monospace'
                    }}
                    onMouseOver={(e) => {
                      e.target.style.background = 'rgba(220, 38, 38, 1)';
                    }}
                    onMouseOut={(e) => {
                      e.target.style.background = 'rgba(220, 38, 38, 0.8)';
                    }}
                  >
                    {showEnterpriseForm ? 'Hide Form' : 'Create Enterprise'}
                  </button>
                </div>

                {showEnterpriseForm && (
                  <div style={{
                    background: 'rgba(0, 0, 0, 0.8)',
                    borderRadius: '10px',
                    padding: '25px',
                    border: '2px solid #dc2626',
                    boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
                    marginBottom: '20px'
                  }}>
                    <h3 style={{
                      color: '#dc2626',
                      fontSize: '18px',
                      fontWeight: 'bold',
                      marginBottom: '20px',
                      fontFamily: 'Courier New, monospace'
                    }}>
                      Create New Enterprise
                    </h3>
                    
                    <div style={{ marginBottom: '15px' }}>
                      <label style={{
                        display: 'block',
                        color: '#dc2626',
                        marginBottom: '8px',
                        fontSize: '14px',
                        fontWeight: 'bold',
                        fontFamily: 'Courier New, monospace'
                      }}>
                        Enterprise Name:
                      </label>
                      <input
                        type="text"
                        value={enterpriseName}
                        onChange={(e) => setEnterpriseName(e.target.value)}
                        style={{
                          width: '100%',
                          padding: '10px',
                          background: 'rgba(0, 0, 0, 0.8)',
                          border: '1px solid #dc2626',
                          borderRadius: '5px',
                          color: '#fff',
                          fontSize: '14px',
                          outline: 'none',
                          fontFamily: 'Courier New, monospace'
                        }}
                      />
                    </div>

                    <div style={{ marginBottom: '15px' }}>
                      <label style={{
                        display: 'block',
                        color: '#dc2626',
                        marginBottom: '8px',
                        fontSize: '14px',
                        fontWeight: 'bold',
                        fontFamily: 'Courier New, monospace'
                      }}>
                        Owner Email:
                      </label>
                      <input
                        type="email"
                        value={enterpriseOwner}
                        onChange={(e) => setEnterpriseOwner(e.target.value)}
                        style={{
                          width: '100%',
                          padding: '10px',
                          background: 'rgba(0, 0, 0, 0.8)',
                          border: '1px solid #dc2626',
                          borderRadius: '5px',
                          color: '#fff',
                          fontSize: '14px',
                          outline: 'none',
                          fontFamily: 'Courier New, monospace'
                        }}
                      />
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                      <label style={{
                        display: 'block',
                        color: '#dc2626',
                        marginBottom: '8px',
                        fontSize: '14px',
                        fontWeight: 'bold',
                        fontFamily: 'Courier New, monospace'
                      }}>
                        Number of Seats:
                      </label>
                      <input
                        type="number"
                        value={enterpriseSeats}
                        onChange={(e) => setEnterpriseSeats(parseInt(e.target.value))}
                        min="1"
                        style={{
                          width: '100%',
                          padding: '10px',
                          background: 'rgba(0, 0, 0, 0.8)',
                          border: '1px solid #dc2626',
                          borderRadius: '5px',
                          color: '#fff',
                          fontSize: '14px',
                          outline: 'none',
                          fontFamily: 'Courier New, monospace'
                        }}
                      />
                    </div>

                    <button
                      onClick={createEnterprise}
                      style={{
                        background: 'rgba(220, 38, 38, 0.8)',
                        color: '#fff',
                        border: '1px solid #dc2626',
                        borderRadius: '5px',
                        padding: '10px 20px',
                        fontSize: '14px',
                        fontWeight: 'bold',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease',
                        fontFamily: 'Courier New, monospace'
                      }}
                      onMouseOver={(e) => {
                        e.target.style.background = 'rgba(220, 38, 38, 1)';
                      }}
                      onMouseOut={(e) => {
                        e.target.style.background = 'rgba(220, 38, 38, 0.8)';
                      }}
                    >
                      Create Enterprise
                    </button>
                  </div>
                )}
                
                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                  gap: '15px'
                }}>
                  {enterprises.map((enterprise, index) => (
                    <div key={index} style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      borderRadius: '10px',
                      padding: '15px',
                      border: '2px solid #dc2626',
                      boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
                      position: 'relative'
                    }}>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Name:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px' }}>{enterprise.name}</span>
                      </div>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Owner:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px' }}>{enterprise.ownerEmail}</span>
                      </div>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Seats:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px' }}>{enterprise.seats}</span>
                      </div>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Members:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px' }}>{enterprise.members ? enterprise.members.length : 0}</span>
                      </div>
                      <button
                        onClick={() => deleteEnterprise(enterprise._id)}
                        style={{
                          position: 'absolute',
                          top: '10px',
                          right: '10px',
                          background: 'rgba(220, 38, 38, 0.8)',
                          color: '#fff',
                          border: '1px solid #dc2626',
                          borderRadius: '5px',
                          padding: '6px 12px',
                          fontSize: '12px',
                          fontWeight: 'bold',
                          cursor: 'pointer',
                          transition: 'all 0.3s ease',
                          fontFamily: 'Courier New, monospace'
                        }}
                        onMouseOver={(e) => {
                          e.target.style.background = 'rgba(220, 38, 38, 1)';
                        }}
                        onMouseOut={(e) => {
                          e.target.style.background = 'rgba(220, 38, 38, 0.8)';
                        }}
                      >
                        Delete
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Pins Tab */}
            {activeTab === 'pins' && (
              <div>
                <div style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  marginBottom: '20px'
                }}>
                  <h2 style={{
                    color: '#dc2626',
                    fontSize: '24px',
                    fontWeight: 'bold',
                    textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
                    fontFamily: 'Courier New, monospace'
                  }}>
                    Pins Management ({pins.length} Total)
                  </h2>
                  <div style={{
                    display: 'flex',
                    gap: '10px',
                    alignItems: 'center'
                  }}>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '8px 15px',
                      border: '1px solid #dc2626',
                      borderRadius: '5px',
                      color: '#fff',
                      fontSize: '12px',
                      fontFamily: 'Courier New, monospace'
                    }}>
                      Completed: {pins.filter(p => p.status === 'completed').length}
                    </div>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '8px 15px',
                      border: '1px solid #dc2626',
                      borderRadius: '5px',
                      color: '#fff',
                      fontSize: '12px',
                      fontFamily: 'Courier New, monospace'
                    }}>
                      Pending: {pins.filter(p => p.status === 'pending').length}
                    </div>
                  </div>
                </div>

                {/* Search and Filter */}
                <div style={{
                  background: 'rgba(0, 0, 0, 0.8)',
                  borderRadius: '10px',
                  padding: '20px',
                  border: '2px solid #dc2626',
                  boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)',
                  marginBottom: '20px'
                }}>
                  <h3 style={{
                    color: '#dc2626',
                    fontSize: '16px',
                    fontWeight: 'bold',
                    marginBottom: '15px',
                    fontFamily: 'Courier New, monospace'
                  }}>
                    Pin Statistics
                  </h3>
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                    gap: '15px'
                  }}>
                    <div style={{
                      background: 'rgba(220, 38, 38, 0.1)',
                      padding: '15px',
                      borderRadius: '5px',
                      border: '1px solid #dc2626',
                      textAlign: 'center'
                    }}>
                      <div style={{ color: '#dc2626', fontSize: '24px', fontWeight: 'bold' }}>
                        {pins.length}
                      </div>
                      <div style={{ color: '#fff', fontSize: '12px', fontFamily: 'Courier New, monospace' }}>
                        Total Pins
                      </div>
                    </div>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '15px',
                      borderRadius: '5px',
                      border: '1px solid #dc2626',
                      textAlign: 'center'
                    }}>
                      <div style={{ color: '#dc2626', fontSize: '24px', fontWeight: 'bold' }}>
                        {pins.filter(p => p.status === 'completed').length}
                      </div>
                      <div style={{ color: '#fff', fontSize: '12px', fontFamily: 'Courier New, monospace' }}>
                        Completed
                      </div>
                    </div>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '15px',
                      borderRadius: '5px',
                      border: '1px solid #dc2626',
                      textAlign: 'center'
                    }}>
                      <div style={{ color: '#dc2626', fontSize: '24px', fontWeight: 'bold' }}>
                        {pins.filter(p => p.status === 'pending').length}
                      </div>
                      <div style={{ color: '#fff', fontSize: '12px', fontFamily: 'Courier New, monospace' }}>
                        Pending
                      </div>
                    </div>
                    <div style={{
                      background: 'rgba(0, 0, 0, 0.8)',
                      padding: '15px',
                      borderRadius: '5px',
                      border: '1px solid #dc2626',
                      textAlign: 'center'
                    }}>
                      <div style={{ color: '#dc2626', fontSize: '24px', fontWeight: 'bold' }}>
                        {new Set(pins.map(p => p.creatorEmail)).size}
                      </div>
                      <div style={{ color: '#fff', fontSize: '12px', fontFamily: 'Courier New, monospace' }}>
                        Unique Creators
                      </div>
                    </div>
                  </div>
                </div>
                
                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
                  gap: '15px',
                  maxHeight: '600px',
                  overflowY: 'auto',
                  padding: '10px',
                  border: '2px solid #dc2626',
                  borderRadius: '10px',
                  background: 'rgba(0, 0, 0, 0.3)'
                }}>
                  {pins.map((pin, index) => (
                    <div key={index} style={{
                      background: pin.status === 'completed' ? 'rgba(220, 38, 38, 0.1)' : 'rgba(0, 0, 0, 0.8)',
                      borderRadius: '10px',
                      padding: '15px',
                      border: pin.status === 'completed' ? '2px solid #dc2626' : '2px solid #666',
                      boxShadow: pin.status === 'completed' ? '0 0 30px rgba(220, 38, 38, 0.3)' : '0 0 15px rgba(0, 0, 0, 0.5)',
                      position: 'relative',
                      transition: 'all 0.3s ease'
                    }}
                    onMouseOver={(e) => {
                      e.target.style.transform = 'translateY(-2px)';
                      e.target.style.boxShadow = pin.status === 'completed' ? '0 0 40px rgba(220, 38, 38, 0.5)' : '0 0 25px rgba(0, 0, 0, 0.7)';
                    }}
                    onMouseOut={(e) => {
                      e.target.style.transform = 'translateY(0)';
                      e.target.style.boxShadow = pin.status === 'completed' ? '0 0 30px rgba(220, 38, 38, 0.3)' : '0 0 15px rgba(0, 0, 0, 0.5)';
                    }}
                    >
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>PIN:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px', fontFamily: 'Courier New, monospace', fontSize: '16px', fontWeight: 'bold' }}>
                          {pin.pin}
                        </span>
                      </div>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Created By:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px' }}>{pin.creatorEmail}</span>
                      </div>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Status:</strong>
                        <span style={{ 
                          color: pin.status === 'completed' ? '#dc2626' : '#999', 
                          marginLeft: '8px',
                          fontWeight: 'bold',
                          textTransform: 'uppercase'
                        }}>
                          {pin.status}
                        </span>
                      </div>
                      <div style={{ marginBottom: '8px' }}>
                        <strong style={{ color: '#dc2626' }}>Created:</strong>
                        <span style={{ color: '#fff', marginLeft: '8px', fontSize: '12px' }}>
                          {new Date(pin.createdAt).toLocaleString()}
                        </span>
                      </div>
                      {pin.completedAt && (
                        <div style={{ marginBottom: '8px' }}>
                          <strong style={{ color: '#dc2626' }}>Completed:</strong>
                          <span style={{ color: '#fff', marginLeft: '8px', fontSize: '12px' }}>
                            {new Date(pin.completedAt).toLocaleString()}
                          </span>
                        </div>
                      )}
                      {pin.status === 'completed' && (
                        <div style={{
                          position: 'absolute',
                          top: '10px',
                          right: '10px',
                          background: 'rgba(220, 38, 38, 0.8)',
                          color: '#fff',
                          padding: '4px 8px',
                          borderRadius: '3px',
                          fontSize: '10px',
                          fontWeight: 'bold',
                          fontFamily: 'Courier New, monospace'
                        }}>
                          ✓ DONE
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Settings Tab */}
            {activeTab === 'settings' && (
              <div>
                <h2 style={{
                  color: '#dc2626',
                  fontSize: '24px',
                  fontWeight: 'bold',
                  marginBottom: '20px',
                  textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
                  fontFamily: 'Courier New, monospace'
                }}>
                  System Settings
                </h2>
                
                <div style={{
                  background: 'rgba(0, 0, 0, 0.8)',
                  borderRadius: '10px',
                  padding: '25px',
                  border: '2px solid #dc2626',
                  boxShadow: '0 0 30px rgba(220, 38, 38, 0.3)'
                }}>
                  <h3 style={{
                    color: '#dc2626',
                    fontSize: '18px',
                    fontWeight: 'bold',
                    marginBottom: '15px',
                    fontFamily: 'Courier New, monospace'
                  }}>
                    System Information
                  </h3>
                  
                  <div style={{ color: '#fff', fontFamily: 'Courier New, monospace' }}>
                    <div style={{ marginBottom: '10px' }}>
                      <strong style={{ color: '#dc2626' }}>Server Status:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>Online</span>
                    </div>
                    <div style={{ marginBottom: '10px' }}>
                      <strong style={{ color: '#dc2626' }}>Database:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>Connected</span>
                    </div>
                    <div style={{ marginBottom: '10px' }}>
                      <strong style={{ color: '#dc2626' }}>API Endpoints:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>Active</span>
                    </div>
                    <div style={{ marginBottom: '10px' }}>
                      <strong style={{ color: '#dc2626' }}>Last Backup:</strong>
                      <span style={{ color: '#fff', marginLeft: '8px' }}>{new Date().toLocaleString()}</span>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Ban Modal */}
        {showBanModal && (
          <div style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0, 0, 0, 0.9)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 10000,
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              background: 'rgba(0, 0, 0, 0.95)',
              borderRadius: '15px',
              padding: '40px',
              border: '2px solid #dc2626',
              boxShadow: '0 0 60px rgba(220, 38, 38, 0.8)',
              maxWidth: '500px',
              width: '90%',
              position: 'relative'
            }}>
              {/* Close Button */}
              <button
                onClick={() => {
                  setShowBanModal(false);
                  setBanReason('');
                }}
                style={{
                  position: 'absolute',
                  top: '15px',
                  right: '15px',
                  background: 'rgba(220, 38, 38, 0.8)',
                  color: '#ffffff',
                  border: 'none',
                  borderRadius: '50%',
                  width: '30px',
                  height: '30px',
                  cursor: 'pointer',
                  fontSize: '16px',
                  fontWeight: 'bold',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  transition: 'all 0.3s ease',
                  boxShadow: '0 0 20px rgba(220, 38, 38, 0.5)'
                }}
                onMouseOver={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 1)';
                  e.target.style.transform = 'scale(1.1)';
                }}
                onMouseOut={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 0.8)';
                  e.target.style.transform = 'scale(1)';
                }}
              >
                ×
              </button>

              {/* Modal Content */}
              <div style={{ textAlign: 'center' }}>
                <h2 style={{
                  color: '#dc2626',
                  marginBottom: '20px',
                  fontSize: '24px',
                  fontWeight: 'bold',
                  textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
                  fontFamily: 'Courier New, monospace'
                }}>
                  Ban User
                </h2>
                
                <p style={{
                  color: '#fff',
                  marginBottom: '30px',
                  fontSize: '16px',
                  lineHeight: '1.5',
                  fontFamily: 'Courier New, monospace'
                }}>
                  Are you sure you want to ban <strong style={{ color: '#dc2626' }}>{selectedUser?.username}</strong>? This action cannot be undone.
                </p>

                <div style={{ marginBottom: '30px' }}>
                  <label style={{
                    display: 'block',
                    color: '#dc2626',
                    marginBottom: '10px',
                    fontSize: '14px',
                    fontWeight: 'bold',
                    fontFamily: 'Courier New, monospace'
                  }}>
                    Ban Reason (Required):
                  </label>
                  <textarea
                    value={banReason}
                    onChange={(e) => setBanReason(e.target.value)}
                    placeholder="Enter reason for banning this user..."
                    style={{
                      width: '100%',
                      height: '80px',
                      padding: '12px',
                      background: 'rgba(0, 0, 0, 0.8)',
                      border: '1px solid #dc2626',
                      borderRadius: '8px',
                      color: '#fff',
                      fontSize: '14px',
                      resize: 'vertical',
                      fontFamily: 'Courier New, monospace',
                      outline: 'none',
                      transition: 'border-color 0.3s ease'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#dc2626';
                      e.target.style.boxShadow = '0 0 20px rgba(220, 38, 38, 0.3)';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = '#dc2626';
                      e.target.style.boxShadow = 'none';
                    }}
                  />
                </div>

                <div style={{ display: 'flex', gap: '15px', justifyContent: 'center' }}>
                  <button
                    onClick={() => {
                      setShowBanModal(false);
                      setBanReason('');
                    }}
                    style={{
                      background: 'rgba(100, 100, 100, 0.8)',
                      color: '#fff',
                      border: '1px solid #666',
                      borderRadius: '8px',
                      padding: '12px 24px',
                      fontSize: '14px',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      fontFamily: 'Courier New, monospace'
                    }}
                    onMouseOver={(e) => {
                      e.target.style.background = 'rgba(100, 100, 100, 1)';
                      e.target.style.transform = 'translateY(-2px)';
                    }}
                    onMouseOut={(e) => {
                      e.target.style.background = 'rgba(100, 100, 100, 0.8)';
                      e.target.style.transform = 'translateY(0)';
                    }}
                  >
                    Cancel
                  </button>
                  <button
                    onClick={banUser}
                    style={{
                      background: 'rgba(220, 38, 38, 0.8)',
                      color: '#ffffff',
                      border: '1px solid #dc2626',
                      borderRadius: '8px',
                      padding: '12px 24px',
                      fontSize: '14px',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      boxShadow: '0 0 20px rgba(220, 38, 38, 0.3)',
                      fontFamily: 'Courier New, monospace'
                    }}
                    onMouseOver={(e) => {
                      e.target.style.background = 'rgba(220, 38, 38, 1)';
                      e.target.style.transform = 'translateY(-2px)';
                      e.target.style.boxShadow = '0 0 30px rgba(220, 38, 38, 0.5)';
                    }}
                    onMouseOut={(e) => {
                      e.target.style.background = 'rgba(220, 38, 38, 0.8)';
                      e.target.style.transform = 'translateY(0)';
                      e.target.style.boxShadow = '0 0 20px rgba(220, 38, 38, 0.3)';
                    }}
                  >
                    Confirm Ban
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
