import React, { useState } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

function App() {
  const [pin, setPin] = useState('');
  const [scanStatus, setScanStatus] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [scanResults, setScanResults] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!pin.trim()) {
      toast.error('L√ºtfen PIN kodunu girin');
      return;
    }

    setIsLoading(true);
    setScanStatus('pending');
    setScanResults(null);

    try {
      const response = await axios.post('/api/verify-pin', { pin: pin.trim() });
      
      if (response.data.message.includes('ba≈ülatƒ±ldƒ±')) {
        toast.success('PIN doƒürulandƒ±! Tarama ba≈ülatƒ±ldƒ±.');
        setScanStatus('pending');
        
        setTimeout(() => {
          setScanStatus('completed');
          setScanResults({
            data: {
              riskLevel: 'D√º≈ü√ºk',
              securityChecks: {
                malware: 'Temiz',
                suspiciousActivity: 'Tespit edilmedi'
              },
              recommendations: ['Sistem g√ºncel', 'Antivirus aktif']
            },
            completedAt: new Date()
          });
          toast.success('Tarama tamamlandƒ±!');
        }, 3000);
      }
    } catch (error) {
      toast.error(error.response?.data?.message || 'Bir hata olu≈ütu');
      setScanStatus('error');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'url(/background.jpg) center/cover no-repeat, linear-gradient(135deg, #ff0000 0%, #cc0000 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Siyah Yaƒümur Efekti */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1
      }}>
        {Array.from({ length: 50 }).map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: '2px',
              height: `${Math.random() * 100 + 50}px`,
              background: 'linear-gradient(to bottom, rgba(0,0,0,0.8), rgba(0,0,0,0.3))',
              animation: `rain ${Math.random() * 2 + 1}s linear infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
        `}
      </style>

      {/* Ana Kutu */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.1)',
        backdropFilter: 'blur(20px)',
        borderRadius: '20px',
        padding: '40px',
        boxShadow: '0 20px 40px rgba(0, 0, 0, 0.3)',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        maxWidth: '500px',
        width: '100%',
        position: 'relative',
        overflow: 'hidden',
        zIndex: 2
      }}>
        <h1 style={{
          textAlign: 'center',
          color: '#fff',
          marginBottom: '30px',
          fontSize: '2.5rem',
          fontWeight: '700',
          textShadow: '2px 2px 4px rgba(0,0,0,0.5)'
        }}>üîç Void Scanner</h1>
        
        <p style={{
          textAlign: 'center',
          color: 'rgba(255, 255, 255, 0.9)',
          marginBottom: '40px',
          fontSize: '1.1rem',
          textShadow: '1px 1px 2px rgba(0,0,0,0.5)'
        }}>
          G√ºvenlik taramasƒ± i√ßin PIN kodunuzu girin
        </p>
        
        <form onSubmit={handleSubmit} style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '20px'
        }}>
          <input
            type="text"
            placeholder="PIN Kodunuzu Girin"
            value={pin}
            onChange={(e) => setPin(e.target.value.toUpperCase())}
            maxLength="8"
            disabled={isLoading}
            style={{
              padding: '15px',
              border: '2px solid rgba(255, 255, 255, 0.3)',
              borderRadius: '10px',
              fontSize: '16px',
              background: 'rgba(255, 255, 255, 0.1)',
              color: '#fff',
              backdropFilter: 'blur(10px)',
              transition: 'border-color 0.3s ease'
            }}
          />
          
          <button type="submit" disabled={isLoading} style={{
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            color: 'white',
            border: 'none',
            padding: '15px',
            borderRadius: '10px',
            fontSize: '16px',
            fontWeight: '600',
            cursor: 'pointer',
            transition: 'transform 0.2s ease',
            boxShadow: '0 10px 20px rgba(0, 0, 0, 0.3)'
          }}>
            {isLoading ? 'Tarama Ba≈ülatƒ±lƒ±yor...' : 'Tarama Ba≈ülat'}
          </button>
        </form>

        {scanStatus && (
          <div style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(10px)',
            borderRadius: '10px',
            padding: '20px',
            marginTop: '20px',
            borderLeft: `4px solid ${scanStatus === 'completed' ? '#28a745' : scanStatus === 'pending' ? '#ffc107' : '#dc3545'}`,
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }}>
            <h3 style={{ margin: '0 0 10px 0', color: '#fff', textShadow: '1px 1px 2px rgba(0,0,0,0.5)' }}>
              {scanStatus === 'pending' && 'üîÑ Tarama Devam Ediyor'}
              {scanStatus === 'completed' && '‚úÖ Tarama Tamamlandƒ±'}
              {scanStatus === 'error' && '‚ùå Tarama Ba≈üarƒ±sƒ±z'}
            </h3>
            
            {scanStatus === 'pending' && (
              <p style={{ margin: '5px 0', color: 'rgba(255, 255, 255, 0.9)' }}>
                Sisteminiz tarama altƒ±nda. L√ºtfen bekleyin...
                <br />
                <small>Tahmini s√ºre: 3 dakika</small>
              </p>
            )}
            
            {scanStatus === 'completed' && scanResults && (
              <div>
                <p style={{ margin: '5px 0', color: 'rgba(255, 255, 255, 0.9)' }}>
                  <strong>Risk Seviyesi:</strong> {scanResults.data.riskLevel}
                </p>
                <p style={{ margin: '5px 0', color: 'rgba(255, 255, 255, 0.9)' }}>
                  <strong>Malware Durumu:</strong> {scanResults.data.securityChecks.malware}
                </p>
                <p style={{ margin: '5px 0', color: 'rgba(255, 255, 255, 0.9)' }}>
                  <strong>≈û√ºpheli Aktivite:</strong> {scanResults.data.securityChecks.suspiciousActivity}
                </p>
                <p style={{ margin: '5px 0', color: 'rgba(255, 255, 255, 0.9)' }}>
                  <strong>Tarama Tarihi:</strong> {new Date(scanResults.completedAt).toLocaleString('tr-TR')}
                </p>
                
                {scanResults.data.recommendations && (
                  <div>
                    <strong style={{ color: '#fff' }}>√ñneriler:</strong>
                    <ul style={{ color: 'rgba(255, 255, 255, 0.9)' }}>
                      {scanResults.data.recommendations.map((rec, index) => (
                        <li key={index}>{rec}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>
      
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
        }}
      />
    </div>
  );
}

export default App;
