import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const Dashboard = ({ user, onLogout }) => {
  const [pins, setPins] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedPin, setSelectedPin] = useState(null);
  const [showPinDetails, setShowPinDetails] = useState(false);

  useEffect(() => {
    fetchPins();
  }, []);

  const fetchPins = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/my-pins', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPins(response.data);
    } catch (error) {
      console.error('Fetch Pins Error:', error);
      if (error.code === 'ECONNREFUSED') {
        toast.error('Backend sunucusu çalışmıyor!');
      } else {
        toast.error('PIN\'ler yüklenemedi!');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const createRandomPin = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('/api/create-pin', {
        targetInfo: {
          name: 'Random Target',
          email: '',
          phone: '',
          notes: 'Random PIN'
        },
        durationHours: 24
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      toast.success(`PIN oluşturuldu: ${response.data.pin}`);
      fetchPins();
    } catch (error) {
      console.error('Create Pin Error:', error);
      if (error.code === 'ECONNREFUSED') {
        toast.error('Backend sunucusu çalışmıyor!');
      } else {
        toast.error(error.response?.data?.message || 'PIN oluşturulamadı!');
      }
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#00ff00';
      case 'used': return '#ffaa00';
      case 'expired': return '#ff0000';
      default: return '#666';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'active': return 'Aktif';
      case 'used': return 'Kullanıldı';
      case 'expired': return 'Süresi Doldu';
      default: return 'Bilinmiyor';
    }
  };

  const getRiskColor = (riskLevel) => {
    switch (riskLevel) {
      case 'Yüksek': return '#ff0000';
      case 'Orta': return '#ffaa00';
      case 'Düşük': return '#00ff00';
      case 'Çok Düşük': return '#00aaff';
      default: return '#666';
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleString('tr-TR');
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Panoya kopyalandı!');
  };

  const openPinDetails = (pin) => {
    setSelectedPin(pin);
    setShowPinDetails(true);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'url(/background.jpg) center/cover no-repeat, linear-gradient(135deg, #ff0000 0%, #cc0000 100%)',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Rain Effect */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1
      }}>
        {Array.from({ length: 30 }).map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: '2px',
              height: `${Math.random() * 100 + 50}px`,
              background: 'linear-gradient(to bottom, rgba(0,0,0,0.8), rgba(0,0,0,0.3))',
              animation: `rain ${Math.random() * 2 + 1}s linear infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
        `}
      </style>

      {/* Header */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.1)',
        backdropFilter: 'blur(15px)',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '20px',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        position: 'relative',
        zIndex: 2
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          flexWrap: 'wrap'
        }}>
          <div>
            <h1 style={{
              color: '#fff',
              fontSize: '2rem',
              margin: '0 0 5px 0',
              textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)',
              letterSpacing: '2px'
            }}>
              VOID SCANNER
            </h1>
            <p style={{
              color: 'rgba(255, 255, 255, 0.8)',
              margin: '0',
              fontSize: '1rem'
            }}>
              Hoş geldin, {user.name}!
            </p>
          </div>
          <button
            onClick={onLogout}
            style={{
              background: 'linear-gradient(135deg, #ff0000 0%, #cc0000 100%)',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '8px',
              fontSize: '14px',
              fontWeight: '700',
              cursor: 'pointer',
              boxShadow: '0 5px 15px rgba(255, 0, 0, 0.3)',
              textTransform: 'uppercase',
              letterSpacing: '1px'
            }}
          >
            Çıkış
          </button>
        </div>
      </div>

      {/* Main Content - 3 Column Layout */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: '300px 1fr 300px',
        gap: '20px',
        position: 'relative',
        zIndex: 2
      }}>
        
        {/* LEFT SIDE - Membership Info */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(15px)',
          borderRadius: '15px',
          padding: '20px',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          height: 'fit-content'
        }}>
          <h3 style={{
            color: '#fff',
            fontSize: '1.3rem',
            margin: '0 0 20px 0',
            textAlign: 'center',
            textShadow: '1px 1px 2px rgba(0,0,0,0.5)'
          }}>
            Üyelik Bilgileri
          </h3>
          
          <div style={{
            background: 'rgba(255, 255, 255, 0.05)',
            borderRadius: '10px',
            padding: '15px',
            marginBottom: '15px'
          }}>
            <div style={{
              color: '#00ff00',
              fontSize: '1.1rem',
              fontWeight: '700',
              marginBottom: '5px'
            }}>
              ACMaster
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '14px'
            }}>
              15 Kişilik Slot
            </div>
          </div>

          <div style={{
            background: 'rgba(255, 255, 255, 0.05)',
            borderRadius: '10px',
            padding: '15px',
            marginBottom: '15px'
          }}>
            <div style={{
              color: '#fff',
              fontSize: '1rem',
              fontWeight: '700',
              marginBottom: '5px'
            }}>
              Kullanılan PIN'ler
            </div>
            <div style={{
              color: '#ffaa00',
              fontSize: '1.5rem',
              fontWeight: '700'
            }}>
              {pins.length}
            </div>
          </div>

          <div style={{
            background: 'rgba(255, 255, 255, 0.05)',
            borderRadius: '10px',
            padding: '15px',
            marginBottom: '15px'
          }}>
            <div style={{
              color: '#fff',
              fontSize: '1rem',
              fontWeight: '700',
              marginBottom: '5px'
            }}>
              Aktif PIN'ler
            </div>
            <div style={{
              color: '#00ff00',
              fontSize: '1.5rem',
              fontWeight: '700'
            }}>
              {pins.filter(pin => pin.status === 'active').length}
            </div>
          </div>

          <div style={{
            background: 'rgba(255, 255, 255, 0.05)',
            borderRadius: '10px',
            padding: '15px'
          }}>
            <div style={{
              color: '#fff',
              fontSize: '1rem',
              fontWeight: '700',
              marginBottom: '5px'
            }}>
              Tamamlanan Taramalar
            </div>
            <div style={{
              color: '#00aaff',
              fontSize: '1.5rem',
              fontWeight: '700'
            }}>
              {pins.filter(pin => pin.scanResults?.status === 'completed').length}
            </div>
          </div>
        </div>

        {/* CENTER - PIN List */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(15px)',
          borderRadius: '15px',
          padding: '20px',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          maxHeight: '70vh',
          overflow: 'auto'
        }}>
          <h2 style={{
            color: '#fff',
            fontSize: '1.5rem',
            margin: '0 0 20px 0',
            textShadow: '1px 1px 2px rgba(0,0,0,0.5)',
            textAlign: 'center'
          }}>
            PIN'lerim ({pins.length})
          </h2>

          {isLoading ? (
            <div style={{
              textAlign: 'center',
              color: 'rgba(255, 255, 255, 0.8)',
              padding: '40px'
            }}>
              Yükleniyor...
            </div>
          ) : pins.length === 0 ? (
            <div style={{
              textAlign: 'center',
              color: 'rgba(255, 255, 255, 0.8)',
              padding: '40px'
            }}>
              Henüz PIN oluşturmadınız.
            </div>
          ) : (
            <div style={{
              display: 'flex',
              flexDirection: 'column',
              gap: '10px'
            }}>
              {pins.map((pin) => (
                <div
                  key={pin._id}
                  style={{
                    background: 'rgba(255, 255, 255, 0.05)',
                    borderRadius: '10px',
                    padding: '15px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease'
                  }}
                  onClick={() => openPinDetails(pin)}
                  onMouseOver={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.1)'}
                  onMouseOut={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.05)'}
                >
                  <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginBottom: '10px'
                  }}>
                    <div style={{
                      fontSize: '1.2rem',
                      fontWeight: '700',
                      color: '#fff',
                      fontFamily: 'monospace',
                      letterSpacing: '2px'
                    }}>
                      {pin.pin}
                    </div>
                    <div style={{
                      padding: '4px 8px',
                      borderRadius: '4px',
                      fontSize: '12px',
                      fontWeight: '700',
                      color: '#fff',
                      background: getStatusColor(pin.status)
                    }}>
                      {getStatusText(pin.status)}
                    </div>
                  </div>

                  <div style={{
                    fontSize: '14px',
                    color: 'rgba(255, 255, 255, 0.8)',
                    marginBottom: '5px'
                  }}>
                    <strong>Hedef:</strong> {pin.targetInfo?.name || 'Belirtilmemiş'}
                  </div>

                  <div style={{
                    fontSize: '12px',
                    color: 'rgba(255, 255, 255, 0.6)',
                    marginBottom: '5px'
                  }}>
                    <strong>Oluşturulma:</strong> {formatDate(pin.createdAt)}
                  </div>

                  {pin.scanResults?.status === 'completed' && (
                    <div style={{
                      fontSize: '12px',
                      color: 'rgba(255, 255, 255, 0.8)',
                      marginBottom: '5px'
                    }}>
                      <strong>Risk:</strong> 
                      <span style={{
                        color: getRiskColor(pin.scanResults.riskLevel),
                        fontWeight: '700',
                        marginLeft: '5px'
                      }}>
                        {pin.scanResults.riskLevel}
                      </span>
                    </div>
                  )}

                  <div style={{
                    fontSize: '12px',
                    color: 'rgba(255, 255, 255, 0.5)',
                    textAlign: 'right'
                  }}>
                    Detayları görmek için tıklayın →
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* RIGHT SIDE - PIN Creation */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(15px)',
          borderRadius: '15px',
          padding: '20px',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          height: 'fit-content'
        }}>
          <h3 style={{
            color: '#fff',
            fontSize: '1.3rem',
            margin: '0 0 20px 0',
            textAlign: 'center',
            textShadow: '1px 1px 2px rgba(0,0,0,0.5)'
          }}>
            PIN Oluştur
          </h3>

          <div style={{
            background: 'rgba(255, 255, 255, 0.05)',
            borderRadius: '10px',
            padding: '20px',
            marginBottom: '15px',
            textAlign: 'center'
          }}>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '14px',
              marginBottom: '15px'
            }}>
              Otomatik random PIN oluşturulacak
            </div>
            
            <button
              onClick={createRandomPin}
              style={{
                background: 'linear-gradient(135deg, #00ff00 0%, #00cc00 100%)',
                color: 'white',
                border: 'none',
                padding: '15px 25px',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '700',
                cursor: 'pointer',
                boxShadow: '0 5px 15px rgba(0, 255, 0, 0.3)',
                textTransform: 'uppercase',
                letterSpacing: '1px',
                width: '100%'
              }}
            >
              + Yeni PIN Oluştur
            </button>
          </div>

          <div style={{
            background: 'rgba(255, 255, 255, 0.05)',
            borderRadius: '10px',
            padding: '15px',
            marginBottom: '15px'
          }}>
            <div style={{
              color: '#fff',
              fontSize: '14px',
              fontWeight: '700',
              marginBottom: '10px'
            }}>
              PIN Özellikleri:
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '12px',
              lineHeight: '1.5'
            }}>
              • 8 karakter uzunluğunda<br/>
              • 24 saat geçerli<br/>
              • Benzersiz kod<br/>
              • Güvenli tarama
            </div>
          </div>

          <div style={{
            background: 'rgba(255, 255, 255, 0.05)',
            borderRadius: '10px',
            padding: '15px'
          }}>
            <div style={{
              color: '#fff',
              fontSize: '14px',
              fontWeight: '700',
              marginBottom: '10px'
            }}>
              Nasıl Kullanılır:
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '12px',
              lineHeight: '1.5'
            }}>
              1. PIN oluştur<br/>
              2. Hedef kişiye gönder<br/>
              3. Tarama sonuçlarını bekle<br/>
              4. Detayları görüntüle
            </div>
          </div>
        </div>
      </div>

      {/* PIN Details Modal */}
      {showPinDetails && selectedPin && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          background: 'rgba(0, 0, 0, 0.8)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000,
          padding: '20px'
        }}>
          <div style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(15px)',
            borderRadius: '15px',
            padding: '30px',
            width: '100%',
            maxWidth: '800px',
            maxHeight: '80vh',
            overflow: 'auto',
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '20px'
            }}>
              <h3 style={{
                color: '#fff',
                fontSize: '1.5rem',
                margin: '0'
              }}>
                PIN Detayları
              </h3>
              <button
                onClick={() => setShowPinDetails(false)}
                style={{
                  background: 'rgba(255, 255, 255, 0.1)',
                  color: '#fff',
                  border: '1px solid rgba(255, 255, 255, 0.3)',
                  padding: '5px 10px',
                  borderRadius: '4px',
                  fontSize: '14px',
                  cursor: 'pointer'
                }}
              >
                ✕
              </button>
            </div>

            <div style={{
              fontSize: '2rem',
              fontWeight: '700',
              color: '#fff',
              fontFamily: 'monospace',
              letterSpacing: '3px',
              textAlign: 'center',
              marginBottom: '20px',
              padding: '15px',
              background: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '8px'
            }}>
              {selectedPin.pin}
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
              gap: '15px',
              marginBottom: '20px'
            }}>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Durum:</strong>
                <div style={{
                  color: getStatusColor(selectedPin.status),
                  fontWeight: '700'
                }}>
                  {getStatusText(selectedPin.status)}
                </div>
              </div>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Hedef:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {selectedPin.targetInfo?.name || 'Belirtilmemiş'}
                </div>
              </div>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Oluşturulma:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {formatDate(selectedPin.createdAt)}
                </div>
              </div>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Son Kullanma:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {formatDate(selectedPin.expiresAt)}
                </div>
              </div>
            </div>

            {selectedPin.scanResults?.status === 'completed' && (
              <div style={{
                background: 'rgba(255, 255, 255, 0.05)',
                borderRadius: '10px',
                padding: '20px',
                marginTop: '20px'
              }}>
                <h4 style={{
                  color: '#fff',
                  fontSize: '1.2rem',
                  margin: '0 0 15px 0'
                }}>
                  Tarama Sonuçları
                </h4>

                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
                  gap: '15px',
                  marginBottom: '15px'
                }}>
                  <div>
                    <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Risk Seviyesi:</strong>
                    <div style={{
                      color: getRiskColor(selectedPin.scanResults.riskLevel),
                      fontWeight: '700',
                      fontSize: '1.1rem'
                    }}>
                      {selectedPin.scanResults.riskLevel}
                    </div>
                  </div>
                  <div>
                    <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Tamamlanma:</strong>
                    <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                      {formatDate(selectedPin.scanResults.completedAt)}
                    </div>
                  </div>
                </div>

                {selectedPin.scanResults.summary && (
                  <div>
                    <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Özet:</strong>
                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))',
                      gap: '10px',
                      marginTop: '10px'
                    }}>
                      <div style={{
                        background: 'rgba(255, 255, 255, 0.05)',
                        padding: '10px',
                        borderRadius: '6px',
                        textAlign: 'center'
                      }}>
                        <div style={{ color: '#fff', fontWeight: '700', fontSize: '1.2rem' }}>
                          {selectedPin.scanResults.summary.suspiciousProcessesCount || 0}
                        </div>
                        <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '12px' }}>
                          Şüpheli Süreç
                        </div>
                      </div>
                      <div style={{
                        background: 'rgba(255, 255, 255, 0.05)',
                        padding: '10px',
                        borderRadius: '6px',
                        textAlign: 'center'
                      }}>
                        <div style={{ color: '#fff', fontWeight: '700', fontSize: '1.2rem' }}>
                          {selectedPin.scanResults.summary.suspiciousFilesCount || 0}
                        </div>
                        <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '12px' }}>
                          Şüpheli Dosya
                        </div>
                      </div>
                      <div style={{
                        background: 'rgba(255, 255, 255, 0.05)',
                        padding: '10px',
                        borderRadius: '6px',
                        textAlign: 'center'
                      }}>
                        <div style={{ color: '#fff', fontWeight: '700', fontSize: '1.2rem' }}>
                          {selectedPin.scanResults.summary.memoryInjectionsCount || 0}
                        </div>
                        <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '12px' }}>
                          Memory Injection
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            )}

            <div style={{
              display: 'flex',
              gap: '10px',
              justifyContent: 'center',
              marginTop: '20px'
            }}>
              <button
                onClick={() => copyToClipboard(selectedPin.pin)}
                style={{
                  background: 'linear-gradient(135deg, #00ff00 0%, #00cc00 100%)',
                  color: 'white',
                  border: 'none',
                  padding: '10px 20px',
                  borderRadius: '8px',
                  fontSize: '14px',
                  fontWeight: '700',
                  cursor: 'pointer',
                  textTransform: 'uppercase'
                }}
              >
                PIN'i Kopyala
              </button>
            </div>
          </div>
        </div>
      )}
      
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
        }}
      />
    </div>
  );
};

export default Dashboard;
