import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';
import PinDetailsPage from './PinDetailsPage';
import CreatePinPage from './CreatePinPage';

const Dashboard = ({ user, onLogout }) => {
  const [pins, setPins] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedPin, setSelectedPin] = useState(null);
  const [showPinDetails, setShowPinDetails] = useState(false);
  const [showPinDetailsPage, setShowPinDetailsPage] = useState(false);
  const [showCreatePinPage, setShowCreatePinPage] = useState(false);
  const [scanResults, setScanResults] = useState(null);
  const [screenshot, setScreenshot] = useState(null);

  useEffect(() => {
    fetchPins();
  }, []);

  const fetchPins = async () => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/pins', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPins(response.data);
    } catch (error) {
      console.error('PIN\'ler yüklenemedi:', error);
      toast.error('PIN\'ler yüklenemedi!');
    } finally {
      setIsLoading(false);
    }
  };

  const createPin = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('/api/pins', {
        targetInfo: {
          name: 'Random Target',
          description: 'Test target for scanning'
        }
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      toast.success('PIN başarıyla oluşturuldu!');
      fetchPins(); // PIN listesini yenile
    } catch (error) {
      console.error('PIN oluşturulamadı:', error);
      toast.error('PIN oluşturulamadı!');
    }
  };

  const deletePin = async (pinId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/pins/${pinId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      toast.success('PIN başarıyla silindi!');
      fetchPins(); // PIN listesini yenile
    } catch (error) {
      console.error('PIN silinemedi:', error);
      toast.error('PIN silinemedi!');
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleString('tr-TR');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#10b981';
      case 'used': return '#f59e0b';
      case 'expired': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'active': return 'Aktif';
      case 'used': return 'Kullanıldı';
      case 'expired': return 'Süresi Doldu';
      default: return 'Bilinmiyor';
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Panoya kopyalandı!');
  };

  const viewPinDetails = async (pin) => {
    try {
      setSelectedPin(pin);
      setShowPinDetailsPage(true);
    } catch (error) {
      console.error('PIN detayları yüklenemedi:', error);
      toast.error('PIN detayları yüklenemedi!');
    }
  };

  const parseScanResults = (results) => {
    if (!results) return null;
    
    // Eğer results string değilse, string'e çevir
    let resultsString = results;
    if (typeof results !== 'string') {
      resultsString = String(results);
    }
    
    const lines = resultsString.split('\n');
    const sections = {
      suspiciousActivities: [],
      deletedFiles: [],
      recentApps: [],
      unlicensedApps: [],
      screenshot: null
    };
    
    let currentSection = null;
    
    lines.forEach(line => {
      line = line.trim();
      
      if (line.includes('ŞÜPHELİ AKTİVİTELER')) {
        currentSection = 'suspiciousActivities';
      } else if (line.includes('SON SİLİNEN DOSYALAR')) {
        currentSection = 'deletedFiles';
      } else if (line.includes('SON ÇALIŞTIRILAN UYGULAMALAR')) {
        currentSection = 'recentApps';
      } else if (line.includes('LİSANSSIZ UYGULAMALAR')) {
        currentSection = 'unlicensedApps';
      } else if (line.includes('EKRAN GÖRÜNTÜSÜ')) {
        currentSection = 'screenshot';
      } else if (line && currentSection && line.startsWith('⚠️')) {
        sections.suspiciousActivities.push(line.replace('⚠️ ', ''));
      } else if (line && currentSection && line.startsWith('📁')) {
        sections.deletedFiles.push(line.replace('📁 ', ''));
      } else if (line && currentSection && line.startsWith('💻')) {
        sections.recentApps.push(line.replace('💻 ', ''));
      } else if (line && currentSection && line.startsWith('🔓')) {
        sections.unlicensedApps.push(line.replace('🔓 ', ''));
      } else if (line && currentSection === 'screenshot' && line.includes('✅')) {
        sections.screenshot = line.replace('✅ Ekran görüntüsü alındı: ', '');
      }
    });
    
    return sections;
  };

  if (isLoading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#ffffff',
        fontSize: '1.2rem'
      }}>
        <div style={{
          textAlign: 'center',
          padding: '40px',
          background: 'rgba(20, 20, 20, 0.8)',
          borderRadius: '15px',
          border: '1px solid rgba(220, 38, 38, 0.3)'
        }}>
          <div style={{
            width: '50px',
            height: '50px',
            border: '3px solid rgba(220, 38, 38, 0.3)',
            borderTop: '3px solid #dc2626',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 20px'
          }}></div>
          PIN'ler yükleniyor...
        </div>
        <style>
          {`
            @keyframes spin {
              0% { transform: rotate(0deg); }
              100% { transform: rotate(360deg); }
            }
          `}
        </style>
      </div>
    );
  }

  // Kullanıcının Enterprise bilgisi
  const userEnterprise = {
    name: 'VOIDSCAN',
    slot: '22/3',
    status: 'active',
    lastActivity: '5 dakika önce'
  };

  if (showCreatePinPage) {
    return (
      <CreatePinPage 
        onBack={() => setShowCreatePinPage(false)}
        onPinCreated={fetchPins}
      />
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
      color: '#ffffff',
      position: 'relative',
      overflow: 'hidden',
      display: 'flex'
    }}>
      {/* Sol Sidebar */}
      <div style={{
        width: '280px',
        background: 'rgba(15, 15, 15, 0.95)',
        backdropFilter: 'blur(20px)',
        borderRight: '1px solid rgba(220, 38, 38, 0.3)',
        padding: '20px',
        position: 'relative',
        zIndex: 2,
        overflowY: 'auto'
      }}>
        {/* Enterprise Info */}
        <div style={{ marginBottom: '30px' }}>
          <h3 style={{
            color: '#dc2626',
            fontSize: '1.1rem',
            fontWeight: '700',
            margin: '0 0 15px 0',
            textTransform: 'uppercase',
            letterSpacing: '1px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            <span style={{
              width: '8px',
              height: '8px',
              background: '#dc2626',
              borderRadius: '50%',
              boxShadow: '0 0 10px rgba(220, 38, 38, 0.5)'
            }}></span>
            Enterprise
          </h3>
          
          <div style={{
            background: 'rgba(255, 255, 255, 0.03)',
            borderRadius: '8px',
            padding: '12px 15px',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            transition: 'all 0.3s ease'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '5px'
            }}>
              <span style={{
                color: '#ffffff',
                fontSize: '0.9rem',
                fontWeight: '600',
                fontFamily: 'monospace'
              }}>
                {userEnterprise.name} {userEnterprise.slot}
              </span>
              <div style={{
                width: '8px',
                height: '8px',
                borderRadius: '50%',
                background: userEnterprise.status === 'active' ? '#10b981' : '#6b7280',
                boxShadow: `0 0 8px ${userEnterprise.status === 'active' ? 'rgba(16, 185, 129, 0.5)' : 'rgba(107, 114, 128, 0.5)'}`
              }}></div>
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.6)',
              fontSize: '0.75rem',
              fontStyle: 'italic'
            }}>
              {userEnterprise.lastActivity}
            </div>
          </div>
        </div>

        {/* Create Pin Button */}
        <div style={{ marginBottom: '30px' }}>
          <button
            onClick={() => setShowCreatePinPage(true)}
            style={{
              width: '100%',
              background: 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)',
              color: 'white',
              border: 'none',
              padding: '15px 20px',
              borderRadius: '10px',
              fontSize: '1rem',
              fontWeight: '700',
              cursor: 'pointer',
              textTransform: 'uppercase',
              letterSpacing: '1px',
              transition: 'all 0.3s ease',
              boxShadow: '0 4px 15px rgba(220, 38, 38, 0.3)',
              border: '1px solid rgba(220, 38, 38, 0.3)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '10px'
            }}
            onMouseOver={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 6px 20px rgba(220, 38, 38, 0.4)';
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 4px 15px rgba(220, 38, 38, 0.3)';
            }}
          >
            Create Pin
          </button>
        </div>

        {/* Dashboard Stats */}
        <div style={{
          background: 'rgba(220, 38, 38, 0.05)',
          borderRadius: '12px',
          padding: '20px',
          border: '1px solid rgba(220, 38, 38, 0.2)'
        }}>
          <h4 style={{
            color: '#dc2626',
            fontSize: '1rem',
            fontWeight: '600',
            margin: '0 0 15px 0',
            textAlign: 'center'
          }}>
            Dashboard Stats
          </h4>
          
          <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '8px 0',
              borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
            }}>
              <span style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '0.9rem' }}>Total Scans</span>
              <span style={{ color: '#dc2626', fontWeight: '700', fontSize: '1.1rem' }}>{pins.length}</span>
            </div>
            
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '8px 0',
              borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
            }}>
              <span style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '0.9rem' }}>Active</span>
              <span style={{ color: '#10b981', fontWeight: '700', fontSize: '1.1rem' }}>
                {pins.filter(pin => pin.status === 'active').length}
              </span>
            </div>
            
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '8px 0',
              borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
            }}>
              <span style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '0.9rem' }}>Completed</span>
              <span style={{ color: '#f59e0b', fontWeight: '700', fontSize: '1.1rem' }}>
                {pins.filter(pin => pin.status === 'used').length}
              </span>
            </div>
            
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '8px 0'
            }}>
              <span style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '0.9rem' }}>Expired</span>
              <span style={{ color: '#ef4444', fontWeight: '700', fontSize: '1.1rem' }}>
                {pins.filter(pin => pin.status === 'expired').length}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Ana İçerik Alanı */}
      <div style={{
        flex: 1,
        position: 'relative',
        zIndex: 2
      }}>
        {/* Yağmur efekti */}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          pointerEvents: 'none',
          zIndex: 1
        }}>
          {Array.from({ length: 20 }).map((_, i) => (
            <div
              key={i}
              style={{
                position: 'absolute',
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                width: '1px',
                height: `${Math.random() * 50 + 30}px`,
                background: 'linear-gradient(to bottom, rgba(220, 38, 38, 0.3), rgba(220, 38, 38, 0.1))',
                animation: `rain ${Math.random() * 3 + 2}s linear infinite`,
                animationDelay: `${Math.random() * 2}s`
              }}
            />
          ))}
        </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
        `}
      </style>

        {/* Header */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.9)',
          backdropFilter: 'blur(20px)',
          padding: '20px 30px',
          borderBottom: '1px solid rgba(220, 38, 38, 0.3)',
          position: 'relative',
          zIndex: 2
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <h1 style={{
              color: '#ffffff',
              fontSize: '2.5rem',
              margin: 0,
              fontWeight: '900',
              textShadow: '0 0 30px rgba(220, 38, 38, 0.7)',
              fontFamily: 'Arial, sans-serif',
              letterSpacing: '3px',
              background: 'linear-gradient(45deg, #ffffff 0%, #dc2626 50%, #ffffff 100%)',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}>
              VOID SCANNER DASHBOARD
            </h1>
            
            <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
              <div style={{
                color: 'rgba(255, 255, 255, 0.8)',
                fontSize: '0.9rem',
                textAlign: 'right'
              }}>
                <div>Hoş geldin, <strong style={{ color: '#dc2626' }}>{user.username}</strong></div>
                <div style={{ fontSize: '0.8rem', color: 'rgba(255, 255, 255, 0.6)' }}>
                  {user.role === 'admin' ? '👑 Admin' : '👤 Müşteri'}
                </div>
              </div>
              
              <button
                onClick={onLogout}
                style={{
                  background: 'rgba(220, 38, 38, 0.2)',
                  color: '#ffffff',
                  border: '1px solid rgba(220, 38, 38, 0.5)',
                  borderRadius: '8px',
                  padding: '10px 16px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: '600',
                  transition: 'all 0.3s ease'
                }}
                onMouseOver={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 0.3)';
                }}
                onMouseOut={(e) => {
                  e.target.style.background = 'rgba(220, 38, 38, 0.2)';
                }}
              >
                Çıkış Yap
              </button>
            </div>
          </div>
        </div>

        {/* Ana İçerik */}
        <div style={{
          flex: 1,
          padding: '30px',
          position: 'relative',
          zIndex: 2,
          overflowY: 'auto'
        }}>
        {/* Profesyonel Dashboard Kartları */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
          gap: '25px',
          marginBottom: '40px'
        }}>
          {/* Total Scans Card */}
          <div style={{
            background: 'linear-gradient(135deg, rgba(20, 20, 20, 0.9) 0%, rgba(30, 30, 30, 0.9) 100%)',
            borderRadius: '20px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.4)',
            boxShadow: '0 15px 40px rgba(0, 0, 0, 0.4)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            <div style={{
              position: 'absolute',
              top: '-20px',
              right: '-20px',
              width: '80px',
              height: '80px',
              background: 'radial-gradient(circle, rgba(220, 38, 38, 0.2) 0%, transparent 70%)',
              borderRadius: '50%'
            }}></div>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              marginBottom: '20px'
            }}>
              <div>
                <div style={{
                  fontSize: '0.9rem',
                  color: 'rgba(255, 255, 255, 0.7)',
                  fontWeight: '500',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  marginBottom: '5px'
                }}>
                  Total Scans
                </div>
                <div style={{
                  fontSize: '3rem',
                  fontWeight: '800',
                  color: '#dc2626',
                  textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
                  fontFamily: 'monospace'
                }}>
                  {pins.length}
                </div>
              </div>
              <div style={{
                width: '60px',
                height: '60px',
                background: 'rgba(220, 38, 38, 0.1)',
                borderRadius: '15px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                border: '1px solid rgba(220, 38, 38, 0.3)'
              }}>
                <div style={{
                  fontSize: '1.2rem',
                  color: '#dc2626',
                  fontWeight: '700'
                }}>SCAN</div>
              </div>
            </div>
            <div style={{
              fontSize: '0.85rem',
              color: 'rgba(255, 255, 255, 0.6)',
              fontStyle: 'italic'
            }}>
              Total scan operations performed
            </div>
          </div>

          {/* Active Scans Card */}
          <div style={{
            background: 'linear-gradient(135deg, rgba(20, 20, 20, 0.9) 0%, rgba(30, 30, 30, 0.9) 100%)',
            borderRadius: '20px',
            padding: '30px',
            border: '1px solid rgba(16, 185, 129, 0.4)',
            boxShadow: '0 15px 40px rgba(0, 0, 0, 0.4)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            <div style={{
              position: 'absolute',
              top: '-20px',
              right: '-20px',
              width: '80px',
              height: '80px',
              background: 'radial-gradient(circle, rgba(16, 185, 129, 0.2) 0%, transparent 70%)',
              borderRadius: '50%'
            }}></div>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              marginBottom: '20px'
            }}>
              <div>
                <div style={{
                  fontSize: '0.9rem',
                  color: 'rgba(255, 255, 255, 0.7)',
                  fontWeight: '500',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  marginBottom: '5px'
                }}>
                  Active Scans
                </div>
                <div style={{
                  fontSize: '3rem',
                  fontWeight: '800',
                  color: '#10b981',
                  textShadow: '0 0 20px rgba(16, 185, 129, 0.5)',
                  fontFamily: 'monospace'
                }}>
                  {pins.filter(pin => pin.status === 'active').length}
                </div>
              </div>
              <div style={{
                width: '60px',
                height: '60px',
                background: 'rgba(16, 185, 129, 0.1)',
                borderRadius: '15px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                border: '1px solid rgba(16, 185, 129, 0.3)'
              }}>
                <div style={{
                  fontSize: '1.8rem',
                  color: '#10b981'
                }}>⚡</div>
              </div>
            </div>
            <div style={{
              fontSize: '0.85rem',
              color: 'rgba(255, 255, 255, 0.6)',
              fontStyle: 'italic'
            }}>
              Currently running scans
            </div>
          </div>

          {/* Completed Scans Card */}
          <div style={{
            background: 'linear-gradient(135deg, rgba(20, 20, 20, 0.9) 0%, rgba(30, 30, 30, 0.9) 100%)',
            borderRadius: '20px',
            padding: '30px',
            border: '1px solid rgba(245, 158, 11, 0.4)',
            boxShadow: '0 15px 40px rgba(0, 0, 0, 0.4)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            <div style={{
              position: 'absolute',
              top: '-20px',
              right: '-20px',
              width: '80px',
              height: '80px',
              background: 'radial-gradient(circle, rgba(245, 158, 11, 0.2) 0%, transparent 70%)',
              borderRadius: '50%'
            }}></div>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              marginBottom: '20px'
            }}>
              <div>
                <div style={{
                  fontSize: '0.9rem',
                  color: 'rgba(255, 255, 255, 0.7)',
                  fontWeight: '500',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  marginBottom: '5px'
                }}>
                  Completed
                </div>
                <div style={{
                  fontSize: '3rem',
                  fontWeight: '800',
                  color: '#f59e0b',
                  textShadow: '0 0 20px rgba(245, 158, 11, 0.5)',
                  fontFamily: 'monospace'
                }}>
                  {pins.filter(pin => pin.status === 'used').length}
                </div>
              </div>
              <div style={{
                width: '60px',
                height: '60px',
                background: 'rgba(245, 158, 11, 0.1)',
                borderRadius: '15px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                border: '1px solid rgba(245, 158, 11, 0.3)'
              }}>
                <div style={{
                  fontSize: '1.8rem',
                  color: '#f59e0b'
                }}>✅</div>
              </div>
            </div>
            <div style={{
              fontSize: '0.85rem',
              color: 'rgba(255, 255, 255, 0.6)',
              fontStyle: 'italic'
            }}>
              Successfully completed scans
            </div>
          </div>

          {/* Expired Scans Card */}
          <div style={{
            background: 'linear-gradient(135deg, rgba(20, 20, 20, 0.9) 0%, rgba(30, 30, 30, 0.9) 100%)',
            borderRadius: '20px',
            padding: '30px',
            border: '1px solid rgba(239, 68, 68, 0.4)',
            boxShadow: '0 15px 40px rgba(0, 0, 0, 0.4)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            <div style={{
              position: 'absolute',
              top: '-20px',
              right: '-20px',
              width: '80px',
              height: '80px',
              background: 'radial-gradient(circle, rgba(239, 68, 68, 0.2) 0%, transparent 70%)',
              borderRadius: '50%'
            }}></div>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              marginBottom: '20px'
            }}>
              <div>
                <div style={{
                  fontSize: '0.9rem',
                  color: 'rgba(255, 255, 255, 0.7)',
                  fontWeight: '500',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  marginBottom: '5px'
                }}>
                  Expired
                </div>
                <div style={{
                  fontSize: '3rem',
                  fontWeight: '800',
                  color: '#ef4444',
                  textShadow: '0 0 20px rgba(239, 68, 68, 0.5)',
                  fontFamily: 'monospace'
                }}>
                  {pins.filter(pin => pin.status === 'expired').length}
                </div>
              </div>
              <div style={{
                width: '60px',
                height: '60px',
                background: 'rgba(239, 68, 68, 0.1)',
                borderRadius: '15px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                border: '1px solid rgba(239, 68, 68, 0.3)'
              }}>
                <div style={{
                  fontSize: '1.8rem',
                  color: '#ef4444'
                }}>⏰</div>
              </div>
            </div>
            <div style={{
              fontSize: '0.85rem',
              color: 'rgba(255, 255, 255, 0.6)',
              fontStyle: 'italic'
            }}>
              Expired scan sessions
            </div>
          </div>
        </div>

        {/* PIN Oluşturma Butonu - KALDIRILDI, artık sidebar'da */}

        {/* PIN Listesi */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.8)',
          borderRadius: '20px',
          padding: '30px',
          border: '1px solid rgba(220, 38, 38, 0.3)',
          boxShadow: '0 15px 40px rgba(0, 0, 0, 0.4)'
        }}>
          <h2 style={{
            color: '#ffffff',
            fontSize: '1.5rem',
            margin: '0 0 20px 0',
            fontWeight: '600',
            display: 'flex',
            alignItems: 'center',
            gap: '10px'
          }}>
            📋 PIN Listesi
          </h2>
          
          {pins.length === 0 ? (
            <div style={{
              textAlign: 'center',
              padding: '40px',
              color: 'rgba(255, 255, 255, 0.6)',
              fontSize: '1.1rem'
            }}>
              Henüz PIN oluşturulmamış. Yukarıdaki butona tıklayarak yeni PIN oluşturun.
            </div>
          ) : (
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))',
              gap: '20px'
            }}>
              {pins.map((pin) => (
                <div
                  key={pin._id}
                  style={{
                    background: 'rgba(255, 255, 255, 0.05)',
                    borderRadius: '12px',
                    padding: '20px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    transition: 'all 0.3s ease',
                    cursor: 'pointer'
                  }}
                  onMouseOver={(e) => {
                    e.currentTarget.style.background = 'rgba(255, 255, 255, 0.08)';
                    e.currentTarget.style.transform = 'translateY(-2px)';
                    e.currentTarget.style.boxShadow = '0 8px 25px rgba(0, 0, 0, 0.3)';
                  }}
                  onMouseOut={(e) => {
                    e.currentTarget.style.background = 'rgba(255, 255, 255, 0.05)';
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.boxShadow = 'none';
                  }}
                  onClick={() => viewPinDetails(pin)}
                >
                  <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginBottom: '15px'
                  }}>
                    <div style={{
                      fontSize: '1.3rem',
                      fontWeight: '700',
                      color: '#ffffff',
                      fontFamily: 'monospace',
                      letterSpacing: '2px'
                    }}>
                      {pin.pin}
                    </div>
                    <div style={{
                      padding: '4px 12px',
                      borderRadius: '20px',
                      fontSize: '0.8rem',
                      fontWeight: '600',
                      background: `rgba(${getStatusColor(pin.status).replace('#', '')}, 0.2)`,
                      color: getStatusColor(pin.status),
                      border: `1px solid ${getStatusColor(pin.status)}`
                    }}>
                      {getStatusText(pin.status)}
                    </div>
                  </div>
                  
                  <div style={{
                    color: 'rgba(255, 255, 255, 0.8)',
                    fontSize: '0.9rem',
                    lineHeight: '1.5',
                    marginBottom: '15px'
                  }}>
                    <div><strong>Hedef:</strong> {pin.targetInfo?.name || 'Belirtilmemiş'}</div>
                    <div><strong>Oluşturulma:</strong> {formatDate(pin.createdAt)}</div>
                    <div><strong>Son Kullanma:</strong> {formatDate(pin.expiresAt)}</div>
                  </div>
                  
                  <div style={{
                    display: 'flex',
                    gap: '10px',
                    justifyContent: 'flex-end'
                  }}>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        copyToClipboard(pin.pin);
                      }}
                      style={{
                        background: 'rgba(16, 185, 129, 0.2)',
                        color: '#10b981',
                        border: '1px solid rgba(16, 185, 129, 0.5)',
                        borderRadius: '6px',
                        padding: '6px 12px',
                        fontSize: '0.8rem',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease'
                      }}
                      onMouseOver={(e) => {
                        e.target.style.background = 'rgba(16, 185, 129, 0.3)';
                      }}
                      onMouseOut={(e) => {
                        e.target.style.background = 'rgba(16, 185, 129, 0.2)';
                      }}
                    >
                      Kopyala
                    </button>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        deletePin(pin._id);
                      }}
                      style={{
                        background: 'rgba(239, 68, 68, 0.2)',
                        color: '#ef4444',
                        border: '1px solid rgba(239, 68, 68, 0.5)',
                        borderRadius: '6px',
                        padding: '6px 12px',
                        fontSize: '0.8rem',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease'
                      }}
                      onMouseOver={(e) => {
                        e.target.style.background = 'rgba(239, 68, 68, 0.3)';
                      }}
                      onMouseOut={(e) => {
                        e.target.style.background = 'rgba(239, 68, 68, 0.2)';
                      }}
                    >
                      Sil
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
        </div>
      </div>
      
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#1f2937',
            color: '#fff',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            borderRadius: '8px'
          },
        }}
      />

      {/* PIN Detayları Sayfası */}
      {showPinDetailsPage && selectedPin && (
        <PinDetailsPage 
          pin={selectedPin}
          onBack={() => {
            setShowPinDetailsPage(false);
            setSelectedPin(null);
          }}
        />
      )}
    </div>
  );
};

export default Dashboard;
