import React, { useState } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

function App() {
  const [pin, setPin] = useState('');
  const [scanStatus, setScanStatus] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [scanResults, setScanResults] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!pin.trim()) {
      toast.error('Lütfen PIN kodunu girin');
      return;
    }

    setIsLoading(true);
    setScanStatus('pending');
    setScanResults(null);

    try {
      const response = await axios.post('/api/verify-pin', { pin: pin.trim() });
      
      if (response.data.message.includes('başlatıldı')) {
        toast.success('PIN doğrulandı! Tarama başlatıldı.');
        setScanStatus('pending');
        
        setTimeout(() => {
          setScanStatus('completed');
          setScanResults({
            data: {
              riskLevel: 'Düşük',
              securityChecks: {
                malware: 'Temiz',
                suspiciousActivity: 'Tespit edilmedi'
              },
              recommendations: ['Sistem güncel', 'Antivirus aktif']
            },
            completedAt: new Date()
          });
          toast.success('Tarama tamamlandı!');
        }, 3000);
      }
    } catch (error) {
      toast.error(error.response?.data?.message || 'Bir hata oluştu');
      setScanStatus('error');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'url(/background.jpg) center/cover no-repeat, linear-gradient(135deg, #ff0000 0%, #cc0000 100%)',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1
      }}>
        {Array.from({ length: 50 }).map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: '2px',
              height: `${Math.random() * 100 + 50}px`,
              background: 'linear-gradient(to bottom, rgba(0,0,0,0.8), rgba(0,0,0,0.3))',
              animation: `rain ${Math.random() * 2 + 1}s linear infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
          
          @keyframes voidPulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.05); }
            100% { transform: scale(1); }
          }
        `}
      </style>

      <div style={{
        textAlign: 'center',
        marginBottom: '60px',
        zIndex: 2
      }}>
        <div style={{
          fontSize: '8rem',
          fontWeight: '100',
          color: '#fff',
          fontFamily: 'Georgia, serif',
          letterSpacing: '15px',
          textTransform: 'uppercase',
          textShadow: '4px 4px 8px rgba(0, 0, 0, 0.8), 8px 8px 16px rgba(0, 0, 0, 0.6)',
          transform: 'rotate(-1deg)',
          marginBottom: '20px',
          animation: 'voidPulse 3s ease-in-out infinite',
          position: 'relative',
          background: 'linear-gradient(45deg, #fff 0%, #ff0000 50%, #fff 100%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text',
          filter: 'drop-shadow(0 0 5px rgba(255, 0, 0, 0.5))',
          fontStyle: 'italic',
          textStroke: '1px rgba(255, 255, 255, 0.3)',
          WebkitTextStroke: '1px rgba(255, 255, 255, 0.3)'
        }}>
          VOID
        </div>
        <div style={{
          fontSize: '1.5rem',
          color: 'rgba(255, 255, 255, 0.8)',
          textShadow: '1px 1px 2px rgba(0,0,0,0.5)',
          letterSpacing: '4px',
          fontWeight: '300',
          marginBottom: '10px'
        }}>
          ADVANCED SECURITY SCANNER
        </div>
        <div style={{
          fontSize: '1rem',
          color: 'rgba(255, 255, 255, 0.6)',
          textShadow: '1px 1px 2px rgba(0,0,0,0.5)',
          letterSpacing: '2px',
          fontWeight: '300'
        }}>
          Professional Anti-Cheat Detection System
        </div>
      </div>

      <div style={{
        background: 'rgba(255, 255, 255, 0.1)',
        backdropFilter: 'blur(15px)',
        borderRadius: '20px',
        padding: '40px',
        boxShadow: '0 25px 50px rgba(0, 0, 0, 0.4)',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        maxWidth: '500px',
        width: '100%',
        position: 'relative',
        zIndex: 2
      }}>
        <form onSubmit={handleSubmit} style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '30px'
        }}>
          <div>
            <label style={{
              display: 'block',
              color: 'rgba(255, 255, 255, 0.9)',
              fontSize: '1.1rem',
              marginBottom: '15px',
              textShadow: '1px 1px 2px rgba(0,0,0,0.5)',
              letterSpacing: '1px'
            }}>
              SECURITY PIN
            </label>
            <input
              type="text"
              placeholder="Enter your security PIN"
              value={pin}
              onChange={(e) => setPin(e.target.value.toUpperCase())}
              maxLength="8"
              disabled={isLoading}
              style={{
                width: '100%',
                padding: '20px',
                border: '2px solid rgba(255, 255, 255, 0.3)',
                borderRadius: '10px',
                fontSize: '18px',
                background: 'rgba(255, 255, 255, 0.1)',
                color: '#fff',
                backdropFilter: 'blur(10px)',
                transition: 'all 0.3s ease',
                textAlign: 'center',
                letterSpacing: '3px',
                fontWeight: '600'
              }}
            />
          </div>
          
          <button type="submit" disabled={isLoading} style={{
            background: 'linear-gradient(135deg, #ff0000 0%, #cc0000 100%)',
            color: 'white',
            border: 'none',
            padding: '20px',
            borderRadius: '10px',
            fontSize: '18px',
            fontWeight: '700',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            boxShadow: '0 15px 30px rgba(255, 0, 0, 0.3)',
            textTransform: 'uppercase',
            letterSpacing: '2px',
            position: 'relative',
            overflow: 'hidden'
          }}>
            {isLoading ? 'SCANNING...' : 'START SCAN'}
          </button>
        </form>

        {scanStatus && (
          <div style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(15px)',
            borderRadius: '15px',
            padding: '30px',
            marginTop: '30px',
            borderLeft: `4px solid ${scanStatus === 'completed' ? '#28a745' : scanStatus === 'pending' ? '#ffc107' : '#dc3545'}`,
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }}>
            <h3 style={{ margin: '0 0 20px 0', color: '#fff', textShadow: '1px 1px 2px rgba(0,0,0,0.5)', fontSize: '1.4rem', textAlign: 'center' }}>
              {scanStatus === 'pending' && 'SCAN IN PROGRESS'}
              {scanStatus === 'completed' && 'SCAN COMPLETED'}
              {scanStatus === 'error' && 'SCAN FAILED'}
            </h3>
            
            {scanStatus === 'pending' && (
              <div style={{ textAlign: 'center' }}>
                <p style={{ margin: '10px 0', color: 'rgba(255, 255, 255, 0.9)', fontSize: '1.1rem' }}>
                  System analysis in progress...
                </p>
                <p style={{ margin: '5px 0', color: 'rgba(255, 255, 255, 0.7)', fontSize: '0.9rem' }}>
                  Estimated time: 3 minutes
                </p>
              </div>
            )}
            
            {scanStatus === 'completed' && scanResults && (
              <div>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginBottom: '20px' }}>
                  <div style={{ textAlign: 'center', padding: '15px', background: 'rgba(255, 255, 255, 0.05)', borderRadius: '10px' }}>
                    <div style={{ color: '#fff', fontSize: '1.2rem', fontWeight: 'bold' }}>RISK LEVEL</div>
                    <div style={{ color: '#28a745', fontSize: '1.1rem', marginTop: '5px' }}>{scanResults.data.riskLevel}</div>
                  </div>
                  <div style={{ textAlign: 'center', padding: '15px', background: 'rgba(255, 255, 255, 0.05)', borderRadius: '10px' }}>
                    <div style={{ color: '#fff', fontSize: '1.2rem', fontWeight: 'bold' }}>MALWARE</div>
                    <div style={{ color: '#28a745', fontSize: '1.1rem', marginTop: '5px' }}>{scanResults.data.securityChecks.malware}</div>
                  </div>
                </div>
                
                <div style={{ marginBottom: '20px' }}>
                  <div style={{ color: '#fff', fontSize: '1.1rem', fontWeight: 'bold', marginBottom: '10px' }}>SECURITY REPORT</div>
                  <p style={{ margin: '8px 0', color: 'rgba(255, 255, 255, 0.9)', fontSize: '1rem' }}>
                    <strong>Suspicious Activity:</strong> {scanResults.data.securityChecks.suspiciousActivity}
                  </p>
                  <p style={{ margin: '8px 0', color: 'rgba(255, 255, 255, 0.9)', fontSize: '1rem' }}>
                    <strong>Scan Date:</strong> {new Date(scanResults.completedAt).toLocaleString('tr-TR')}
                  </p>
                </div>
                
                {scanResults.data.recommendations && (
                  <div>
                    <div style={{ color: '#fff', fontSize: '1.1rem', fontWeight: 'bold', marginBottom: '10px' }}>RECOMMENDATIONS</div>
                    <ul style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '1rem', paddingLeft: '20px' }}>
                      {scanResults.data.recommendations.map((rec, index) => (
                        <li key={index} style={{ marginBottom: '5px' }}>{rec}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>
      
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
        }}
      />
    </div>
  );
}

export default App;
