import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import toast, { Toaster } from 'react-hot-toast';

// Styled Components
const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
`;

const Card = styled.div`
  background: white;
  border-radius: 20px;
  padding: 40px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  max-width: 500px;
  width: 100%;
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
  margin-bottom: 30px;
  font-size: 2.5rem;
  font-weight: 700;
`;

const Subtitle = styled.p`
  text-align: center;
  color: #666;
  margin-bottom: 40px;
  font-size: 1.1rem;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const Input = styled.input`
  padding: 15px;
  border: 2px solid #e1e5e9;
  border-radius: 10px;
  font-size: 16px;
  transition: border-color 0.3s ease;
  
  &:focus {
    outline: none;
    border-color: #667eea;
  }
`;

const Button = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 15px;
  border-radius: 10px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: transform 0.2s ease;
  
  &:hover {
    transform: translateY(-2px);
  }
  
  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const StatusCard = styled.div`
  background: #f8f9fa;
  border-radius: 10px;
  padding: 20px;
  margin-top: 20px;
  border-left: 4px solid ${props => 
    props.status === 'completed' ? '#28a745' : 
    props.status === 'pending' ? '#ffc107' : '#dc3545'
  };
`;

const StatusTitle = styled.h3`
  margin: 0 0 10px 0;
  color: #333;
`;

const StatusText = styled.p`
  margin: 5px 0;
  color: #666;
`;

const LoadingSpinner = styled.div`
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 3px solid #f3f3f3;
  border-top: 3px solid #667eea;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

function App() {
  const [pin, setPin] = useState('');
  const [scanStatus, setScanStatus] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [scanResults, setScanResults] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!pin.trim()) {
      toast.error('L√ºtfen PIN kodunu girin');
      return;
    }

    setIsLoading(true);
    setScanStatus('pending');
    setScanResults(null);

    try {
      const response = await axios.post('/api/verify-pin', { pin: pin.trim() });
      
      if (response.data.message.includes('ba≈ülatƒ±ldƒ±')) {
        toast.success('PIN doƒürulandƒ±! Tarama ba≈ülatƒ±ldƒ±.');
        setScanStatus('pending');
        
        // Tarama durumunu kontrol et
        setTimeout(() => {
          setScanStatus('completed');
          setScanResults({
            data: {
              riskLevel: 'D√º≈ü√ºk',
              securityChecks: {
                malware: 'Temiz',
                suspiciousActivity: 'Tespit edilmedi'
              },
              recommendations: ['Sistem g√ºncel', 'Antivirus aktif']
            },
            completedAt: new Date()
          });
          toast.success('Tarama tamamlandƒ±!');
        }, 3000);
      }
    } catch (error) {
      toast.error(error.response?.data?.message || 'Bir hata olu≈ütu');
      setScanStatus('error');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Container>
      <Card>
        <Title>üîç Void Scanner</Title>
        <Subtitle>
          G√ºvenlik taramasƒ± i√ßin PIN kodunuzu girin
        </Subtitle>
        
        <Form onSubmit={handleSubmit}>
          <Input
            type="text"
            placeholder="PIN Kodunuzu Girin"
            value={pin}
            onChange={(e) => setPin(e.target.value.toUpperCase())}
            maxLength="8"
            disabled={isLoading}
          />
          
          <Button type="submit" disabled={isLoading}>
            {isLoading ? (
              <>
                <LoadingSpinner /> Tarama Ba≈ülatƒ±lƒ±yor...
              </>
            ) : (
              'Tarama Ba≈ülat'
            )}
          </Button>
        </Form>

        {scanStatus && (
          <StatusCard status={scanStatus}>
            <StatusTitle>
              {scanStatus === 'pending' && 'üîÑ Tarama Devam Ediyor'}
              {scanStatus === 'completed' && '‚úÖ Tarama Tamamlandƒ±'}
              {scanStatus === 'error' && '‚ùå Tarama Ba≈üarƒ±sƒ±z'}
            </StatusTitle>
            
            {scanStatus === 'pending' && (
              <StatusText>
                Sisteminiz tarama altƒ±nda. L√ºtfen bekleyin...
                <br />
                <small>Tahmini s√ºre: 3 dakika</small>
              </StatusText>
            )}
            
            {scanStatus === 'completed' && scanResults && (
              <div>
                <StatusText>
                  <strong>Risk Seviyesi:</strong> {scanResults.data.riskLevel}
                </StatusText>
                <StatusText>
                  <strong>Malware Durumu:</strong> {scanResults.data.securityChecks.malware}
                </StatusText>
                <StatusText>
                  <strong>≈û√ºpheli Aktivite:</strong> {scanResults.data.securityChecks.suspiciousActivity}
                </StatusText>
                <StatusText>
                  <strong>Tarama Tarihi:</strong> {new Date(scanResults.completedAt).toLocaleString('tr-TR')}
                </StatusText>
                
                {scanResults.data.recommendations && (
                  <div>
                    <strong>√ñneriler:</strong>
                    <ul>
                      {scanResults.data.recommendations.map((rec, index) => (
                        <li key={index}>{rec}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}
          </StatusCard>
        )}
      </Card>
      
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
        }}
      />
    </Container>
  );
}

export default App;
