import React, { useState } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const PinEntryPage = () => {
  const [pin, setPin] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isVerified, setIsVerified] = useState(false);

  const handlePinSubmit = async (e) => {
    e.preventDefault();
    if (!pin || pin.length !== 8) {
      toast.error('PIN 8 karakter olmalÄ±!');
      return;
    }

    setIsLoading(true);
    try {
      const response = await axios.post('/api/verify-pin', {
        pin: pin.toUpperCase()
      });

      if (response.data.valid) {
        setIsVerified(true);
        toast.success('PIN doÄŸrulandÄ±! Scanner indirilebilir.');
      } else {
        toast.error('GeÃ§ersiz PIN!');
      }
    } catch (error) {
      console.error('PIN Verification Error:', error);
      if (error.code === 'ECONNREFUSED') {
        toast.error('Backend sunucusu Ã§alÄ±ÅŸmÄ±yor!');
      } else if (error.response?.status === 400) {
        toast.error(error.response.data.message || 'GeÃ§ersiz PIN!');
      } else {
        toast.error('PIN doÄŸrulanamadÄ±!');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const downloadScanner = async () => {
    try {
      console.log('Download baÅŸlatÄ±lÄ±yor...');
      const response = await fetch('http://localhost:5001/api/download-scanner');
      console.log('Response status:', response.status);
      
      if (!response.ok) {
        throw new Error(`Download failed: ${response.status}`);
      }
      
      const blob = await response.blob();
      console.log('Blob oluÅŸturuldu, boyut:', blob.size);
      
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'VoidScanner.exe';
      link.style.display = 'none';
      document.body.appendChild(link);
      
      console.log('Link tÄ±klanÄ±yor...');
      link.click();
      
      setTimeout(() => {
        document.body.removeChild(link);
        window.URL.revokeObjectURL(url);
      }, 100);
      
      toast.success('Scanner baÅŸarÄ±yla indirildi!');
    } catch (error) {
      console.error('Download Error:', error);
      toast.error(`Scanner indirilemedi: ${error.message}`);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0f0f0f 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Subtle Rain Effect */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1
      }}>
        {Array.from({ length: 15 }).map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: '1px',
              height: `${Math.random() * 50 + 30}px`,
              background: 'linear-gradient(to bottom, rgba(220, 38, 38, 0.3), rgba(220, 38, 38, 0.1))',
              animation: `rain ${Math.random() * 3 + 2}s linear infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
        `}
      </style>

      {/* Main Container */}
      <div style={{
        background: 'rgba(20, 20, 20, 0.8)',
        backdropFilter: 'blur(20px)',
        borderRadius: '15px',
        padding: '40px',
        width: '100%',
        maxWidth: '500px',
        border: '1px solid rgba(220, 38, 38, 0.2)',
        position: 'relative',
        zIndex: 2,
        boxShadow: '0 20px 60px rgba(0, 0, 0, 0.5)'
      }}>
        
        {/* Header */}
        <div style={{
          textAlign: 'center',
          marginBottom: '40px'
        }}>
          <h1 style={{
            color: '#ffffff',
            fontSize: '2.5rem',
            margin: '0 0 10px 0',
            textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
            letterSpacing: '4px',
            fontWeight: '700',
            background: 'linear-gradient(135deg, #ffffff 0%, #dc2626 100%)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            VOID SCANNER
          </h1>
          <p style={{
            color: 'rgba(255, 255, 255, 0.7)',
            fontSize: '1.1rem',
            margin: '0',
            fontWeight: '300'
          }}>
            Advanced Security Scanner
          </p>
        </div>

        {!isVerified ? (
          /* PIN Entry Form */
          <div>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              textAlign: 'center',
              fontWeight: '600'
            }}>
              PIN Kodunu Girin
            </h2>

            <form onSubmit={handlePinSubmit}>
              <div style={{
                marginBottom: '30px'
              }}>
                <input
                  type="text"
                  value={pin}
