import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const BanPage = () => {
  const location = useLocation();
  const [banInfo, setBanInfo] = useState(null);

  useEffect(() => {
    // URL'den ban bilgilerini al
    const searchParams = new URLSearchParams(location.search);
    const reason = searchParams.get('reason') || 'No reason provided';
    const bannedAt = searchParams.get('bannedAt') || new Date().toISOString();
    
    setBanInfo({
      reason,
      bannedAt
    });
  }, [location]);

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return 'Unknown date';
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
      color: '#ffffff',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      position: 'relative',
      overflow: 'hidden',
      fontFamily: 'Courier New, monospace'
    }}>
      {/* Background Effects */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: `
          radial-gradient(circle at 20% 80%, rgba(220, 38, 38, 0.1) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(220, 38, 38, 0.1) 0%, transparent 50%),
          radial-gradient(circle at 40% 40%, rgba(220, 38, 38, 0.05) 0%, transparent 50%)
        `,
        zIndex: 1
      }} />
      
      {/* Animated particles */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        zIndex: 1
      }}>
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              width: '2px',
              height: '2px',
              background: 'rgba(220, 38, 38, 0.6)',
              borderRadius: '50%',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animation: `float ${3 + Math.random() * 4}s ease-in-out infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>

      {/* Main Content */}
      <div style={{
        background: 'rgba(0, 0, 0, 0.8)',
        backdropFilter: 'blur(20px)',
        borderRadius: '20px',
        padding: '60px 40px',
        border: '2px solid rgba(220, 38, 38, 0.3)',
        boxShadow: '0 20px 60px rgba(0, 0, 0, 0.5), 0 0 100px rgba(220, 38, 38, 0.1)',
        textAlign: 'center',
        maxWidth: '600px',
        width: '90%',
        position: 'relative',
        zIndex: 2
      }}>
        {/* Warning Icon */}
        <div style={{
          fontSize: '4rem',
          color: '#ef4444',
          marginBottom: '30px',
          textShadow: '0 0 20px rgba(239, 68, 68, 0.5)'
        }}>
          ⚠️
        </div>

        {/* Title */}
        <h1 style={{
          fontSize: '2.5rem',
          fontWeight: '700',
          color: '#ffffff',
          marginBottom: '20px',
          textShadow: '0 0 20px rgba(220, 38, 38, 0.3)',
          letterSpacing: '2px'
        }}>
          ACCOUNT BANNED
        </h1>

        {/* Subtitle */}
        <p style={{
          fontSize: '1.2rem',
          color: 'rgba(255, 255, 255, 0.8)',
          marginBottom: '40px',
          lineHeight: '1.6'
        }}>
          This account has been permanently banned
        </p>

        {/* Ban Details */}
        {banInfo && (
          <div style={{
            background: 'rgba(220, 38, 38, 0.1)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            marginBottom: '40px'
          }}>
            <h3 style={{
              fontSize: '1.3rem',
              color: '#ef4444',
              marginBottom: '20px',
              fontWeight: '600'
            }}>
              Ban Details
            </h3>
            
            <div style={{
              textAlign: 'left',
              color: 'rgba(255, 255, 255, 0.9)',
              lineHeight: '1.8'
            }}>
              <div style={{ marginBottom: '15px' }}>
                <strong style={{ color: '#ef4444' }}>Reason:</strong>
                <div style={{ 
                  marginTop: '5px',
                  padding: '10px',
                  background: 'rgba(0, 0, 0, 0.3)',
                  borderRadius: '8px',
                  border: '1px solid rgba(220, 38, 38, 0.2)'
                }}>
                  {banInfo.reason}
                </div>
              </div>
              
              <div>
                <strong style={{ color: '#ef4444' }}>Banned On:</strong>
                <div style={{ 
                  marginTop: '5px',
                  padding: '10px',
                  background: 'rgba(0, 0, 0, 0.3)',
                  borderRadius: '8px',
                  border: '1px solid rgba(220, 38, 38, 0.2)'
                }}>
                  {formatDate(banInfo.bannedAt)}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Appeal Information */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.6)',
          borderRadius: '15px',
          padding: '30px',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          marginBottom: '30px'
        }}>
          <h3 style={{
            fontSize: '1.2rem',
            color: '#ffffff',
            marginBottom: '15px',
            fontWeight: '600'
          }}>
            Want to Appeal?
          </h3>
          <p style={{
            color: 'rgba(255, 255, 255, 0.8)',
            lineHeight: '1.6',
            marginBottom: '20px'
          }}>
            If you believe this ban was issued in error, you can appeal by opening a ticket in our Discord server.
          </p>
          
          <a
            href="https://discord.gg/NPDrBmvU"
            target="_blank"
            rel="noopener noreferrer"
            style={{
              display: 'inline-block',
              background: 'linear-gradient(135deg, #5865f2 0%, #4752c4 100%)',
              color: '#ffffff',
              textDecoration: 'none',
              padding: '15px 30px',
              borderRadius: '10px',
              fontWeight: '600',
              fontSize: '1rem',
              transition: 'all 0.3s ease',
              boxShadow: '0 4px 15px rgba(88, 101, 242, 0.3)',
              border: '1px solid rgba(88, 101, 242, 0.4)'
            }}
            onMouseOver={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 8px 25px rgba(88, 101, 242, 0.5)';
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 4px 15px rgba(88, 101, 242, 0.3)';
            }}
          >
            Open Discord Ticket
          </a>
        </div>

        {/* Footer */}
        <div style={{
          color: 'rgba(255, 255, 255, 0.5)',
          fontSize: '0.9rem',
          borderTop: '1px solid rgba(255, 255, 255, 0.1)',
          paddingTop: '20px'
        }}>
          Void Scanner Security System
        </div>
      </div>

      {/* CSS Animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(180deg); }
        }
      `}</style>
    </div>
  );
};

export default BanPage;
