import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const Dashboard = ({ user, onLogout }) => {
  const [pins, setPins] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedPin, setSelectedPin] = useState(null);
  const [showPinDetails, setShowPinDetails] = useState(false);
  const [scanResults, setScanResults] = useState(null);
  const [screenshot, setScreenshot] = useState(null);

  useEffect(() => {
    fetchPins();
  }, []);

  const fetchPins = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/my-pins', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPins(response.data);
    } catch (error) {
      console.error('Fetch Pins Error:', error);
      if (error.code === 'ECONNREFUSED') {
        toast.error('Backend sunucusu çalışmıyor!');
      } else {
        toast.error('PIN\'ler yüklenemedi!');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const createRandomPin = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('/api/create-pin', {
        targetInfo: {
          name: 'Random Target',
          email: '',
          phone: '',
          notes: 'Random PIN'
        },
        durationHours: 24
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      toast.success(`PIN oluşturuldu: ${response.data.pin}`);
      fetchPins();
    } catch (error) {
      console.error('Create Pin Error:', error);
      if (error.code === 'ECONNREFUSED') {
        toast.error('Backend sunucusu çalışmıyor!');
      } else {
        toast.error(error.response?.data?.message || 'PIN oluşturulamadı!');
      }
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#10b981';
      case 'used': return '#f59e0b';
      case 'expired': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'active': return 'Aktif';
      case 'used': return 'Kullanıldı';
      case 'expired': return 'Süresi Doldu';
      default: return 'Bilinmiyor';
    }
  };

  const getRiskColor = (riskLevel) => {
    switch (riskLevel) {
      case 'Yüksek': return '#ef4444';
      case 'Orta': return '#f59e0b';
      case 'Düşük': return '#10b981';
      case 'Çok Düşük': return '#3b82f6';
      default: return '#6b7280';
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleString('tr-TR');
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Panoya kopyalandı!');
  };

  const viewPinDetails = async (pin) => {
    try {
      setSelectedPin(pin);
      setShowPinDetails(true);
      
      // Tarama sonuçlarını getir
      const token = localStorage.getItem('token');
      const response = await axios.get(`/api/pin-details/${pin.pin}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      if (response.data.scanResults) {
        setScanResults(response.data.scanResults);
      }
      
      // Ekran görüntüsünü getir
      if (response.data.screenshotPath) {
        try {
          const screenshotResponse = await axios.get(`/api/screenshot/${pin.pin}`, {
            responseType: 'blob',
            headers: { Authorization: `Bearer ${token}` }
          });
          const imageUrl = URL.createObjectURL(screenshotResponse.data);
          setScreenshot(imageUrl);
        } catch (screenshotError) {
          console.log('Ekran görüntüsü bulunamadı');
        }
      }
      
    } catch (error) {
      console.error('PIN detayları yüklenemedi:', error);
      toast.error('PIN detayları yüklenemedi!');
    }
  };

  const parseScanResults = (results) => {
    if (!results) return null;
    
    const lines = results.split('\n');
    const sections = {
      suspiciousActivities: [],
      deletedFiles: [],
      recentApps: [],
      unlicensedApps: [],
      screenshot: null
    };
    
    let currentSection = null;
    
    lines.forEach(line => {
      line = line.trim();
      
      if (line.includes('ŞÜPHELİ AKTİVİTELER')) {
        currentSection = 'suspiciousActivities';
      } else if (line.includes('SON SİLİNEN DOSYALAR')) {
        currentSection = 'deletedFiles';
      } else if (line.includes('SON ÇALIŞTIRILAN UYGULAMALAR')) {
        currentSection = 'recentApps';
      } else if (line.includes('LİSANSSIZ UYGULAMALAR')) {
        currentSection = 'unlicensedApps';
      } else if (line.includes('EKRAN GÖRÜNTÜSÜ')) {
        currentSection = 'screenshot';
      } else if (line && currentSection && line.startsWith('⚠️')) {
        sections.suspiciousActivities.push(line.replace('⚠️ ', ''));
      } else if (line && currentSection && line.startsWith('📁')) {
        sections.deletedFiles.push(line.replace('📁 ', ''));
      } else if (line && currentSection && line.startsWith('💻')) {
        sections.recentApps.push(line.replace('💻 ', ''));
      } else if (line && currentSection && line.startsWith('🔓')) {
        sections.unlicensedApps.push(line.replace('🔓 ', ''));
      } else if (line && currentSection === 'screenshot' && line.includes('✅')) {
        sections.screenshot = line.replace('✅ Ekran görüntüsü alındı: ', '');
      }
    });
    
    return sections;
  };

  const openPinDetails = (pin) => {
    setSelectedPin(pin);
    setShowPinDetails(true);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0f0f0f 100%)',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Subtle Rain Effect */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1
      }}>
        {Array.from({ length: 15 }).map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: '1px',
              height: `${Math.random() * 50 + 30}px`,
              background: 'linear-gradient(to bottom, rgba(220, 38, 38, 0.3), rgba(220, 38, 38, 0.1))',
              animation: `rain ${Math.random() * 3 + 2}s linear infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
        `}
      </style>

      {/* Header */}
      <div style={{
        background: 'rgba(20, 20, 20, 0.8)',
        backdropFilter: 'blur(20px)',
        borderRadius: '12px',
        padding: '24px',
        marginBottom: '24px',
        border: '1px solid rgba(220, 38, 38, 0.2)',
        position: 'relative',
        zIndex: 2,
        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          flexWrap: 'wrap'
        }}>
          <div>
            <h1 style={{
              color: '#ffffff',
              fontSize: '2.2rem',
              margin: '0 0 8px 0',
              textShadow: '0 0 20px rgba(220, 38, 38, 0.5)',
              letterSpacing: '3px',
              fontWeight: '700',
              background: 'linear-gradient(135deg, #ffffff 0%, #dc2626 100%)',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}>
              VOID SCANNER
            </h1>
            <p style={{
              color: 'rgba(255, 255, 255, 0.7)',
              margin: '0',
              fontSize: '1.1rem',
              fontWeight: '300'
            }}>
              Hoş geldin, <span style={{color: '#dc2626', fontWeight: '600'}}>{user.name}</span>!
            </p>
          </div>
          <button
            onClick={onLogout}
            style={{
              background: 'linear-gradient(135deg, #dc2626 0%, #991b1b 100%)',
              color: 'white',
              border: 'none',
              padding: '12px 24px',
              borderRadius: '8px',
              fontSize: '14px',
              fontWeight: '600',
              cursor: 'pointer',
              boxShadow: '0 4px 12px rgba(220, 38, 38, 0.3)',
              textTransform: 'uppercase',
              letterSpacing: '1px',
              transition: 'all 0.3s ease',
              border: '1px solid rgba(220, 38, 38, 0.3)'
            }}
            onMouseOver={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 6px 16px rgba(220, 38, 38, 0.4)';
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 4px 12px rgba(220, 38, 38, 0.3)';
            }}
          >
            Çıkış
          </button>
        </div>
      </div>

      {/* Main Content - 3 Column Layout */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: '300px 1fr 300px',
        gap: '20px',
        position: 'relative',
        zIndex: 2
      }}>
        
        {/* LEFT SIDE - Membership Info */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.8)',
          backdropFilter: 'blur(20px)',
          borderRadius: '12px',
          padding: '24px',
          border: '1px solid rgba(220, 38, 38, 0.2)',
          height: 'fit-content',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
        }}>
          <h3 style={{
            color: '#ffffff',
            fontSize: '1.4rem',
            margin: '0 0 24px 0',
            textAlign: 'center',
            textShadow: '0 0 10px rgba(220, 38, 38, 0.3)',
            fontWeight: '600',
            letterSpacing: '1px'
          }}>
            Üyelik Bilgileri
          </h3>
          
          <div style={{
            background: 'rgba(220, 38, 38, 0.1)',
            borderRadius: '10px',
            padding: '18px',
            marginBottom: '16px',
            border: '1px solid rgba(220, 38, 38, 0.2)',
            transition: 'all 0.3s ease'
          }}>
            <div style={{
              color: '#dc2626',
              fontSize: '1.2rem',
              fontWeight: '700',
              marginBottom: '6px',
              textShadow: '0 0 8px rgba(220, 38, 38, 0.3)'
            }}>
              ACMaster
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '14px',
              fontWeight: '400'
            }}>
              15 Kişilik Slot
            </div>
          </div>

          <div style={{
            background: 'rgba(16, 185, 129, 0.1)',
            borderRadius: '10px',
            padding: '18px',
            marginBottom: '16px',
            border: '1px solid rgba(16, 185, 129, 0.2)'
          }}>
            <div style={{
              color: '#ffffff',
              fontSize: '1rem',
              fontWeight: '600',
              marginBottom: '6px'
            }}>
              Kullanılan PIN'ler
            </div>
            <div style={{
              color: '#10b981',
              fontSize: '1.8rem',
              fontWeight: '700',
              textShadow: '0 0 8px rgba(16, 185, 129, 0.3)'
            }}>
              {pins.length}
            </div>
          </div>

          <div style={{
            background: 'rgba(245, 158, 11, 0.1)',
            borderRadius: '10px',
            padding: '18px',
            marginBottom: '16px',
            border: '1px solid rgba(245, 158, 11, 0.2)'
          }}>
            <div style={{
              color: '#ffffff',
              fontSize: '1rem',
              fontWeight: '600',
              marginBottom: '6px'
            }}>
              Aktif PIN'ler
            </div>
            <div style={{
              color: '#f59e0b',
              fontSize: '1.8rem',
              fontWeight: '700',
              textShadow: '0 0 8px rgba(245, 158, 11, 0.3)'
            }}>
              {pins.filter(pin => pin.status === 'active').length}
            </div>
          </div>

          <div style={{
            background: 'rgba(59, 130, 246, 0.1)',
            borderRadius: '10px',
            padding: '18px',
            border: '1px solid rgba(59, 130, 246, 0.2)'
          }}>
            <div style={{
              color: '#ffffff',
              fontSize: '1rem',
              fontWeight: '600',
              marginBottom: '6px'
            }}>
              Tamamlanan Taramalar
            </div>
            <div style={{
              color: '#3b82f6',
              fontSize: '1.8rem',
              fontWeight: '700',
              textShadow: '0 0 8px rgba(59, 130, 246, 0.3)'
            }}>
              {pins.filter(pin => pin.scanResults?.status === 'completed').length}
            </div>
          </div>
        </div>

        {/* CENTER - PIN List */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.8)',
          backdropFilter: 'blur(20px)',
          borderRadius: '12px',
          padding: '24px',
          border: '1px solid rgba(220, 38, 38, 0.2)',
          maxHeight: '70vh',
          overflow: 'auto',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
        }}>
          <h2 style={{
            color: '#ffffff',
            fontSize: '1.6rem',
            margin: '0 0 24px 0',
            textShadow: '0 0 10px rgba(220, 38, 38, 0.3)',
            textAlign: 'center',
            fontWeight: '600',
            letterSpacing: '1px'
          }}>
            PIN'lerim ({pins.length})
          </h2>

          {isLoading ? (
            <div style={{
              textAlign: 'center',
              color: 'rgba(255, 255, 255, 0.7)',
              padding: '40px',
              fontSize: '1.1rem'
            }}>
              Yükleniyor...
            </div>
          ) : pins.length === 0 ? (
            <div style={{
              textAlign: 'center',
              color: 'rgba(255, 255, 255, 0.7)',
              padding: '40px',
              fontSize: '1.1rem'
            }}>
              Henüz PIN oluşturmadınız.
            </div>
          ) : (
            <div style={{
              display: 'flex',
              flexDirection: 'column',
              gap: '12px'
            }}>
              {pins.map((pin) => (
                <div
                  key={pin._id}
                  style={{
                    background: 'rgba(255, 255, 255, 0.05)',
                    borderRadius: '10px',
                    padding: '18px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    borderLeft: `4px solid ${getStatusColor(pin.status)}`
                  }}
                  onClick={() => viewPinDetails(pin)}
                  onMouseOver={(e) => {
                    e.target.style.background = 'rgba(255, 255, 255, 0.08)';
                    e.target.style.transform = 'translateX(4px)';
                    e.target.style.boxShadow = '0 4px 16px rgba(0, 0, 0, 0.2)';
                  }}
                  onMouseOut={(e) => {
                    e.target.style.background = 'rgba(255, 255, 255, 0.05)';
                    e.target.style.transform = 'translateX(0)';
                    e.target.style.boxShadow = 'none';
                  }}
                >
                  <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginBottom: '12px'
                  }}>
                    <div style={{
                      fontSize: '1.3rem',
                      fontWeight: '700',
                      color: '#ffffff',
                      fontFamily: 'monospace',
                      letterSpacing: '2px',
                      textShadow: '0 0 8px rgba(220, 38, 38, 0.3)'
                    }}>
                      {pin.pin}
                    </div>
                    <div style={{
                      padding: '6px 12px',
                      borderRadius: '6px',
                      fontSize: '12px',
                      fontWeight: '600',
                      color: '#ffffff',
                      background: getStatusColor(pin.status),
                      textTransform: 'uppercase',
                      letterSpacing: '1px'
                    }}>
                      {getStatusText(pin.status)}
                    </div>
                  </div>

                  <div style={{
                    fontSize: '14px',
                    color: 'rgba(255, 255, 255, 0.8)',
                    marginBottom: '6px',
                    fontWeight: '500'
                  }}>
                    <strong>Hedef:</strong> {pin.targetInfo?.name || 'Belirtilmemiş'}
                  </div>

                  <div style={{
                    fontSize: '12px',
                    color: 'rgba(255, 255, 255, 0.6)',
                    marginBottom: '6px'
                  }}>
                    <strong>Oluşturulma:</strong> {formatDate(pin.createdAt)}
                  </div>

                  {pin.scanResults?.status === 'completed' && (
                    <div style={{
                      fontSize: '12px',
                      color: 'rgba(255, 255, 255, 0.8)',
                      marginBottom: '6px'
                    }}>
                      <strong>Risk:</strong> 
                      <span style={{
                        color: getRiskColor(pin.scanResults.riskLevel),
                        fontWeight: '700',
                        marginLeft: '5px'
                      }}>
                        {pin.scanResults.riskLevel}
                      </span>
                    </div>
                  )}

                  <div style={{
                    fontSize: '11px',
                    color: 'rgba(255, 255, 255, 0.4)',
                    textAlign: 'right',
                    fontStyle: 'italic'
                  }}>
                    Detayları görmek için tıklayın →
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* RIGHT SIDE - PIN Creation */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.8)',
          backdropFilter: 'blur(20px)',
          borderRadius: '12px',
          padding: '24px',
          border: '1px solid rgba(220, 38, 38, 0.2)',
          height: 'fit-content',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
        }}>
          <h3 style={{
            color: '#ffffff',
            fontSize: '1.4rem',
            margin: '0 0 24px 0',
            textAlign: 'center',
            textShadow: '0 0 10px rgba(220, 38, 38, 0.3)',
            fontWeight: '600',
            letterSpacing: '1px'
          }}>
            PIN Oluştur
          </h3>

          <div style={{
            background: 'rgba(16, 185, 129, 0.1)',
            borderRadius: '10px',
            padding: '20px',
            marginBottom: '16px',
            textAlign: 'center',
            border: '1px solid rgba(16, 185, 129, 0.2)'
          }}>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '14px',
              marginBottom: '16px',
              fontWeight: '400'
            }}>
              Otomatik random PIN oluşturulacak
            </div>
            
            <button
              onClick={createRandomPin}
              style={{
                background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                color: 'white',
                border: 'none',
                padding: '16px 28px',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: 'pointer',
                boxShadow: '0 4px 12px rgba(16, 185, 129, 0.3)',
                textTransform: 'uppercase',
                letterSpacing: '1px',
                width: '100%',
                transition: 'all 0.3s ease',
                border: '1px solid rgba(16, 185, 129, 0.3)'
              }}
              onMouseOver={(e) => {
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 6px 16px rgba(16, 185, 129, 0.4)';
              }}
              onMouseOut={(e) => {
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = '0 4px 12px rgba(16, 185, 129, 0.3)';
              }}
            >
              + Yeni PIN Oluştur
            </button>
          </div>

          <div style={{
            background: 'rgba(255, 255, 255, 0.05)',
            borderRadius: '10px',
            padding: '16px',
            marginBottom: '16px',
            border: '1px solid rgba(255, 255, 255, 0.1)'
          }}>
            <div style={{
              color: '#ffffff',
              fontSize: '14px',
              fontWeight: '600',
              marginBottom: '10px'
            }}>
              PIN Özellikleri:
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.7)',
              fontSize: '12px',
              lineHeight: '1.6'
            }}>
              • 8 karakter uzunluğunda<br/>
              • 24 saat geçerli<br/>
              • Benzersiz kod<br/>
              • Güvenli tarama
            </div>
          </div>

          <div style={{
            background: 'rgba(255, 255, 255, 0.05)',
            borderRadius: '10px',
            padding: '16px',
            border: '1px solid rgba(255, 255, 255, 0.1)'
          }}>
            <div style={{
              color: '#ffffff',
              fontSize: '14px',
              fontWeight: '600',
              marginBottom: '10px'
            }}>
              Nasıl Kullanılır:
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.7)',
              fontSize: '12px',
              lineHeight: '1.6'
            }}>
              1. PIN oluştur<br/>
              2. Hedef kişiye gönder<br/>
              3. Tarama sonuçlarını bekle<br/>
              4. Detayları görüntüle
            </div>
          </div>
        </div>
      </div>

      {/* PIN Details Modal */}
      {showPinDetails && selectedPin && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          background: 'rgba(0, 0, 0, 0.9)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000,
          padding: '20px'
        }}>
          <div style={{
            background: 'rgba(20, 20, 20, 0.95)',
            backdropFilter: 'blur(20px)',
            borderRadius: '15px',
            padding: '30px',
            width: '100%',
            maxWidth: '800px',
            maxHeight: '80vh',
            overflow: 'auto',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 20px 60px rgba(0, 0, 0, 0.5)'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '20px'
            }}>
              <h3 style={{
                color: '#ffffff',
                fontSize: '1.6rem',
                margin: '0',
                fontWeight: '600'
              }}>
                PIN Detayları
              </h3>
              <button
                onClick={() => setShowPinDetails(false)}
                style={{
                  background: 'rgba(255, 255, 255, 0.1)',
                  color: '#fff',
                  border: '1px solid rgba(255, 255, 255, 0.3)',
                  padding: '8px 12px',
                  borderRadius: '6px',
                  fontSize: '14px',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
                onMouseOver={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.2)'}
                onMouseOut={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.1)'}
              >
                ✕
              </button>
            </div>

            <div style={{
              fontSize: '2.2rem',
              fontWeight: '700',
              color: '#ffffff',
              fontFamily: 'monospace',
              letterSpacing: '4px',
              textAlign: 'center',
              marginBottom: '24px',
              padding: '20px',
              background: 'rgba(220, 38, 38, 0.1)',
              borderRadius: '10px',
              border: '1px solid rgba(220, 38, 38, 0.2)',
              textShadow: '0 0 20px rgba(220, 38, 38, 0.5)'
            }}>
              {selectedPin.pin}
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
              gap: '16px',
              marginBottom: '24px'
            }}>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Durum:</strong>
                <div style={{
                  color: getStatusColor(selectedPin.status),
                  fontWeight: '700',
                  fontSize: '1.1rem'
                }}>
                  {getStatusText(selectedPin.status)}
                </div>
              </div>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Hedef:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {selectedPin.targetInfo?.name || 'Belirtilmemiş'}
                </div>
              </div>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Oluşturulma:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {formatDate(selectedPin.createdAt)}
                </div>
              </div>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Son Kullanma:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {formatDate(selectedPin.expiresAt)}
                </div>
              </div>
            </div>

            {scanResults && (
              <div style={{
                background: 'rgba(255, 255, 255, 0.05)',
                borderRadius: '12px',
                padding: '24px',
                marginTop: '24px',
                border: '1px solid rgba(255, 255, 255, 0.1)'
              }}>
                <h4 style={{
                  color: '#ffffff',
                  fontSize: '1.3rem',
                  margin: '0 0 16px 0',
                  fontWeight: '600',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}>
                  🔍 Gerçek Tarama Sonuçları
                </h4>

                {(() => {
                  const parsedResults = parseScanResults(scanResults);
                  if (!parsedResults) return null;

                  return (
                    <div style={{ display: 'grid', gap: '20px' }}>
                      {/* Şüpheli Aktiviteler */}
                      <div style={{
                        background: 'rgba(220, 38, 38, 0.1)',
                        borderRadius: '8px',
                        padding: '16px',
                        border: '1px solid rgba(220, 38, 38, 0.3)'
                      }}>
                        <h5 style={{
                          color: '#ef4444',
                          fontSize: '1.1rem',
                          margin: '0 0 12px 0',
                          fontWeight: '600',
                          display: 'flex',
                          alignItems: 'center',
                          gap: '8px'
                        }}>
                          ⚠️ Şüpheli Aktiviteler ({parsedResults.suspiciousActivities.length})
                        </h5>
                        <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '0.9rem' }}>
                          {parsedResults.suspiciousActivities.length > 0 ? (
                            <ul style={{ margin: 0, paddingLeft: '20px' }}>
                              {parsedResults.suspiciousActivities.map((activity, index) => (
                                <li key={index} style={{ marginBottom: '4px' }}>{activity}</li>
                              ))}
                            </ul>
                          ) : (
                            <div style={{ color: '#10b981' }}>✅ Şüpheli aktivite bulunamadı</div>
                          )}
                        </div>
                      </div>

                      {/* Son Silinen Dosyalar */}
                      <div style={{
                        background: 'rgba(245, 158, 11, 0.1)',
                        borderRadius: '8px',
                        padding: '16px',
                        border: '1px solid rgba(245, 158, 11, 0.3)'
                      }}>
                        <h5 style={{
                          color: '#f59e0b',
                          fontSize: '1.1rem',
                          margin: '0 0 12px 0',
                          fontWeight: '600',
                          display: 'flex',
                          alignItems: 'center',
                          gap: '8px'
                        }}>
                          🗑️ Son Silinen Dosyalar ({parsedResults.deletedFiles.length})
                        </h5>
                        <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '0.9rem' }}>
                          {parsedResults.deletedFiles.length > 0 ? (
                            <ul style={{ margin: 0, paddingLeft: '20px' }}>
                              {parsedResults.deletedFiles.map((file, index) => (
                                <li key={index} style={{ marginBottom: '4px' }}>{file}</li>
                              ))}
                            </ul>
                          ) : (
                            <div style={{ color: '#10b981' }}>✅ Son silinen dosya bulunamadı</div>
                          )}
                        </div>
                      </div>

                      {/* Son Çalıştırılan Uygulamalar */}
                      <div style={{
                        background: 'rgba(59, 130, 246, 0.1)',
                        borderRadius: '8px',
                        padding: '16px',
                        border: '1px solid rgba(59, 130, 246, 0.3)'
                      }}>
                        <h5 style={{
                          color: '#3b82f6',
                          fontSize: '1.1rem',
                          margin: '0 0 12px 0',
                          fontWeight: '600',
                          display: 'flex',
                          alignItems: 'center',
                          gap: '8px'
                        }}>
                          🚀 Son Çalıştırılan Uygulamalar ({parsedResults.recentApps.length})
                        </h5>
                        <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '0.9rem' }}>
                          {parsedResults.recentApps.length > 0 ? (
                            <ul style={{ margin: 0, paddingLeft: '20px' }}>
                              {parsedResults.recentApps.map((app, index) => (
                                <li key={index} style={{ marginBottom: '4px' }}>{app}</li>
                              ))}
                            </ul>
                          ) : (
                            <div style={{ color: '#10b981' }}>✅ Son çalıştırılan uygulama bulunamadı</div>
                          )}
                        </div>
                      </div>

                      {/* Lisanssız Uygulamalar */}
                      <div style={{
                        background: 'rgba(239, 68, 68, 0.1)',
                        borderRadius: '8px',
                        padding: '16px',
                        border: '1px solid rgba(239, 68, 68, 0.3)'
                      }}>
                        <h5 style={{
                          color: '#ef4444',
                          fontSize: '1.1rem',
                          margin: '0 0 12px 0',
                          fontWeight: '600',
                          display: 'flex',
                          alignItems: 'center',
                          gap: '8px'
                        }}>
                          ❌ Lisanssız Uygulamalar ({parsedResults.unlicensedApps.length})
                        </h5>
                        <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '0.9rem' }}>
                          {parsedResults.unlicensedApps.length > 0 ? (
                            <ul style={{ margin: 0, paddingLeft: '20px' }}>
                              {parsedResults.unlicensedApps.map((app, index) => (
                                <li key={index} style={{ marginBottom: '4px' }}>{app}</li>
                              ))}
                            </ul>
                          ) : (
                            <div style={{ color: '#10b981' }}>✅ Lisanssız uygulama bulunamadı</div>
                          )}
                        </div>
                      </div>

                      {/* Ekran Görüntüsü */}
                      {screenshot && (
                        <div style={{
                          background: 'rgba(16, 185, 129, 0.1)',
                          borderRadius: '8px',
                          padding: '16px',
                          border: '1px solid rgba(16, 185, 129, 0.3)'
                        }}>
                          <h5 style={{
                            color: '#10b981',
                            fontSize: '1.1rem',
                            margin: '0 0 12px 0',
                            fontWeight: '600',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '8px'
                          }}>
                            📸 Ekran Görüntüsü
                          </h5>
                          <div style={{
                            borderRadius: '8px',
                            overflow: 'hidden',
                            border: '2px solid rgba(16, 185, 129, 0.3)',
                            maxWidth: '100%'
                          }}>
                            <img 
                              src={screenshot} 
                              alt="Ekran Görüntüsü" 
                              style={{
                                width: '100%',
                                height: 'auto',
                                display: 'block'
                              }}
                            />
                          </div>
                        </div>
                      )}
                    </div>
                  );
                })()}
              </div>
            )}

            <div style={{
              display: 'flex',
              gap: '12px',
              justifyContent: 'center',
              marginTop: '24px'
            }}>
              <button
                onClick={() => copyToClipboard(selectedPin.pin)}
                style={{
                  background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                  color: 'white',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '8px',
                  fontSize: '14px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  transition: 'all 0.3s ease',
                  border: '1px solid rgba(16, 185, 129, 0.3)'
                }}
                onMouseOver={(e) => {
                  e.target.style.transform = 'translateY(-2px)';
                  e.target.style.boxShadow = '0 6px 16px rgba(16, 185, 129, 0.4)';
                }}
                onMouseOut={(e) => {
                  e.target.style.transform = 'translateY(0)';
                  e.target.style.boxShadow = 'none';
                }}
              >
                PIN'i Kopyala
              </button>
            </div>
          </div>
        </div>
      )}
      
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#1f2937',
            color: '#fff',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            borderRadius: '8px'
          },
        }}
      />
    </div>
  );
};

export default Dashboard;
