import React, { useState } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const CreatePinPage = ({ onBack, onPinCreated }) => {
  const [isCreating, setIsCreating] = useState(false);
  const [targetInfo, setTargetInfo] = useState({
    name: '',
    description: '',
    ip: '',
    location: ''
  });

  const handleCreatePin = async () => {
    if (!targetInfo.name.trim()) {
      toast.error('Hedef adı gereklidir!');
      return;
    }

    try {
      setIsCreating(true);
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5001/api/pins', {
        targetInfo: {
          name: targetInfo.name,
          description: targetInfo.description || 'Tarama hedefi',
          ip: targetInfo.ip || 'Bilinmiyor',
          location: targetInfo.location || 'Bilinmiyor'
        }
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      toast.success('PIN başarıyla oluşturuldu!');
      onPinCreated();
      onBack();
    } catch (error) {
      console.error('PIN oluşturulamadı:', error);
      toast.error('PIN oluşturulamadı!');
    } finally {
      setIsCreating(false);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
      color: '#ffffff',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Yağmur efekti */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1
      }}>
        {Array.from({ length: 15 }).map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: '1px',
              height: `${Math.random() * 40 + 20}px`,
              background: 'linear-gradient(to bottom, rgba(220, 38, 38, 0.2), rgba(220, 38, 38, 0.05))',
              animation: `rain ${Math.random() * 3 + 2}s linear infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
        `}
      </style>

      {/* Header */}
      <div style={{
        background: 'rgba(20, 20, 20, 0.9)',
        backdropFilter: 'blur(20px)',
        padding: '20px',
        borderBottom: '1px solid rgba(220, 38, 38, 0.3)',
        position: 'relative',
        zIndex: 2
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          maxWidth: '1200px',
          margin: '0 auto'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
            <button
              onClick={onBack}
              style={{
                background: 'rgba(220, 38, 38, 0.2)',
                color: '#ffffff',
                border: '1px solid rgba(220, 38, 38, 0.5)',
                borderRadius: '8px',
                padding: '10px 16px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '600',
                transition: 'all 0.3s ease',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}
              onMouseOver={(e) => {
                e.target.style.background = 'rgba(220, 38, 38, 0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.background = 'rgba(220, 38, 38, 0.2)';
              }}
            >
              ← Geri
            </button>
            <h1 style={{
              color: '#ffffff',
              fontSize: '1.8rem',
              margin: 0,
              fontWeight: '600',
              textShadow: '0 0 10px rgba(220, 38, 38, 0.3)'
            }}>
              PIN Oluştur
            </h1>
          </div>
        </div>
      </div>

      {/* Ana İçerik */}
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '40px',
        position: 'relative',
        zIndex: 2
      }}>
        <div style={{
          background: 'rgba(20, 20, 20, 0.8)',
          borderRadius: '20px',
          padding: '40px',
          border: '1px solid rgba(220, 38, 38, 0.3)',
          boxShadow: '0 20px 60px rgba(0, 0, 0, 0.4)'
        }}>
          <div style={{
            textAlign: 'center',
            marginBottom: '40px'
          }}>
            <div style={{
              fontSize: '3rem',
              fontWeight: '700',
              color: '#dc2626',
              marginBottom: '10px',
              textShadow: '0 0 20px rgba(220, 38, 38, 0.5)'
            }}>
              🎯
            </div>
            <h2 style={{
              color: '#ffffff',
              fontSize: '2rem',
              margin: '0 0 10px 0',
              fontWeight: '600'
            }}>
              Yeni Tarama PIN'i Oluştur
            </h2>
            <p style={{
              color: 'rgba(255, 255, 255, 0.7)',
              fontSize: '1.1rem',
              margin: 0
            }}>
              Hedef bilgilerini girerek güvenli tarama PIN'i oluşturun
            </p>
          </div>

          <div style={{
            display: 'grid',
            gap: '25px',
            maxWidth: '600px',
            margin: '0 auto'
          }}>
            {/* Hedef Adı */}
            <div>
              <label style={{
                display: 'block',
                color: '#ffffff',
                fontSize: '1rem',
                fontWeight: '600',
                marginBottom: '8px'
              }}>
                Hedef Adı *
              </label>
              <input
                type="text"
                value={targetInfo.name}
                onChange={(e) => setTargetInfo({...targetInfo, name: e.target.value})}
                placeholder="Örn: ACMASTER 15/1"
                style={{
                  width: '100%',
                  padding: '15px 20px',
                  background: 'rgba(255, 255, 255, 0.05)',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  borderRadius: '10px',
                  color: '#ffffff',
                  fontSize: '1rem',
                  transition: 'all 0.3s ease'
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = 'rgba(220, 38, 38, 0.6)';
                  e.target.style.background = 'rgba(255, 255, 255, 0.08)';
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = 'rgba(220, 38, 38, 0.3)';
                  e.target.style.background = 'rgba(255, 255, 255, 0.05)';
                }}
              />
            </div>

            {/* Açıklama */}
            <div>
              <label style={{
                display: 'block',
                color: '#ffffff',
                fontSize: '1rem',
                fontWeight: '600',
                marginBottom: '8px'
              }}>
                Açıklama
              </label>
              <textarea
                value={targetInfo.description}
                onChange={(e) => setTargetInfo({...targetInfo, description: e.target.value})}
                placeholder="Hedef hakkında ek bilgiler..."
                rows={3}
                style={{
                  width: '100%',
                  padding: '15px 20px',
                  background: 'rgba(255, 255, 255, 0.05)',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  borderRadius: '10px',
                  color: '#ffffff',
                  fontSize: '1rem',
                  resize: 'vertical',
                  transition: 'all 0.3s ease'
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = 'rgba(220, 38, 38, 0.6)';
                  e.target.style.background = 'rgba(255, 255, 255, 0.08)';
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = 'rgba(220, 38, 38, 0.3)';
                  e.target.style.background = 'rgba(255, 255, 255, 0.05)';
                }}
              />
            </div>

            {/* IP Adresi */}
            <div>
              <label style={{
                display: 'block',
                color: '#ffffff',
                fontSize: '1rem',
                fontWeight: '600',
                marginBottom: '8px'
              }}>
                IP Adresi
              </label>
              <input
                type="text"
                value={targetInfo.ip}
                onChange={(e) => setTargetInfo({...targetInfo, ip: e.target.value})}
                placeholder="192.168.1.100"
                style={{
                  width: '100%',
                  padding: '15px 20px',
                  background: 'rgba(255, 255, 255, 0.05)',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  borderRadius: '10px',
                  color: '#ffffff',
                  fontSize: '1rem',
                  transition: 'all 0.3s ease'
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = 'rgba(220, 38, 38, 0.6)';
                  e.target.style.background = 'rgba(255, 255, 255, 0.08)';
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = 'rgba(220, 38, 38, 0.3)';
                  e.target.style.background = 'rgba(255, 255, 255, 0.05)';
                }}
              />
            </div>

            {/* Konum */}
            <div>
              <label style={{
                display: 'block',
                color: '#ffffff',
                fontSize: '1rem',
                fontWeight: '600',
                marginBottom: '8px'
              }}>
                Konum
              </label>
              <input
                type="text"
                value={targetInfo.location}
                onChange={(e) => setTargetInfo({...targetInfo, location: e.target.value})}
                placeholder="İstanbul, Türkiye"
                style={{
                  width: '100%',
                  padding: '15px 20px',
                  background: 'rgba(255, 255, 255, 0.05)',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  borderRadius: '10px',
                  color: '#ffffff',
                  fontSize: '1rem',
                  transition: 'all 0.3s ease'
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = 'rgba(220, 38, 38, 0.6)';
                  e.target.style.background = 'rgba(255, 255, 255, 0.08)';
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = 'rgba(220, 38, 38, 0.3)';
                  e.target.style.background = 'rgba(255, 255, 255, 0.05)';
                }}
              />
            </div>

            {/* Oluştur Butonu */}
            <div style={{
              textAlign: 'center',
              marginTop: '30px'
            }}>
              <button
                onClick={handleCreatePin}
                disabled={isCreating}
                style={{
                  background: isCreating 
                    ? 'rgba(220, 38, 38, 0.3)' 
                    : 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)',
                  color: 'white',
                  border: 'none',
                  padding: '18px 40px',
                  borderRadius: '12px',
                  fontSize: '1.1rem',
                  fontWeight: '700',
                  cursor: isCreating ? 'not-allowed' : 'pointer',
                  textTransform: 'uppercase',
                  letterSpacing: '2px',
                  transition: 'all 0.3s ease',
                  boxShadow: '0 8px 25px rgba(220, 38, 38, 0.3)',
                  border: '1px solid rgba(220, 38, 38, 0.3)',
                  minWidth: '200px'
                }}
                onMouseOver={(e) => {
                  if (!isCreating) {
                    e.target.style.transform = 'translateY(-3px)';
                    e.target.style.boxShadow = '0 12px 35px rgba(220, 38, 38, 0.4)';
                  }
                }}
                onMouseOut={(e) => {
                  if (!isCreating) {
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = '0 8px 25px rgba(220, 38, 38, 0.3)';
                  }
                }}
              >
                {isCreating ? (
                  <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '10px'
                  }}>
                    <div style={{
                      width: '20px',
                      height: '20px',
                      border: '2px solid rgba(255, 255, 255, 0.3)',
                      borderTop: '2px solid #ffffff',
                      borderRadius: '50%',
                      animation: 'spin 1s linear infinite'
                    }}></div>
                    Oluşturuluyor...
                  </div>
                ) : (
                  'PIN Oluştur'
                )}
              </button>
            </div>
          </div>
        </div>
      </div>

      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#1f2937',
            color: '#fff',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            borderRadius: '8px'
          },
        }}
      />

      <style>
        {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}
      </style>
    </div>
  );
};

export default CreatePinPage;
