import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const ViewResultsPage = ({ pin, onBack }) => {
  const [pinData, setPinData] = useState(null);
  const [scanResults, setScanResults] = useState(null);
  const [screenshot, setScreenshot] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchPinDetails();
  }, [pin]);

  const fetchPinDetails = async () => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5005/api/pin-details/${pin.pin}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setPinData(response.data.pin);
      
      if (response.data.pin.scanResults) {
        setScanResults(response.data.pin.scanResults);
      }
      
      // Ekran g√∂r√ºnt√ºs√ºn√º getir
      if (response.data.pin.screenshotPath) {
        try {
          const screenshotResponse = await axios.get(`http://localhost:5005/api/screenshot/${pin.pin}`, {
            responseType: 'blob',
            headers: { Authorization: `Bearer ${token}` }
          });
          const imageUrl = URL.createObjectURL(screenshotResponse.data);
          setScreenshot(imageUrl);
        } catch (screenshotError) {
          console.log('Ekran g√∂r√ºnt√ºs√º bulunamadƒ±');
        }
      }
      
    } catch (error) {
      console.error('PIN detaylarƒ± y√ºklenemedi:', error);
      toast.error('PIN detaylarƒ± y√ºklenemedi!');
    } finally {
      setIsLoading(false);
    }
  };

  const parseScanResults = (results) => {
    if (!results) return null;
    
    let resultsString = results;
    if (typeof results !== 'string') {
      resultsString = String(results);
    }
    
    const lines = resultsString.split('\n');
    const sections = {
      suspiciousActivities: [],
      systemInfo: [],
      screenshot: null
    };
    
    let currentSection = null;
    
    lines.forEach(line => {
      line = line.trim();
      
      if (line.includes('≈û√úPHELƒ∞ AKTƒ∞Vƒ∞TELER')) {
        currentSection = 'suspiciousActivities';
      } else if (line.includes('Sƒ∞STEM Bƒ∞LGƒ∞LERƒ∞')) {
        currentSection = 'systemInfo';
      } else if (line.includes('EKRAN G√ñR√úNT√úS√ú')) {
        currentSection = 'screenshot';
      } else if (line.includes('Fƒ∞VEM MODS')) {
        currentSection = 'fivemMods';
      } else if (line && currentSection && line.startsWith('‚ö†Ô∏è')) {
        sections.suspiciousActivities.push(line.replace('‚ö†Ô∏è ', ''));
      } else if (line && currentSection === 'screenshot' && line.includes('‚úÖ')) {
        sections.screenshot = line.replace('‚úÖ Ekran g√∂r√ºnt√ºs√º alƒ±ndƒ±: ', '');
      } else if (line && currentSection === 'systemInfo' && (line.startsWith('üñ•Ô∏è') || line.startsWith('üë§') || line.startsWith('üìÅ') || line.startsWith('‚è∞') || line.startsWith('üìÖ') || line.startsWith('üîí') || line.startsWith('üì¶') || line.startsWith('üåç') || line.startsWith('üéÆ') || line.startsWith('üóëÔ∏è') || line.startsWith('üíæ'))) {
        sections.systemInfo.push(line);
      }
    });
    
    return sections;
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleString('tr-TR');
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Panoya kopyalandƒ±!');
  };

  if (isLoading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#ffffff',
        fontSize: '1.2rem'
      }}>
        <div style={{
          textAlign: 'center',
          padding: '40px',
          background: 'rgba(20, 20, 20, 0.8)',
          borderRadius: '15px',
          border: '1px solid rgba(220, 38, 38, 0.3)'
        }}>
          <div style={{
            width: '50px',
            height: '50px',
            border: '3px solid rgba(220, 38, 38, 0.3)',
            borderTop: '3px solid #dc2626',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 20px'
          }}></div>
          Tarama sonu√ßlarƒ± y√ºkleniyor...
        </div>
        <style>
          {`
            @keyframes spin {
              0% { transform: rotate(0deg); }
              100% { transform: rotate(360deg); }
            }
          `}
        </style>
      </div>
    );
  }

  const parsedResults = scanResults ? parseScanResults(scanResults) : null;

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
      color: '#ffffff',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Yaƒümur efekti */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1
      }}>
        {Array.from({ length: 20 }).map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: '1px',
              height: `${Math.random() * 50 + 30}px`,
              background: 'linear-gradient(to bottom, rgba(220, 38, 38, 0.3), rgba(220, 38, 38, 0.1))',
              animation: `rain ${Math.random() * 3 + 2}s linear infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
        `}
      </style>

      {/* Header */}
      <div style={{
        background: 'rgba(20, 20, 20, 0.9)',
        backdropFilter: 'blur(20px)',
        padding: '20px',
        borderBottom: '1px solid rgba(220, 38, 38, 0.3)',
        position: 'relative',
        zIndex: 2
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          maxWidth: '1400px',
          margin: '0 auto'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
            <button
              onClick={onBack}
              style={{
                background: 'rgba(220, 38, 38, 0.2)',
                color: '#ffffff',
                border: '1px solid rgba(220, 38, 38, 0.5)',
                borderRadius: '8px',
                padding: '10px 16px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '600',
                transition: 'all 0.3s ease',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}
              onMouseOver={(e) => {
                e.target.style.background = 'rgba(220, 38, 38, 0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.background = 'rgba(220, 38, 38, 0.2)';
              }}
            >
              ‚Üê Geri
            </button>
            <h1 style={{
              color: '#ffffff',
              fontSize: '1.8rem',
              margin: 0,
              fontWeight: '600',
              textShadow: '0 0 10px rgba(220, 38, 38, 0.3)'
            }}>
              üìä Tarama Sonu√ßlarƒ±
            </h1>
          </div>
          
          <div style={{
            fontSize: '2rem',
            fontWeight: '700',
            color: '#ffffff',
            fontFamily: 'monospace',
            letterSpacing: '4px',
            textShadow: '0 0 15px rgba(220, 38, 38, 0.5)',
            background: 'rgba(220, 38, 38, 0.1)',
            padding: '12px 20px',
            borderRadius: '10px',
            border: '1px solid rgba(220, 38, 38, 0.3)'
          }}>
            {pinData?.pin || pin.pin}
          </div>
        </div>
      </div>

      {/* Ana ƒ∞√ßerik */}
      <div style={{
        maxWidth: '1400px',
        margin: '0 auto',
        padding: '30px',
        position: 'relative',
        zIndex: 2
      }}>
        
        {/* PIN Bilgileri */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.8)',
          borderRadius: '15px',
          padding: '30px',
          border: '1px solid rgba(220, 38, 38, 0.3)',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
          marginBottom: '30px'
        }}>
          <h2 style={{
            color: '#ffffff',
            fontSize: '1.5rem',
            margin: '0 0 20px 0',
            fontWeight: '600',
            display: 'flex',
            alignItems: 'center',
            gap: '10px'
          }}>
            üìã PIN Bilgileri
          </h2>
          
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: '20px'
          }}>
            <div style={{
              background: 'rgba(34, 197, 94, 0.1)',
              borderRadius: '10px',
              padding: '20px',
              border: '1px solid rgba(34, 197, 94, 0.3)'
            }}>
              <h3 style={{ color: '#22c55e', margin: '0 0 10px 0', fontSize: '1.1rem' }}>Durum</h3>
              <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '14px' }}>
                {pinData?.scanCompleted ? '‚úÖ Tamamlandƒ±' : '‚è≥ Beklemede'}
              </div>
            </div>
            
            <div style={{
              background: 'rgba(59, 130, 246, 0.1)',
              borderRadius: '10px',
              padding: '20px',
              border: '1px solid rgba(59, 130, 246, 0.3)'
            }}>
              <h3 style={{ color: '#3b82f6', margin: '0 0 10px 0', fontSize: '1.1rem' }}>Olu≈üturulma</h3>
              <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '14px' }}>
                {formatDate(pinData?.createdAt)}
              </div>
            </div>
            
            <div style={{
              background: 'rgba(168, 85, 247, 0.1)',
              borderRadius: '10px',
              padding: '20px',
              border: '1px solid rgba(168, 85, 247, 0.3)'
            }}>
              <h3 style={{ color: '#a855f7', margin: '0 0 10px 0', fontSize: '1.1rem' }}>Tarama Zamanƒ±</h3>
              <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '14px' }}>
                {pinData?.completedAt ? formatDate(pinData.completedAt) : 'Hen√ºz tamamlanmadƒ±'}
              </div>
            </div>
          </div>
        </div>

        {/* Sistem Bilgileri */}
        {parsedResults?.systemInfo?.length > 0 && (
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
            marginBottom: '30px'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600',
              display: 'flex',
              alignItems: 'center',
              gap: '10px'
            }}>
              üíª Sistem Bilgileri
            </h2>
            
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
              gap: '15px'
            }}>
              {parsedResults.systemInfo.map((info, index) => (
                <div key={index} style={{
                  background: 'rgba(34, 197, 94, 0.1)',
                  borderRadius: '10px',
                  padding: '15px',
                  border: '1px solid rgba(34, 197, 94, 0.3)',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
                onClick={() => copyToClipboard(info)}
                onMouseOver={(e) => {
                  e.target.style.background = 'rgba(34, 197, 94, 0.2)';
                  e.target.style.transform = 'translateY(-2px)';
                }}
                onMouseOut={(e) => {
                  e.target.style.background = 'rgba(34, 197, 94, 0.1)';
                  e.target.style.transform = 'translateY(0)';
                }}
                >
                  <div style={{ color: 'rgba(255, 255, 255, 0.9)', fontSize: '14px', lineHeight: '1.5' }}>
                    {info}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* ≈û√ºpheli Aktiviteler */}
        {parsedResults?.suspiciousActivities?.length > 0 && (
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
            marginBottom: '30px'
          }}>
            <h2 style={{
              color: '#ef4444',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600',
              display: 'flex',
              alignItems: 'center',
              gap: '10px'
            }}>
              ‚ö†Ô∏è ≈û√ºpheli Aktiviteler ({parsedResults.suspiciousActivities.length})
            </h2>
            
            <div style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '14px' }}>
              <ul style={{ margin: 0, paddingLeft: '20px' }}>
                {parsedResults.suspiciousActivities.map((activity, index) => (
                  <li key={index} style={{ marginBottom: '8px' }}>{activity}</li>
                ))}
              </ul>
            </div>
          </div>
        )}

        {/* Ekran G√∂r√ºnt√ºs√º */}
        {screenshot && (
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
            marginBottom: '30px'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600',
              display: 'flex',
              alignItems: 'center',
              gap: '10px'
            }}>
              üì∏ Ekran G√∂r√ºnt√ºs√º
            </h2>
            
            <div style={{
              borderRadius: '10px',
              overflow: 'hidden',
              border: '2px solid rgba(220, 38, 38, 0.3)',
              background: 'rgba(0, 0, 0, 0.5)'
            }}>
              <img 
                src={screenshot} 
                alt="Ekran G√∂r√ºnt√ºs√º" 
                style={{
                  width: '100%',
                  height: 'auto',
                  display: 'block'
                }}
              />
            </div>
          </div>
        )}

        {/* Ham Sonu√ßlar */}
        {scanResults && (
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '30px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
            marginBottom: '30px'
          }}>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              fontWeight: '600',
              display: 'flex',
              alignItems: 'center',
              gap: '10px'
            }}>
              üìÑ Ham Sonu√ßlar
            </h2>
            
            <div style={{
              background: 'rgba(0, 0, 0, 0.5)',
              borderRadius: '10px',
              padding: '20px',
              border: '1px solid rgba(255, 255, 255, 0.1)',
              fontFamily: 'monospace',
              fontSize: '12px',
              lineHeight: '1.6',
              color: 'rgba(255, 255, 255, 0.8)',
              whiteSpace: 'pre-wrap',
              overflow: 'auto',
              maxHeight: '400px'
            }}>
              {scanResults}
            </div>
          </div>
        )}

        {/* Sonu√ß Yok */}
        {!scanResults && (
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '60px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
            textAlign: 'center'
          }}>
            <div style={{
              fontSize: '4rem',
              marginBottom: '20px'
            }}>
              üìä
            </div>
            <h2 style={{
              color: '#ffffff',
              fontSize: '1.5rem',
              margin: '0 0 10px 0',
              fontWeight: '600'
            }}>
              Tarama Sonu√ßlarƒ± Bulunamadƒ±
            </h2>
            <p style={{
              color: 'rgba(255, 255, 255, 0.6)',
              fontSize: '14px',
              margin: 0
            }}>
              Bu PIN i√ßin hen√ºz tarama yapƒ±lmamƒ±≈ü veya sonu√ßlar y√ºklenmemi≈ü.
            </p>
          </div>
        )}
      </div>

      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#1f2937',
            color: '#fff',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            borderRadius: '8px'
          },
        }}
      />
    </div>
  );
};

export default ViewResultsPage;
