import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const PinDetailsPage = ({ pin, onBack }) => {
  const [pinData, setPinData] = useState(null);
  const [scanResults, setScanResults] = useState(null);
  const [screenshot, setScreenshot] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    fetchPinDetails();
  }, [pin]);

  const fetchPinDetails = async () => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5005/api/pin-details/${pin.pin}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setPinData(response.data.pin);
      
      if (response.data.pin.scanResults) {
        setScanResults(response.data.pin.scanResults);
      }
      
      // Ekran g√∂r√ºnt√ºs√ºn√º getir
      if (response.data.pin.screenshotPath) {
        try {
          const screenshotResponse = await axios.get(`http://localhost:5005/api/screenshot/${pin.pin}`, {
            responseType: 'blob',
            headers: { Authorization: `Bearer ${token}` }
          });
          const imageUrl = URL.createObjectURL(screenshotResponse.data);
          setScreenshot(imageUrl);
        } catch (screenshotError) {
          console.log('Ekran g√∂r√ºnt√ºs√º bulunamadƒ±');
        }
      }
      
    } catch (error) {
      console.error('PIN detaylarƒ± y√ºklenemedi:', error);
      toast.error('PIN detaylarƒ± y√ºklenemedi!');
    } finally {
      setIsLoading(false);
    }
  };

  const parseScanResults = (results) => {
    if (!results) return null;
    
    let resultsString = results;
    if (typeof results !== 'string') {
      resultsString = String(results);
    }
    
    const lines = resultsString.split('\n');
    const sections = {
      suspiciousActivities: [],
      deletedFiles: [],
      recentApps: [],
      unlicensedApps: [],
      screenshot: null,
      systemInfo: [],
      networkActivity: [],
      registryChanges: [],
      startupPrograms: [],
      browserHistory: [],
      installedSoftware: []
    };
    
    let currentSection = null;
    
    lines.forEach(line => {
      line = line.trim();
      
      if (line.includes('≈û√úPHELƒ∞ AKTƒ∞Vƒ∞TELER')) {
        currentSection = 'suspiciousActivities';
      } else if (line.includes('SON Sƒ∞Lƒ∞NEN DOSYALAR')) {
        currentSection = 'deletedFiles';
      } else if (line.includes('SON √áALI≈ûTIRILAN UYGULAMALAR')) {
        currentSection = 'recentApps';
      } else if (line.includes('Lƒ∞SANSSIZ UYGULAMALAR')) {
        currentSection = 'unlicensedApps';
      } else if (line.includes('Sƒ∞STEM Bƒ∞LGƒ∞LERƒ∞')) {
        currentSection = 'systemInfo';
      } else if (line.includes('EKRAN G√ñR√úNT√úS√ú')) {
        currentSection = 'screenshot';
