import React, { useEffect, useState } from 'react';

const BanPage = ({ banInfo }) => {
  const [banData, setBanData] = useState(banInfo || {
    reason: 'No reason provided',
    bannedAt: new Date().toISOString()
  });

  useEffect(() => {
    // URL'den ban bilgilerini al (eÄŸer URL'den geliyorsa)
    if (window.location.search) {
      const searchParams = new URLSearchParams(window.location.search);
      const reason = searchParams.get('reason') || 'No reason provided';
      const bannedAt = searchParams.get('bannedAt') || new Date().toISOString();
      
      setBanData({
        reason,
        bannedAt
      });
    }
  }, []);

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return 'Unknown date';
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #000000 0%, #0d0d0d 25%, #1a0a0a 50%, #0d0d0d 75%, #000000 100%)',
      color: '#ffffff',
      fontFamily: 'Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Background Pattern */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: `
          radial-gradient(circle at 20% 80%, rgba(220, 38, 38, 0.05) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(220, 38, 38, 0.05) 0%, transparent 50%),
          radial-gradient(circle at 40% 40%, rgba(220, 38, 38, 0.03) 0%, transparent 50%)
        `,
        pointerEvents: 'none'
      }}></div>

      <div style={{
        background: 'linear-gradient(135deg, rgba(0, 0, 0, 0.95) 0%, rgba(10, 10, 10, 0.98) 100%)',
        border: '3px solid #dc2626',
        borderRadius: '24px',
        padding: '50px',
        maxWidth: '700px',
        width: '100%',
        textAlign: 'center',
        boxShadow: '0 20px 60px rgba(220, 38, 38, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.1)',
        position: 'relative',
        zIndex: 10,
        backdropFilter: 'blur(20px)'
      }}>
        {/* Animated border */}
        <div style={{
          position: 'absolute',
          top: '-3px',
          left: '-3px',
          right: '-3px',
          bottom: '-3px',
          background: 'linear-gradient(45deg, #dc2626, #ff4444, #dc2626, #ff4444)',
          borderRadius: '24px',
          zIndex: -1,
          animation: 'borderGlow 3s linear infinite'
        }}></div>
        
        <div style={{
          fontSize: '64px',
          marginBottom: '30px',
          animation: 'pulse 2s ease-in-out infinite alternate',
          filter: 'drop-shadow(0 0 20px rgba(220, 38, 38, 0.8))'
        }}>
          ðŸš«
        </div>
        
        <h1 style={{
          fontSize: '42px',
          fontWeight: '800',
          color: '#ffffff',
          marginBottom: '15px',
          textShadow: '0 0 30px rgba(220, 38, 38, 0.8), 0 4px 8px rgba(0, 0, 0, 0.5)',
          letterSpacing: '2px',
          textTransform: 'uppercase'
        }}>
          ACCOUNT BANNED
        </h1>

        <div style={{
          color: '#dc2626',
          fontSize: '18px',
          fontWeight: '600',
          letterSpacing: '3px',
          textTransform: 'uppercase',
          marginBottom: '30px',
          opacity: 0.8
        }}>
          PERMANENT SUSPENSION
        </div>
        
        <p style={{
          fontSize: '20px',
          marginBottom: '40px',
          color: '#cccccc',
          lineHeight: '1.6',
          fontWeight: '500'
        }}>
          This account has been permanently banned from Void Scanner.
        </p>
        
        {banData && (
          <div style={{
            background: 'linear-gradient(135deg, rgba(220, 38, 38, 0.15) 0%, rgba(0, 0, 0, 0.8) 100%)',
            border: '2px solid rgba(220, 38, 38, 0.4)',
            borderRadius: '16px',
            padding: '25px',
            marginBottom: '35px',
            textAlign: 'left',
            boxShadow: '0 8px 32px rgba(220, 38, 38, 0.2), inset 0 1px 0 rgba(255, 255, 255, 0.1)'
          }}>
            <h3 style={{
              color: '#dc2626',
              fontSize: '18px',
              marginBottom: '15px',
              fontWeight: '700',
              letterSpacing: '1px',
              textTransform: 'uppercase'
            }}>
              Ban Details
            </h3>
            <div style={{ marginBottom: '12px' }}>
              <span style={{
                color: '#ffffff',
                fontSize: '16px',
                fontWeight: '600'
              }}>
                Reason:
              </span>
              <span style={{
                color: '#dc2626',
                fontSize: '16px',
                marginLeft: '8px',
                fontWeight: '500'
              }}>
                {banData.reason}
              </span>
            </div>
            <div>
              <span style={{
                color: '#ffffff',
                fontSize: '16px',
                fontWeight: '600'
              }}>
                Banned At:
              </span>
              <span style={{
                color: '#cccccc',
                fontSize: '16px',
                marginLeft: '8px',
                fontWeight: '500'
              }}>
                {formatDate(banData.bannedAt)}
              </span>
            </div>
          </div>
        )}
        
        <div style={{
          background: 'linear-gradient(135deg, rgba(220, 38, 38, 0.1) 0%, rgba(0, 0, 0, 0.8) 100%)',
          border: '2px solid rgba(220, 38, 38, 0.3)',
          borderRadius: '16px',
          padding: '25px',
          marginBottom: '40px',
          boxShadow: '0 8px 32px rgba(220, 38, 38, 0.2), inset 0 1px 0 rgba(255, 255, 255, 0.1)'
        }}>
          <h3 style={{
            color: '#dc2626',
            fontSize: '18px',
            marginBottom: '15px',
            fontWeight: '700',
            letterSpacing: '1px',
            textTransform: 'uppercase'
          }}>
            What to do next?
          </h3>
          <p style={{
            color: '#ffffff',
            fontSize: '16px',
            lineHeight: '1.6',
            fontWeight: '500'
          }}>
            If you believe this ban was issued in error, please open a ticket on our Discord server.
            Our support team will review your case and respond within 24-48 hours.
          </p>
        </div>
        
        <a 
          href="https://discord.gg/NPDrBmvU"
          target="_blank"
          rel="noopener noreferrer"
          style={{
            display: 'inline-block',
            background: 'linear-gradient(135deg, rgba(220, 38, 38, 0.8) 0%, rgba(220, 38, 38, 0.6) 100%)',
            color: '#ffffff',
            padding: '18px 40px',
            borderRadius: '16px',
            textDecoration: 'none',
            fontSize: '18px',
            fontWeight: '700',
            transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
            boxShadow: '0 8px 32px rgba(220, 38, 38, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.2)',
            textTransform: 'uppercase',
            letterSpacing: '2px',
            textShadow: '0 0 20px rgba(220, 38, 38, 0.8)',
            border: '2px solid rgba(220, 38, 38, 0.6)'
          }}
          onMouseOver={(e) => {
            e.target.style.transform = 'translateY(-4px) scale(1.05)';
            e.target.style.boxShadow = '0 16px 48px rgba(220, 38, 38, 0.6), inset 0 1px 0 rgba(255, 255, 255, 0.3)';
            e.target.style.background = 'linear-gradient(135deg, rgba(220, 38, 38, 1) 0%, rgba(220, 38, 38, 0.8) 100%)';
          }}
          onMouseOut={(e) => {
            e.target.style.transform = 'translateY(0) scale(1)';
            e.target.style.boxShadow = '0 8px 32px rgba(220, 38, 38, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.2)';
            e.target.style.background = 'linear-gradient(135deg, rgba(220, 38, 38, 0.8) 0%, rgba(220, 38, 38, 0.6) 100%)';
          }}
        >
          Open Discord Ticket
        </a>
      </div>
      
      <style jsx>{`
        @keyframes pulse {
          0% { transform: scale(1); }
          100% { transform: scale(1.1); }
        }
        
        @keyframes borderGlow {
          0% { box-shadow: 0 0 30px rgba(220, 38, 38, 0.5); }
          50% { box-shadow: 0 0 60px rgba(220, 38, 38, 0.8); }
          100% { box-shadow: 0 0 30px rgba(220, 38, 38, 0.5); }
        }
      `}</style>
    </div>
  );
};

export default BanPage;
