import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';
import PinDetailsPage from './PinDetailsPage';
import CreatePinPage from './CreatePinPage';

const Dashboard = ({ user, onLogout }) => {
  const [pins, setPins] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedPin, setSelectedPin] = useState(null);
  const [showPinDetails, setShowPinDetails] = useState(false);
  const [showPinDetailsPage, setShowPinDetailsPage] = useState(false);
  const [showCreatePinPage, setShowCreatePinPage] = useState(false);
  const [scanResults, setScanResults] = useState(null);
  const [screenshot, setScreenshot] = useState(null);

  useEffect(() => {
    fetchPins();
  }, []);

  const fetchPins = async () => {
    try {
      setIsLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/pins', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPins(response.data);
    } catch (error) {
      console.error('PIN\'ler yÃ¼klenemedi:', error);
      toast.error('PIN\'ler yÃ¼klenemedi!');
    } finally {
      setIsLoading(false);
    }
  };

  const createPin = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('/api/pins', {
        targetInfo: {
          name: 'Random Target',
          description: 'Test target for scanning'
        }
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      toast.success('PIN baÅŸarÄ±yla oluÅŸturuldu!');
      fetchPins(); // PIN listesini yenile
    } catch (error) {
      console.error('PIN oluÅŸturulamadÄ±:', error);
      toast.error('PIN oluÅŸturulamadÄ±!');
    }
  };

  const deletePin = async (pinId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/pins/${pinId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      toast.success('PIN baÅŸarÄ±yla silindi!');
      fetchPins(); // PIN listesini yenile
    } catch (error) {
      console.error('PIN silinemedi:', error);
      toast.error('PIN silinemedi!');
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleString('tr-TR');
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#10b981';
      case 'used': return '#f59e0b';
      case 'expired': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'active': return 'Aktif';
      case 'used': return 'KullanÄ±ldÄ±';
      case 'expired': return 'SÃ¼resi Doldu';
      default: return 'Bilinmiyor';
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Panoya kopyalandÄ±!');
  };

  const viewPinDetails = async (pin) => {
    try {
      setSelectedPin(pin);
      setShowPinDetailsPage(true);
    } catch (error) {
      console.error('PIN detaylarÄ± yÃ¼klenemedi:', error);
      toast.error('PIN detaylarÄ± yÃ¼klenemedi!');
    }
  };

  const parseScanResults = (results) => {
    if (!results) return null;
    
    // EÄŸer results string deÄŸilse, string'e Ã§evir
    let resultsString = results;
    if (typeof results !== 'string') {
      resultsString = String(results);
    }
    
    const lines = resultsString.split('\n');
    const sections = {
      suspiciousActivities: [],
      deletedFiles: [],
      recentApps: [],
      unlicensedApps: [],
      screenshot: null
    };
    
    let currentSection = null;
    
    lines.forEach(line => {
      line = line.trim();
      
      if (line.includes('ÅÃœPHELÄ° AKTÄ°VÄ°TELER')) {
        currentSection = 'suspiciousActivities';
      } else if (line.includes('SON SÄ°LÄ°NEN DOSYALAR')) {
        currentSection = 'deletedFiles';
      } else if (line.includes('SON Ã‡ALIÅTIRILAN UYGULAMALAR')) {
        currentSection = 'recentApps';
      } else if (line.includes('LÄ°SANSSIZ UYGULAMALAR')) {
        currentSection = 'unlicensedApps';
      } else if (line.includes('EKRAN GÃ–RÃœNTÃœSÃœ')) {
        currentSection = 'screenshot';
      } else if (line && currentSection && line.startsWith('âš ï¸')) {
        sections.suspiciousActivities.push(line.replace('âš ï¸ ', ''));
      } else if (line && currentSection && line.startsWith('ğŸ“')) {
        sections.deletedFiles.push(line.replace('ğŸ“ ', ''));
      } else if (line && currentSection && line.startsWith('ğŸ’»')) {
        sections.recentApps.push(line.replace('ğŸ’» ', ''));
      } else if (line && currentSection && line.startsWith('ğŸ”“')) {
        sections.unlicensedApps.push(line.replace('ğŸ”“ ', ''));
      } else if (line && currentSection === 'screenshot' && line.includes('âœ…')) {
        sections.screenshot = line.replace('âœ… Ekran gÃ¶rÃ¼ntÃ¼sÃ¼ alÄ±ndÄ±: ', '');
      }
    });
    
    return sections;
  };

  if (isLoading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#ffffff',
        fontSize: '1.2rem'
      }}>
        <div style={{
          textAlign: 'center',
          padding: '40px',
          background: 'rgba(20, 20, 20, 0.8)',
          borderRadius: '15px',
          border: '1px solid rgba(220, 38, 38, 0.3)'
        }}>
          <div style={{
            width: '50px',
            height: '50px',
            border: '3px solid rgba(220, 38, 38, 0.3)',
            borderTop: '3px solid #dc2626',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 20px'
          }}></div>
          PIN'ler yÃ¼kleniyor...
        </div>
        <style>
          {`
            @keyframes spin {
              0% { transform: rotate(0deg); }
              100% { transform: rotate(360deg); }
            }
          `}
        </style>
      </div>
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%)',
      color: '#ffffff',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* YaÄŸmur efekti */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1
      }}>
        {Array.from({ length: 20 }).map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: '1px',
              height: `${Math.random() * 50 + 30}px`,
              background: 'linear-gradient(to bottom, rgba(220, 38, 38, 0.3), rgba(220, 38, 38, 0.1))',
              animation: `rain ${Math.random() * 3 + 2}s linear infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
        `}
      </style>

      {/* Header */}
      <div style={{
        background: 'rgba(20, 20, 20, 0.9)',
        backdropFilter: 'blur(20px)',
        padding: '20px',
        borderBottom: '1px solid rgba(220, 38, 38, 0.3)',
        position: 'relative',
        zIndex: 2
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          maxWidth: '1400px',
          margin: '0 auto'
        }}>
          <h1 style={{
            color: '#ffffff',
            fontSize: '2rem',
            margin: 0,
            fontWeight: '700',
            textShadow: '0 0 20px rgba(220, 38, 38, 0.5)'
          }}>
            ğŸ¯ VOID SCANNER DASHBOARD
          </h1>
          
          <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '0.9rem',
              textAlign: 'right'
            }}>
              <div>HoÅŸ geldin, <strong style={{ color: '#dc2626' }}>{user.username}</strong></div>
              <div style={{ fontSize: '0.8rem', color: 'rgba(255, 255, 255, 0.6)' }}>
                {user.role === 'admin' ? 'ğŸ‘‘ Admin' : 'ğŸ‘¤ MÃ¼ÅŸteri'}
              </div>
            </div>
            
            <button
              onClick={onLogout}
              style={{
                background: 'rgba(220, 38, 38, 0.2)',
                color: '#ffffff',
                border: '1px solid rgba(220, 38, 38, 0.5)',
                borderRadius: '8px',
                padding: '10px 16px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '600',
                transition: 'all 0.3s ease'
              }}
              onMouseOver={(e) => {
                e.target.style.background = 'rgba(220, 38, 38, 0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.background = 'rgba(220, 38, 38, 0.2)';
              }}
            >
              Ã‡Ä±kÄ±ÅŸ Yap
            </button>
          </div>
        </div>
      </div>

      {/* Ana Ä°Ã§erik */}
      <div style={{
        maxWidth: '1400px',
        margin: '0 auto',
        padding: '30px',
        position: 'relative',
        zIndex: 2
      }}>
        {/* Ä°statistikler */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '20px',
          marginBottom: '30px'
        }}>
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '25px',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            textAlign: 'center',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
          }}>
            <div style={{
              fontSize: '2.5rem',
              fontWeight: '700',
              color: '#dc2626',
              marginBottom: '10px'
            }}>
              {pins.length}
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '1rem',
              fontWeight: '600'
            }}>
              Toplam PIN
            </div>
          </div>
          
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '25px',
            border: '1px solid rgba(16, 185, 129, 0.3)',
            textAlign: 'center',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
          }}>
            <div style={{
              fontSize: '2.5rem',
              fontWeight: '700',
              color: '#10b981',
              marginBottom: '10px'
            }}>
              {pins.filter(pin => pin.status === 'active').length}
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '1rem',
              fontWeight: '600'
            }}>
              Aktif PIN
            </div>
          </div>
          
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '25px',
            border: '1px solid rgba(245, 158, 11, 0.3)',
            textAlign: 'center',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
          }}>
            <div style={{
              fontSize: '2.5rem',
              fontWeight: '700',
              color: '#f59e0b',
              marginBottom: '10px'
            }}>
              {pins.filter(pin => pin.status === 'used').length}
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '1rem',
              fontWeight: '600'
            }}>
              KullanÄ±lan PIN
            </div>
          </div>
          
          <div style={{
            background: 'rgba(20, 20, 20, 0.8)',
            borderRadius: '15px',
            padding: '25px',
            border: '1px solid rgba(239, 68, 68, 0.3)',
            textAlign: 'center',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
          }}>
            <div style={{
              fontSize: '2.5rem',
              fontWeight: '700',
              color: '#ef4444',
              marginBottom: '10px'
            }}>
              {pins.filter(pin => pin.status === 'expired').length}
            </div>
            <div style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '1rem',
              fontWeight: '600'
            }}>
              SÃ¼resi Dolan PIN
            </div>
          </div>
        </div>

        {/* PIN OluÅŸturma Butonu */}
        <div style={{
          textAlign: 'center',
          marginBottom: '30px'
        }}>
          <button
            onClick={createPin}
            style={{
              background: 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)',
              color: 'white',
              border: 'none',
              padding: '15px 30px',
              borderRadius: '12px',
              fontSize: '16px',
              fontWeight: '700',
              cursor: 'pointer',
              textTransform: 'uppercase',
              letterSpacing: '2px',
              transition: 'all 0.3s ease',
              boxShadow: '0 8px 25px rgba(220, 38, 38, 0.3)',
              border: '1px solid rgba(220, 38, 38, 0.3)'
            }}
            onMouseOver={(e) => {
              e.target.style.transform = 'translateY(-3px)';
              e.target.style.boxShadow = '0 12px 35px rgba(220, 38, 38, 0.4)';
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 8px 25px rgba(220, 38, 38, 0.3)';
            }}
          >
            ğŸ¯ Yeni PIN OluÅŸtur
          </button>
        </div>

        {/* PIN Listesi */}
        <div style={{
          background: 'rgba(20, 20, 20, 0.8)',
          borderRadius: '15px',
          padding: '30px',
          border: '1px solid rgba(220, 38, 38, 0.3)',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)'
        }}>
          <h2 style={{
            color: '#ffffff',
            fontSize: '1.5rem',
            margin: '0 0 20px 0',
            fontWeight: '600',
            display: 'flex',
            alignItems: 'center',
            gap: '10px'
          }}>
            ğŸ“‹ PIN Listesi
          </h2>
          
          {pins.length === 0 ? (
            <div style={{
              textAlign: 'center',
              padding: '40px',
              color: 'rgba(255, 255, 255, 0.6)',
              fontSize: '1.1rem'
            }}>
              HenÃ¼z PIN oluÅŸturulmamÄ±ÅŸ. YukarÄ±daki butona tÄ±klayarak yeni PIN oluÅŸturun.
            </div>
          ) : (
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))',
              gap: '20px'
            }}>
              {pins.map((pin) => (
                <div
                  key={pin._id}
                  style={{
                    background: 'rgba(255, 255, 255, 0.05)',
                    borderRadius: '12px',
                    padding: '20px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    transition: 'all 0.3s ease',
                    cursor: 'pointer'
                  }}
                  onMouseOver={(e) => {
                    e.currentTarget.style.background = 'rgba(255, 255, 255, 0.08)';
                    e.currentTarget.style.transform = 'translateY(-2px)';
                    e.currentTarget.style.boxShadow = '0 8px 25px rgba(0, 0, 0, 0.3)';
                  }}
                  onMouseOut={(e) => {
                    e.currentTarget.style.background = 'rgba(255, 255, 255, 0.05)';
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.boxShadow = 'none';
                  }}
                  onClick={() => viewPinDetails(pin)}
                >
                  <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginBottom: '15px'
                  }}>
                    <div style={{
                      fontSize: '1.3rem',
                      fontWeight: '700',
                      color: '#ffffff',
                      fontFamily: 'monospace',
                      letterSpacing: '2px'
                    }}>
                      {pin.pin}
                    </div>
                    <div style={{
                      padding: '4px 12px',
                      borderRadius: '20px',
                      fontSize: '0.8rem',
                      fontWeight: '600',
                      background: `rgba(${getStatusColor(pin.status).replace('#', '')}, 0.2)`,
                      color: getStatusColor(pin.status),
                      border: `1px solid ${getStatusColor(pin.status)}`
                    }}>
                      {getStatusText(pin.status)}
                    </div>
                  </div>
                  
                  <div style={{
                    color: 'rgba(255, 255, 255, 0.8)',
                    fontSize: '0.9rem',
                    lineHeight: '1.5',
                    marginBottom: '15px'
                  }}>
                    <div><strong>Hedef:</strong> {pin.targetInfo?.name || 'BelirtilmemiÅŸ'}</div>
                    <div><strong>OluÅŸturulma:</strong> {formatDate(pin.createdAt)}</div>
                    <div><strong>Son Kullanma:</strong> {formatDate(pin.expiresAt)}</div>
                  </div>
                  
                  <div style={{
                    display: 'flex',
                    gap: '10px',
                    justifyContent: 'flex-end'
                  }}>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        copyToClipboard(pin.pin);
                      }}
                      style={{
                        background: 'rgba(16, 185, 129, 0.2)',
                        color: '#10b981',
                        border: '1px solid rgba(16, 185, 129, 0.5)',
                        borderRadius: '6px',
                        padding: '6px 12px',
                        fontSize: '0.8rem',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease'
                      }}
                      onMouseOver={(e) => {
                        e.target.style.background = 'rgba(16, 185, 129, 0.3)';
                      }}
                      onMouseOut={(e) => {
                        e.target.style.background = 'rgba(16, 185, 129, 0.2)';
                      }}
                    >
                      Kopyala
                    </button>
                    
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        deletePin(pin._id);
                      }}
                      style={{
                        background: 'rgba(239, 68, 68, 0.2)',
                        color: '#ef4444',
                        border: '1px solid rgba(239, 68, 68, 0.5)',
                        borderRadius: '6px',
                        padding: '6px 12px',
                        fontSize: '0.8rem',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease'
                      }}
                      onMouseOver={(e) => {
                        e.target.style.background = 'rgba(239, 68, 68, 0.3)';
                      }}
                      onMouseOut={(e) => {
                        e.target.style.background = 'rgba(239, 68, 68, 0.2)';
                      }}
                    >
                      Sil
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
      
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#1f2937',
            color: '#fff',
            border: '1px solid rgba(220, 38, 38, 0.3)',
            borderRadius: '8px'
          },
        }}
      />

      {/* PIN DetaylarÄ± SayfasÄ± */}
      {showPinDetailsPage && selectedPin && (
        <PinDetailsPage 
          pin={selectedPin}
          onBack={() => {
            setShowPinDetailsPage(false);
            setSelectedPin(null);
          }}
        />
      )}
    </div>
  );
};

export default Dashboard;
