import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const Dashboard = ({ user, onLogout }) => {
  const [pins, setPins] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showCreatePin, setShowCreatePin] = useState(false);
  const [selectedPin, setSelectedPin] = useState(null);
  const [newPinData, setNewPinData] = useState({
    targetName: '',
    targetEmail: '',
    targetPhone: '',
    notes: '',
    durationHours: 24
  });

  useEffect(() => {
    fetchPins();
  }, []);

  const fetchPins = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/my-pins', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPins(response.data);
    } catch (error) {
      console.error('Fetch Pins Error:', error);
      if (error.code === 'ECONNREFUSED') {
        toast.error('Backend sunucusu çalışmıyor!');
      } else {
        toast.error('PIN\'ler yüklenemedi!');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const createPin = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('/api/create-pin', {
        targetInfo: {
          name: newPinData.targetName,
          email: newPinData.targetEmail,
          phone: newPinData.targetPhone,
          notes: newPinData.notes
        },
        durationHours: newPinData.durationHours
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      toast.success(`PIN oluşturuldu: ${response.data.pin}`);
      setShowCreatePin(false);
      setNewPinData({
        targetName: '',
        targetEmail: '',
        targetPhone: '',
        notes: '',
        durationHours: 24
      });
      fetchPins();
    } catch (error) {
      console.error('Create Pin Error:', error);
      if (error.code === 'ECONNREFUSED') {
        toast.error('Backend sunucusu çalışmıyor!');
      } else {
        toast.error(error.response?.data?.message || 'PIN oluşturulamadı!');
      }
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#00ff00';
      case 'used': return '#ffaa00';
      case 'expired': return '#ff0000';
      default: return '#666';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'active': return 'Aktif';
      case 'used': return 'Kullanıldı';
      case 'expired': return 'Süresi Doldu';
      default: return 'Bilinmiyor';
    }
  };

  const getRiskColor = (riskLevel) => {
    switch (riskLevel) {
      case 'Yüksek': return '#ff0000';
      case 'Orta': return '#ffaa00';
      case 'Düşük': return '#00ff00';
      case 'Çok Düşük': return '#00aaff';
      default: return '#666';
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleString('tr-TR');
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Panoya kopyalandı!');
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'url(/background.jpg) center/cover no-repeat, linear-gradient(135deg, #ff0000 0%, #cc0000 100%)',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Rain Effect */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1
      }}>
        {Array.from({ length: 30 }).map((_, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: '2px',
              height: `${Math.random() * 100 + 50}px`,
              background: 'linear-gradient(to bottom, rgba(0,0,0,0.8), rgba(0,0,0,0.3))',
              animation: `rain ${Math.random() * 2 + 1}s linear infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>
      
      <style>
        {`
          @keyframes rain {
            0% { transform: translateY(-100vh); opacity: 0; }
            10% { opacity: 1; }
            90% { opacity: 1; }
            100% { transform: translateY(100vh); opacity: 0; }
          }
        `}
      </style>

      {/* Header */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.1)',
        backdropFilter: 'blur(15px)',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '20px',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        position: 'relative',
        zIndex: 2
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          flexWrap: 'wrap'
        }}>
          <div>
            <h1 style={{
              color: '#fff',
              fontSize: '2rem',
              margin: '0 0 5px 0',
              textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)',
              letterSpacing: '2px'
            }}>
              VOID SCANNER
            </h1>
            <p style={{
              color: 'rgba(255, 255, 255, 0.8)',
              margin: '0',
              fontSize: '1rem'
            }}>
              Hoş geldin, {user.name}!
            </p>
          </div>
          <div style={{
            display: 'flex',
            gap: '10px',
            flexWrap: 'wrap'
          }}>
            <button
              onClick={() => setShowCreatePin(true)}
              style={{
                background: 'linear-gradient(135deg, #00ff00 0%, #00cc00 100%)',
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                borderRadius: '8px',
                fontSize: '14px',
                fontWeight: '700',
                cursor: 'pointer',
                boxShadow: '0 5px 15px rgba(0, 255, 0, 0.3)',
                textTransform: 'uppercase',
                letterSpacing: '1px'
              }}
            >
              + Yeni PIN
            </button>
            <button
              onClick={onLogout}
              style={{
                background: 'linear-gradient(135deg, #ff0000 0%, #cc0000 100%)',
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                borderRadius: '8px',
                fontSize: '14px',
                fontWeight: '700',
                cursor: 'pointer',
                boxShadow: '0 5px 15px rgba(255, 0, 0, 0.3)',
                textTransform: 'uppercase',
                letterSpacing: '1px'
              }}
            >
              Çıkış
            </button>
          </div>
        </div>
      </div>

      {/* PIN List */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.1)',
        backdropFilter: 'blur(15px)',
        borderRadius: '15px',
        padding: '20px',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        position: 'relative',
        zIndex: 2
      }}>
        <h2 style={{
          color: '#fff',
          fontSize: '1.5rem',
          margin: '0 0 20px 0',
          textShadow: '1px 1px 2px rgba(0,0,0,0.5)'
        }}>
          PIN'lerim ({pins.length})
        </h2>

        {isLoading ? (
          <div style={{
            textAlign: 'center',
            color: 'rgba(255, 255, 255, 0.8)',
            padding: '40px'
          }}>
            Yükleniyor...
          </div>
        ) : pins.length === 0 ? (
          <div style={{
            textAlign: 'center',
            color: 'rgba(255, 255, 255, 0.8)',
            padding: '40px'
          }}>
            Henüz PIN oluşturmadınız.
            <br />
            <button
              onClick={() => setShowCreatePin(true)}
              style={{
                background: 'linear-gradient(135deg, #00ff00 0%, #00cc00 100%)',
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                borderRadius: '8px',
                fontSize: '14px',
                fontWeight: '700',
                cursor: 'pointer',
                marginTop: '10px',
                textTransform: 'uppercase'
              }}
            >
              İlk PIN'i Oluştur
            </button>
          </div>
        ) : (
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
            gap: '15px'
          }}>
            {pins.map((pin) => (
              <div
                key={pin._id}
                style={{
                  background: 'rgba(255, 255, 255, 0.05)',
                  borderRadius: '10px',
                  padding: '15px',
                  border: '1px solid rgba(255, 255, 255, 0.1)',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
                onClick={() => setSelectedPin(pin)}
                onMouseOver={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.1)'}
                onMouseOut={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.05)'}
              >
                <div style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  marginBottom: '10px'
                }}>
                  <div style={{
                    fontSize: '1.2rem',
                    fontWeight: '700',
                    color: '#fff',
                    fontFamily: 'monospace',
                    letterSpacing: '2px'
                  }}>
                    {pin.pin}
                  </div>
                  <div style={{
                    padding: '4px 8px',
                    borderRadius: '4px',
                    fontSize: '12px',
                    fontWeight: '700',
                    color: '#fff',
                    background: getStatusColor(pin.status)
                  }}>
                    {getStatusText(pin.status)}
                  </div>
                </div>

                <div style={{
                  fontSize: '14px',
                  color: 'rgba(255, 255, 255, 0.8)',
                  marginBottom: '8px'
                }}>
                  <strong>Hedef:</strong> {pin.targetInfo?.name || 'Belirtilmemiş'}
                </div>

                <div style={{
                  fontSize: '12px',
                  color: 'rgba(255, 255, 255, 0.6)',
                  marginBottom: '8px'
                }}>
                  <strong>Oluşturulma:</strong> {formatDate(pin.createdAt)}
                </div>

                <div style={{
                  fontSize: '12px',
                  color: 'rgba(255, 255, 255, 0.6)',
                  marginBottom: '8px'
                }}>
                  <strong>Son Kullanma:</strong> {formatDate(pin.expiresAt)}
                </div>

                {pin.scanResults?.status === 'completed' && (
                  <div style={{
                    fontSize: '12px',
                    color: 'rgba(255, 255, 255, 0.8)',
                    marginBottom: '8px'
                  }}>
                    <strong>Risk Seviyesi:</strong> 
                    <span style={{
                      color: getRiskColor(pin.scanResults.riskLevel),
                      fontWeight: '700',
                      marginLeft: '5px'
                    }}>
                      {pin.scanResults.riskLevel}
                    </span>
                  </div>
                )}

                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    copyToClipboard(pin.pin);
                  }}
                  style={{
                    background: 'rgba(255, 255, 255, 0.1)',
                    color: '#fff',
                    border: '1px solid rgba(255, 255, 255, 0.3)',
                    padding: '5px 10px',
                    borderRadius: '4px',
                    fontSize: '12px',
                    cursor: 'pointer',
                    width: '100%'
                  }}
                >
                  PIN'i Kopyala
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Create PIN Modal */}
      {showCreatePin && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          background: 'rgba(0, 0, 0, 0.8)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000,
          padding: '20px'
        }}>
          <div style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(15px)',
            borderRadius: '15px',
            padding: '30px',
            width: '100%',
            maxWidth: '500px',
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }}>
            <h3 style={{
              color: '#fff',
              fontSize: '1.5rem',
              margin: '0 0 20px 0',
              textAlign: 'center'
            }}>
              Yeni PIN Oluştur
            </h3>

            <form onSubmit={createPin}>
              <div style={{ marginBottom: '15px' }}>
                <label style={{
                  display: 'block',
                  color: 'rgba(255, 255, 255, 0.9)',
                  fontSize: '14px',
                  marginBottom: '5px'
                }}>
                  Hedef Kişi Adı
                </label>
                <input
                  type="text"
                  value={newPinData.targetName}
                  onChange={(e) => setNewPinData({...newPinData, targetName: e.target.value})}
                  required
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '2px solid rgba(255, 255, 255, 0.3)',
                    borderRadius: '8px',
                    fontSize: '14px',
                    background: 'rgba(255, 255, 255, 0.1)',
                    color: '#fff',
                    backdropFilter: 'blur(10px)'
                  }}
                  placeholder="Hedef kişinin adı"
                />
              </div>

              <div style={{ marginBottom: '15px' }}>
                <label style={{
                  display: 'block',
                  color: 'rgba(255, 255, 255, 0.9)',
                  fontSize: '14px',
                  marginBottom: '5px'
                }}>
                  Email (Opsiyonel)
                </label>
                <input
                  type="email"
                  value={newPinData.targetEmail}
                  onChange={(e) => setNewPinData({...newPinData, targetEmail: e.target.value})}
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '2px solid rgba(255, 255, 255, 0.3)',
                    borderRadius: '8px',
                    fontSize: '14px',
                    background: 'rgba(255, 255, 255, 0.1)',
                    color: '#fff',
                    backdropFilter: 'blur(10px)'
                  }}
                  placeholder="email@example.com"
                />
              </div>

              <div style={{ marginBottom: '15px' }}>
                <label style={{
                  display: 'block',
                  color: 'rgba(255, 255, 255, 0.9)',
                  fontSize: '14px',
                  marginBottom: '5px'
                }}>
                  Telefon (Opsiyonel)
                </label>
                <input
                  type="tel"
                  value={newPinData.targetPhone}
                  onChange={(e) => setNewPinData({...newPinData, targetPhone: e.target.value})}
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '2px solid rgba(255, 255, 255, 0.3)',
                    borderRadius: '8px',
                    fontSize: '14px',
                    background: 'rgba(255, 255, 255, 0.1)',
                    color: '#fff',
                    backdropFilter: 'blur(10px)'
                  }}
                  placeholder="+90 555 123 45 67"
                />
              </div>

              <div style={{ marginBottom: '15px' }}>
                <label style={{
                  display: 'block',
                  color: 'rgba(255, 255, 255, 0.9)',
                  fontSize: '14px',
                  marginBottom: '5px'
                }}>
                  Notlar (Opsiyonel)
                </label>
                <textarea
                  value={newPinData.notes}
                  onChange={(e) => setNewPinData({...newPinData, notes: e.target.value})}
                  rows="3"
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '2px solid rgba(255, 255, 255, 0.3)',
                    borderRadius: '8px',
                    fontSize: '14px',
                    background: 'rgba(255, 255, 255, 0.1)',
                    color: '#fff',
                    backdropFilter: 'blur(10px)',
                    resize: 'vertical'
                  }}
                  placeholder="Ek notlar..."
                />
              </div>

              <div style={{ marginBottom: '20px' }}>
                <label style={{
                  display: 'block',
                  color: 'rgba(255, 255, 255, 0.9)',
                  fontSize: '14px',
                  marginBottom: '5px'
                }}>
                  Geçerlilik Süresi (Saat)
                </label>
                <select
                  value={newPinData.durationHours}
                  onChange={(e) => setNewPinData({...newPinData, durationHours: parseInt(e.target.value)})}
                  style={{
                    width: '100%',
                    padding: '10px',
                    border: '2px solid rgba(255, 255, 255, 0.3)',
                    borderRadius: '8px',
                    fontSize: '14px',
                    background: 'rgba(255, 255, 255, 0.1)',
                    color: '#fff',
                    backdropFilter: 'blur(10px)'
                  }}
                >
                  <option value={1}>1 Saat</option>
                  <option value={6}>6 Saat</option>
                  <option value={12}>12 Saat</option>
                  <option value={24}>24 Saat</option>
                  <option value={48}>48 Saat</option>
                  <option value={72}>72 Saat</option>
                </select>
              </div>

              <div style={{
                display: 'flex',
                gap: '10px',
                justifyContent: 'flex-end'
              }}>
                <button
                  type="button"
                  onClick={() => setShowCreatePin(false)}
                  style={{
                    background: 'rgba(255, 255, 255, 0.1)',
                    color: '#fff',
                    border: '1px solid rgba(255, 255, 255, 0.3)',
                    padding: '10px 20px',
                    borderRadius: '8px',
                    fontSize: '14px',
                    cursor: 'pointer'
                  }}
                >
                  İptal
                </button>
                <button
                  type="submit"
                  style={{
                    background: 'linear-gradient(135deg, #00ff00 0%, #00cc00 100%)',
                    color: 'white',
                    border: 'none',
                    padding: '10px 20px',
                    borderRadius: '8px',
                    fontSize: '14px',
                    fontWeight: '700',
                    cursor: 'pointer',
                    textTransform: 'uppercase'
                  }}
                >
                  PIN Oluştur
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* PIN Details Modal */}
      {selectedPin && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          background: 'rgba(0, 0, 0, 0.8)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000,
          padding: '20px'
        }}>
          <div style={{
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(15px)',
            borderRadius: '15px',
            padding: '30px',
            width: '100%',
            maxWidth: '600px',
            maxHeight: '80vh',
            overflow: 'auto',
            border: '1px solid rgba(255, 255, 255, 0.2)'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '20px'
            }}>
              <h3 style={{
                color: '#fff',
                fontSize: '1.5rem',
                margin: '0'
              }}>
                PIN Detayları
              </h3>
              <button
                onClick={() => setSelectedPin(null)}
                style={{
                  background: 'rgba(255, 255, 255, 0.1)',
                  color: '#fff',
                  border: '1px solid rgba(255, 255, 255, 0.3)',
                  padding: '5px 10px',
                  borderRadius: '4px',
                  fontSize: '14px',
                  cursor: 'pointer'
                }}
              >
                ✕
              </button>
            </div>

            <div style={{
              fontSize: '1.5rem',
              fontWeight: '700',
              color: '#fff',
              fontFamily: 'monospace',
              letterSpacing: '3px',
              textAlign: 'center',
              marginBottom: '20px',
              padding: '15px',
              background: 'rgba(255, 255, 255, 0.1)',
              borderRadius: '8px'
            }}>
              {selectedPin.pin}
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
              gap: '15px',
              marginBottom: '20px'
            }}>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Durum:</strong>
                <div style={{
                  color: getStatusColor(selectedPin.status),
                  fontWeight: '700'
                }}>
                  {getStatusText(selectedPin.status)}
                </div>
              </div>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Hedef:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {selectedPin.targetInfo?.name || 'Belirtilmemiş'}
                </div>
              </div>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Oluşturulma:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {formatDate(selectedPin.createdAt)}
                </div>
              </div>
              <div>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Son Kullanma:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {formatDate(selectedPin.expiresAt)}
                </div>
              </div>
            </div>

            {selectedPin.targetInfo?.email && (
              <div style={{ marginBottom: '10px' }}>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Email:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {selectedPin.targetInfo.email}
                </div>
              </div>
            )}

            {selectedPin.targetInfo?.phone && (
              <div style={{ marginBottom: '10px' }}>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Telefon:</strong>
                <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                  {selectedPin.targetInfo.phone}
                </div>
              </div>
            )}

            {selectedPin.targetInfo?.notes && (
              <div style={{ marginBottom: '20px' }}>
                <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Notlar:</strong>
                <div style={{ 
                  color: 'rgba(255, 255, 255, 0.8)',
                  background: 'rgba(255, 255, 255, 0.05)',
                  padding: '10px',
                  borderRadius: '8px',
                  marginTop: '5px'
                }}>
                  {selectedPin.targetInfo.notes}
                </div>
              </div>
            )}

            {selectedPin.scanResults?.status === 'completed' && (
              <div style={{
                background: 'rgba(255, 255, 255, 0.05)',
                borderRadius: '10px',
                padding: '20px',
                marginTop: '20px'
              }}>
                <h4 style={{
                  color: '#fff',
                  fontSize: '1.2rem',
                  margin: '0 0 15px 0'
                }}>
                  Tarama Sonuçları
                </h4>

                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
                  gap: '15px',
                  marginBottom: '15px'
                }}>
                  <div>
                    <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Risk Seviyesi:</strong>
                    <div style={{
                      color: getRiskColor(selectedPin.scanResults.riskLevel),
                      fontWeight: '700',
                      fontSize: '1.1rem'
                    }}>
                      {selectedPin.scanResults.riskLevel}
                    </div>
                  </div>
                  <div>
                    <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Tamamlanma:</strong>
                    <div style={{ color: 'rgba(255, 255, 255, 0.8)' }}>
                      {formatDate(selectedPin.scanResults.completedAt)}
                    </div>
                  </div>
                </div>

                {selectedPin.scanResults.summary && (
                  <div>
                    <strong style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Özet:</strong>
                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))',
                      gap: '10px',
                      marginTop: '10px'
                    }}>
                      <div style={{
                        background: 'rgba(255, 255, 255, 0.05)',
                        padding: '10px',
                        borderRadius: '6px',
                        textAlign: 'center'
                      }}>
                        <div style={{ color: '#fff', fontWeight: '700', fontSize: '1.2rem' }}>
                          {selectedPin.scanResults.summary.suspiciousProcessesCount || 0}
                        </div>
                        <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '12px' }}>
                          Şüpheli Süreç
                        </div>
                      </div>
                      <div style={{
                        background: 'rgba(255, 255, 255, 0.05)',
                        padding: '10px',
                        borderRadius: '6px',
                        textAlign: 'center'
                      }}>
                        <div style={{ color: '#fff', fontWeight: '700', fontSize: '1.2rem' }}>
                          {selectedPin.scanResults.summary.suspiciousFilesCount || 0}
                        </div>
                        <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '12px' }}>
                          Şüpheli Dosya
                        </div>
                      </div>
                      <div style={{
                        background: 'rgba(255, 255, 255, 0.05)',
                        padding: '10px',
                        borderRadius: '6px',
                        textAlign: 'center'
                      }}>
                        <div style={{ color: '#fff', fontWeight: '700', fontSize: '1.2rem' }}>
                          {selectedPin.scanResults.summary.memoryInjectionsCount || 0}
                        </div>
                        <div style={{ color: 'rgba(255, 255, 255, 0.7)', fontSize: '12px' }}>
                          Memory Injection
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            )}

            <div style={{
              display: 'flex',
              gap: '10px',
              justifyContent: 'center',
              marginTop: '20px'
            }}>
              <button
                onClick={() => copyToClipboard(selectedPin.pin)}
                style={{
                  background: 'linear-gradient(135deg, #00ff00 0%, #00cc00 100%)',
                  color: 'white',
                  border: 'none',
                  padding: '10px 20px',
                  borderRadius: '8px',
                  fontSize: '14px',
                  fontWeight: '700',
                  cursor: 'pointer',
                  textTransform: 'uppercase'
                }}
              >
                PIN'i Kopyala
              </button>
            </div>
          </div>
        </div>
      )}
      
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
        }}
      />
    </div>
  );
};

export default Dashboard;
